-- Đang đổ dữ liệu cho bảng `characters`
--

INSERT INTO `characters` (`id`, `name`, `path_id`, `element_id`, `hp`, `atk`, `def`, `spd`, `rarity`, `description`, `image`) VALUES
(1, 'Đại Herta', 7, 1, 1164, 679, 485, 99, '5', 'Thành viên tôn quý #83 của Câu Lạc Bộ Thiên Tài, loài người, giới tính nữ, trẻ tuổi, xinh đẹp, dễ thương.\r\n\r\nNgười ta nói rằng cô ấy ẩn cư ở - rìa vũ trụ, hầu như không bao giờ bước ra khỏi đó, lần xuất hiện này...\r\n\r\nChắc chắn là vì một vấn đề nào đó buộc phải đích thân ra tay nhỉ?', 'TheHerta.jpg'),
(2, 'Aglaea', 5, 3, 1241, 698, 545, 102, '5', '\"Thợ Dệt Vàng\" Aglaea\r\n\r\nTại tòa Thánh Thành được bình minh chiếu cố ấy, người thợ dệt vuốt ve tơ vàng, nối kết vận mệnh.\r\n\r\nHậu Duệ Chrysos gánh vác Ngọn Lửa \"Lãng Mạn\", triệu tập anh hùng thế gian, dẫn dắt họ một lần nữa bước lên cuộc hành trình dài.\r\n\r\n... Đánh hạ chúng thần, trả lại ngọn lửa thần, giúp cho Amphoreus gần như bị hủy diệt hồi sinh.', 'Aglaea.jpg'),
(3, 'Tribbie', 3, 4, 1047, 523, 727, 96, '5', '\"Bộ Ba Định Mệnh\" Tribios\r\n\r\nTừ thánh địa được Lời Sấm Truyền Tam Thần ban phước, vị tín sứ tự chia mình thành hàng ngàn thân thể và bắt đầu bước lên cuộc hành trình xa xôi.\r\n\r\nThánh nữ của Janusopolis, Tribios, Hậu Duệ Chrysos đã đánh cắp Ngọn Lửa \"Cánh Cổng\", bôn ba vì chúng sinh, mang thông điệp cứu thế đến muôn nơi.\r\n\r\nTìm kiếm những người con mang dòng máu vàng thần thánh đó, phá vỡ bóng tối bao trùm thế gian, tiến tới một ngày mai trăng sao soi sáng.', 'Tribbie.jpg'),
(4, 'Mydei', 2, 6, 1552, 427, 194, 95, '5', '\"Vương Tử Vong Quốc\" Mydeimos\r\n\r\nKremnos, thành bang chìm trong sương mù, thành bang của hỗn loạn và chiến tranh! Hoàng tộc của nó mang dòng máu sát hại thân thích, thần linh của nó mang tên gọi Tai Ương.\r\n\r\nMydeimos bất tử, con sư tử xa bầy, Hậu Duệ Chrysos săn lùng Ngọn Lửa \"Phân Tranh\". Chịu đựng cái chết hàng vạn lần, tắm máu để trở về quê hương, một mình gánh vác số mệnh điên rồ – Giết vua để trở thành vua, diệt thần để trở thành thần. Vó sắt của chiến tranh đạp qua hoang mạc, cuối cùng cũng phải nhuốm máu quê hương.', 'Mydei.jpg'),
(8, 'Castorice', 5, 4, 1629, 523, 485, 95, '5', '\"Thị Nữ Tử Vong\" Castorice\r\n\r\nAidonia - vương quốc tuyết phủ quanh năm tôn thờ cái chết, nay đã chìm vào giấc ngủ ngọt ngào.\r\n\r\nHỡi Castorice, người con gái của sông Styx, Hậu Duệ Chrysos tìm kiếm Ngọn Lửa \"Tử Vong\", hãy lên đường. Hãy che chở tiếng than khóc của những linh hồn trên thế gian này và ôm lấy nỗi cô đơn của vận mệnh,\r\n\r\n— Sống chết đều là hành trình. Khi cánh bướm đậu trên nhành cây cũng là lúc sự tàn úa rồi sẽ tái sinh.', 'Castorice.jpg'),
(9, 'Anaxa', 7, 5, 970, 756, 557, 97, '5', '\"Học Giả Qua Đời\" Anaxagoras\r\n\r\nĐiện Cây Giác Ngộ, nơi nuôi dưỡng tri thức, chiếc nôi sinh ra các triết gia.\r\n\r\nAnaxagoras báng bổ thần linh, Hậu Duệ Chrysos chất vấn Ngọn Lửa \"Lý Trí\" rằng: Ngươi có sẵn lòng mang tiếng xấu, cũng phải chống lại lời tiên tri, đâm xuyên cành lá nghi ngờ vào Cây Thiêng của Trí Tuệ?\r\n\r\n\"Nực cười. Thế giới này chỉ toàn sự giả dối, chỉ có ta mới là chân thực.\"', 'Anaxa.jpg'),
(10, 'Hyacine', 5, 5, 1086, 388, 630, 110, '5', '\"Thầy Thuốc Ánh Sáng\" Hyacinthia\r\n\r\nThành bang giữa những tầng mây tan rã theo năm tháng, Đình Viện Hoàng Hôn một lần nữa mở rộng cánh cửa, mang đến ánh sáng le lói cho Đêm Vĩnh Hằng.\r\n\r\nThầy Thuốc Hyacinthia, Hậu Duệ Chrysos canh giữ Ngọn Lửa \"Bầu Trời\". Kế thừa ý chí của tổ tiên, vá lại bình minh và hoàng hôn đã nứt vỡ.\r\n\r\n...Mong ánh cầu vồng rọi xuống, xóa tan hận thù, đưa bình minh trở về mặt đất.', 'Hyacine.jpg'),
(11, 'Nhà Khai Phá - Ký Ức', 5, 1, 1241, 601, 606, 95, '5', '\"Tác Giả Sáng Thế\" Nhà Khai Phá\r\nĐội Tàu Astral xuyên qua đêm dài, mầm non của sự sống nảy mầm từ vĩnh hằng.\r\n\r\nĐấng Cứu Thế bên ngoài bầu trời, Khách Vô Danh dẫn dắt \"Sáng Thế\", bạn phải truyền đạt ý chí \"Khai Phá\", cùng các anh hùng chống lại \"Hủy Diệt\"\r\n\r\n- Mang theo ký ức của hôm qua, không quên tâm nguyện của hôm nay, đi về phía ngày mai lấp lánh ánh sao.', 'TrailblazerRemembrance.jpg'),
(12, 'Cipher', 4, 4, 931, 640, 509, 106, '5', '\"Lữ Khách Bước Nhanh\" Cifera\r\n\r\nDolos - thành phố bị cướp phá, nơi ba trăm tên cướp ăn chơi trác táng hoành hành.\r\n\r\nCifera, ngôi sao trộm cắp nhanh nhẹn, Hậu Duệ Chrysos trêu đùa với Ngọn Lửa \"Mưu Kế\", hãy chạy đi. Nguyện những lời dối trá của cô theo gió lan xa, thổi khắp thế gian này.\r\n\r\n- \"Ha, còn muốn lừa tôi sao? Mơ đi!\"', 'Cipher.jpg'),
(13, 'Phainon', 2, 7, 1435, 582, 703, 94, '5', '\"Anh Hùng Vô Danh\" Phainon\r\n\r\nAedes Elysiae, thôn làng vùng ven ẩn mình khỏi thế giới, giờ chỉ còn lại truyền thuyết mơ hồ.\r\n\r\nAnh hùng vô danh Khaslana, Hậu Duệ Chrysos đón nhận Ngọn Lửa \"Phụ Thế\", phải khắc ghi lý tưởng của thế giới, gánh vác vận mệnh của chúng sinh, đem tới tia sáng ban mai cho thế giới mới.', 'Phainon.jpg'),
(14, 'Archer', 6, 4, 1164, 621, 485, 105, '5', '\"Lần đầu gặp mặt, gọi tôi là Archer là được. May thay mối quan hệ giữa hai chúng ta không cần phải chém giết lẫn nhau, vậy thì hãy cố gắng hòa thuận với nhau nhé.\"\r\n\r\nSự tuyệt vọng luân phiên diễn ra giữa quá khứ và tương lai, vô số lý tưởng đã từng bị thiêu rụi trong tấm vải thánh đỏ thẫm. Nhưng, xin đừng để lớp tro tàn ấy đánh lừa...\r\n\r\nNếu có người mượn danh nghĩa lý tưởng tạo nên giấc mơ hư ảo, thì anh ta chắc chắn sẽ lại bùng cháy, chiến đấu với sự giả tạo của thế giới đến cùng.\r\n\r\nBất luận ở đâu, anh ta vẫn luôn là người bạn của chính nghĩa.', 'Archer.jpg'),
(15, 'Saber', 2, 5, 1242, 602, 655, 101, '5', '\"Ngài chính là Master của tôi sao? Kiếm của tôi sẽ do ngài sai bảo. Tôi sẽ dốc hết sức để đem lại thắng lợi cho ngài.\"\r\n\r\nAnh Linh cô độc đi qua đêm dài của vận mệnh, lá cờ của bàn tròn vẫn tung bay trong giấc mơ. Vua kỵ sĩ của Camelot lúc này vẫn chưa đến được quê hương lý tưởng.\r\n\r\nVẫn là cô gái ấy, cô được triệu gọi đến Cuộc Chiến Chén Thánh đặc biệt này. Khi một lần nữa đối mặt với lựa chọn của Kiếm Trong Đá, cô sẽ đột phá ảo ảnh trong quá khứ như thế nào?\r\n\r\n\"Điều ước tôi chưa thực hiện, cũng sẽ kết thúc tại đây.\"', 'Saber.jpg'),
(16, 'Dan Heng', 6, 5, 882, 546, 396, 110, '4', 'Một thanh niên lạnh lùng ít nói, giữ kín như bưng về quá khứ của mình.\r\n\r\nAnh đã chọn đi cùng Đội Tàu Astral để trốn tránh dòng tộc của mình.', 'DanHeng.jpg'),
(17, 'March 7th - Bảo Hộ', 1, 1, 1058, 511, 573, 101, '4', 'Một thiếu nữ ngủ sâu trong tảng băng vĩnh hằng và không biết gì về quá khứ.\r\n\r\nĐể tìm kiếm sự thật về thân thế, cô đã chọn cùng đồng hành với Đội Tàu Astral.\r\n\r\nHiện giờ đã chuẩn bị cho mình khoảng 67 \"câu chuyện cuộc đời\".', 'March7thPreservation.jpg'),
(18, 'March 7th - Săn Bắn', 6, 6, 1058, 564, 441, 102, '4', 'March 7th trong trang phục của Xianzhou, một nữ hiệp trẻ tuổi cầm kiếm.\r\n\r\nNóng lòng bái sư học nghệ cùng Yunli và Yanqing, để lưu lại nhiều \"hồi ức\" đẹp đẽ hơn ở Xianzhou.', 'March7thHunt.jpg'),
(19, 'Cerydra', 3, 5, 1358, 620, 485, 99, '5', '\"Quân Chủ Cờ Vua\" Cerydra\r\n\r\nĐế quốc Phương Bắc, vương triều lụi tàn, vùng đất băng giá vẫn rực cháy khát vọng chinh phạt.\r\n\r\nQuân chủ Cerydra, Hậu Duệ Chrysos nắm giữ Ngọn Lửa \"Luật Pháp\", cô sẽ bày binh bố trận, đấu trí với thần thánh, phán quyết những tội nhân phản nghịch, đặt nền móng cho sự nghiệp Săn Đuổi Lửa trên thế gian này', 'Cerydra.jpg'),
(20, 'Hysilens', 4, 7, 1203, 601, 485, 102, '5', '\"Kiếm Kỵ Gảy Sóng\" Helektra\r\n\r\nStyxia, thành phố ven biển của men say và mộng tưởng, nơi tiếng hát ngày xưa vẫn còn vang vọng giữa những con sóng.\r\n\r\nHelektra, người con gái của biển cả, Hậu Duệ Chrysos thanh tẩy Ngọn Lửa \"Biển Cả\", hãy xua tan dòng chảy ngầm bẩn thỉu, và mở ra một bữa tiệc không say không về cho vị anh hùng bên ngoài bầu trời.\r\n\r\n...Vẫn chưa đến lúc kết thúc, dù hy vọng mong manh như bọt nước, những con sóng vẫn sẽ tiếp tục tiến về phía trước.', 'Hysilens.jpg'),
(21, 'Fugue', 4, 2, 1125, 582, 557, 102, '5', 'Thiếu nữ người Hồ Ly khôn khéo nhanh nhẹn, từ tướng mạo, tên tuổi đến cả thân phận đều bị người ta cướp mất.\r\n\r\nVận mệnh chừa lại cho cô một tia hy vọng sống, nhưng dấu ấn khó phai của Hủy Diệt vẫn đang âm thầm rục rịch.\r\n\r\nTrải qua sinh tử, \"người quên trở về\" lại có được cuộc đời mới... không biết khi nào cô ấy mới có thể trở về cố hương đây?', 'Fugue.jpg'),
(22, 'Sunday', 3, 6, 1242, 640, 533, 96, '5', 'Mộng đẹp của \"Trật Tự\" đã tan biến, nhưng vẫn có người không từ bỏ nguyện vọng ban đầu.\r\n\r\n...Lữ khách với đôi cánh gãy, bước chân của anh ta sẽ đi về đâu?', 'Sunday.jpg'),
(23, 'Rappa', 7, 6, 1086, 717, 460, 96, '5', 'Thiếu nữ kỳ diệu xuất hiện tại Penacony như đạn chớp vào giờ Sửu ba khắc, tự xưng là Ninja, quy kết mọi thứ trên thế gian vì \"Nhẫn Thuật\".\r\n\r\nLà một thành viên của Cảnh Binh Thiên Hà, luôn luôn truy đuổi kẻ ác có tên là Vua Vượn-Nhẫn Giả Tà Ác, cho đến tận cùng ngân hà.', 'Rappa.jpg'),
(24, 'Moze', 6, 3, 811, 599, 352, 111, '4', 'Cảnh vệ của Xianzhou Yaoqing, trầm lặng ít nói, hành động đơn độc.\r\n\r\nChuyên phụ trách tình báo và nhiệm vụ bí mật, rất hiếm lộ diện trước mặt người khác. Khi Moze thể hiện bản thân, chính là lúc kẻ địch gặp tử thần.\r\n\r\nCách thức ám sát phong phú, rất chấp niệm với sự ngăn nắp và tính trật tự.', 'Moze.jpg'),
(25, 'Lingsha', 8, 2, 1358, 679, 436, 98, '5', 'Sở Trưởng mới nhậm chức của Sở Đan Đỉnh Xianzhou Luofu, một thầy thuốc người Vidyadhara thông minh và lanh lợi.\r\n\r\nSở hữu khứu giác nhạy bén, thường dùng nó để chẩn đoán bệnh và xông hương để làm yên lòng người khác.\r\n\r\nRất khéo léo trong việc xử lý các mối quan hệ phức tạp, dù trong lòng có bực bội đến đâu, thì bề ngoài cô vẫn luôn giữ được vẻ bình tĩnh.', 'Lingsha.jpg'),
(26, 'Feixiao', 6, 5, 1047, 601, 388, 112, '5', 'Tướng Quân Thiên Kích của Xianzhou Yaoqing, một trong Đế Cung Thất Thiên Tướng, là người không câu nệ, thẳng thắn hào sảng.\r\n\r\nTinh thông vô số võ nghệ, luyện hóa cơ thể đến cực độ, được mệnh danh là Tướng Quân Đại Thắng, và được quân dân Xianzhou yêu mến.\r\n\r\nNhưng mắc chứng \"Đêm Trăng Cuồng\", nếu muốn tiêu diệt Tà Vật trong thời gian quy định, thì kẻ địch duy nhất của Feixiao chính là bản thân.', 'Feixiao.jpg'),
(27, 'Jiaoqiu', 4, 2, 1358, 601, 509, 98, '5', 'Một thầy thuốc và mưu sĩ Người Hồ Ly ở Xianzhou Yaoqing, thường tươi cười với mọi người, nhưng thực ra lòng đầy mưu mô.\r\n\r\nSinh ra trong một gia đình nổi tiếng thuộc Sở Đan Đỉnh, từng ở ẩn vì chịu tổn thương quá mức và không làm nghề y nữa, sau đó vì để chữa trị cho Tướng Quân Thiên Kích Feixiao nên mới xuống núi lần nữa.\r\n\r\nGiỏi nghiên cứu các phương thuốc nguồn gốc từ thực dưỡng, đặc biệt là những món cay có thể đem đến cảm giác đau cho con người, đã phát minh ra một phương thuốc chữa bệnh đỉnh vạc có tên là \"Cửu Cung Cách\".', 'Jiaoqiu.jpg'),
(28, 'Yunli', 2, 7, 1358, 679, 460, 94, '5', 'Kiếm sĩ săn kiếm của Xianzhou Zhuming, cháu gái cưng được \"Tướng Quân Chúc Uyên\" Huaiyan yêu thương, tính tình thẳng thắn.\r\n\r\nTừ nhỏ đã đi theo Huaiyan học kiếm thuật và kỹ năng rèn đúc, là kiếm sĩ thiên tài trẻ tuổi thứ hai trong \"Diệm Luân Bát Diệp\".\r\n\r\nCăm phẫn tột độ với Kiếm Ma do Zhuming tạo ra, thề sẽ \"săn lùng Kiếm Ma, tiêu diệt tất cả\".', 'Yunli.jpg'),
(29, 'Nhà Khai Phá - Hòa Hợp', 3, 6, 1086, 446, 679, 105, '5', '[Nam]\r\n\r\nThiếu niên lên Đội Tàu Astral.\r\n\r\nĐể diệt trừ hiểm họa mà Stellaron mang đến, đã quyết định đồng hành cùng đoàn tàu.\r\n\r\n[Nữ]\r\n\r\nThiếu nữ lên Đội Tàu Astral.\r\n\r\nĐể diệt trừ hiểm họa mà Stellaron mang đến, đã quyết định đồng hành cùng đoàn tàu.', 'TrailblazerHarmony.jpg'),
(30, 'Jade', 7, 4, 1086, 659, 509, 103, '5', 'Cán bộ cao cấp của Bộ Phận Đầu Tư Chiến Lược thuộc Công Ty Hành Tinh Hòa Bình, một trong 10 Người Có Trái Tim Đá, Đá Tảng là Jade Tín Chấp.\r\n\r\nMột kẻ cho vay kiều diễm lịch thiệp, thấu hiểu lòng người, có sở thích cá nhân mang tên Hiệu Cầm Đồ Bonajade.\r\n\r\nSẵn sàng kiên nhẫn chờ đợi để có được những thứ có giá trị cao, đồng thời cũng giỏi moi giá trị từ những khách hàng tưởng như chẳng có gì trong tay.', 'Jade.jpg'),
(31, 'Firefly', 2, 2, 814, 523, 776, 104, '5', 'Thành viên của Thợ Săn Stellaron, cô gái mặc giáp cơ khí, Sam.\r\n\r\nSinh ra là một vũ khí, vì cải tạo gen mà phải chịu đựng nỗi đau \"mất Entropy\".\r\n\r\nGia nhập Thợ Săn Stellaron để tìm kiếm ý nghĩa của cuộc sống, không ngừng theo đuổi con đường chống lại vận mệnh.', 'Firefly.jpg'),
(32, 'Boothill', 6, 7, 1203, 620, 436, 107, '5', 'Anh chàng cao bồi \"người cải tạo\" lang thang trong vũ trụ, cực kỳ lạc quan, không bị ràng buộc.\r\n\r\nLà một thành viên của \"Cảnh Binh Thiên Hà\", có thể làm bất cứ điều gì để trừ gian diệt ác...\r\n\r\nNhững hành động phách lối của anh ta đều nhằm mục đích thu hút sự chú ý của Công Ty Hành Tinh Hòa Bình - mục tiêu trả thù của anh ta.', 'Boothill.jpg'),
(33, 'Robin', 3, 7, 1280, 640, 485, 102, '5', 'Sinh ra ở Penacony, ca sĩ Tộc Halovian nổi tiếng vũ trụ, cô gái có phong thái trầm tĩnh và thanh lịch.\r\nLần này được Gia Tộc mời về cố hương, để hát tặng cho mọi người trong \"Đại Nhạc Hội Hòa Hợp\".\r\nCó thể dựa vào sức mạnh của \"Hòa Hợp\" để truyền tải giọng hát, thể hiện \"cộng hưởng\" giữa người hâm mộ và cả sinh linh vạn giới.', 'Robin.jpg'),
(34, 'Gallagher', 8, 2, 1305, 529, 441, 98, '4', 'Cảnh Sát Trưởng của Gia Tộc Bloodhound Penacony, tiếp đãi những vị khách đến thăm rất chu đáo, nhưng luôn có sự đề phòng.\r\n\r\nDường như có rất nhiều chuyện đã xảy ra trong quá khứ nhưng lại chưa bao giờ chủ động nhắc đến.', 'Gallagher.jpg'),
(35, 'Aventurine', 1, 6, 1203, 446, 654, 106, '5', 'Cán bộ cao cấp \"Bộ Phận Đầu Tư Chiến Lược\" của Công Ty Hành Tinh Hòa Bình, một trong \"10 người có trái tim đá\", với nền tảng là \"Aventurine Quỷ Dị\".\r\n\r\nMặt luôn nở nụ cười, nhưng lòng dạ khó đoán, có niềm tin vững chắc vào \"rủi ro càng lớn thì thu hoạch càng nhiều\"', 'Aventurine.jpg'),
(36, 'Acheron', 4, 3, 1125, 698, 436, 101, '5', 'Lữ khách tự xưng là \"Cảnh Binh Thiên Hà\", tên thật không rõ.\r\n\r\nĐeo thanh đao dài trên người, độc hành trong ngân hà.', 'Acheron.jpg'),
(37, 'Misha', 2, 1, 1270, 599, 396, 96, '4', 'Đứa trẻ gác cửa ở Khách Sạn Penacony, một cậu bé ngoan ngoãn hiểu chuyện.\r\n\r\nÔm khát vọng dõi theo dấu chân Khách Vô Danh, mơ ước một ngày bản thân cũng có thể bước lên cuộc hành trình.', 'Misha.jpg'),
(38, 'Sparkle', 3, 4, 1397, 523, 485, 101, '5', 'Một trong các thành viên của \"Kẻ Ngốc Đeo Mặt Nạ\", khó đoán, bất chấp thủ đoạn.\r\n\r\nMột bậc thầy sân khấu nguy hiểm, nghiện diễn xuất, lúc nào cũng đeo cả ngàn chiếc mặt nạ, có thể hóa thân thành hàng vạn diện mạo khác nhau.\r\n\r\nCủa cải, địa vị, quyền lợi... đối với Sparkle đều không quan trọng. Điều duy nhất khiến cô hành động, chỉ có mỗi \"niềm vui\".', 'Sparkle.jpg'),
(39, 'Black Swan', 4, 5, 1086, 659, 485, 102, '5', 'Người Lưu Giữ Ký Ức của Sảnh Đường Hồi Ức, nhà chiêm tinh thong dong.\r\n\r\nHãy kiên nhẫn lắng nghe \"ký ức\" của người khác, sẽ có thể nắm giữ vận mệnh trong tay.', 'BlackSwan.jpg'),
(40, 'Xueyi', 2, 4, 1058, 599, 396, 103, '4', 'Phán quan của \"Sở Thập Vương\", cơ quan giám sát sinh tử ở Xianzhou Luofu.\r\n\r\nVốn đã mất từ nhiều năm, mượn cơ thể con rối để \"hoàn dương\" và thực hiện sứ mệnh.', 'Xueyi.jpg'),
(41, 'Dr. Ratio', 6, 6, 1047, 776, 460, 103, '5', 'Học giả Hội Trí Thức thẳng thắn và tự cao, thường che mặt bằng một tác phẩm điêu khắc thạch cao kỳ lạ.\r\n\r\nThể hiện trí thông minh phi thường từ khi còn nhỏ, nhưng bây giờ lại tự coi mình là một kẻ \"tầm thường\".', 'Dr.Ratio.jpg'),
(42, 'Ruan Mei', 3, 1, 1086, 659, 485, 104, '5', 'Hội viên Câu Lạc Bộ Thiên Tài, chuyên gia trong lĩnh vực khoa học sinh mệnh, cùng hợp tác với nhóm người Herta phát triển Vũ Trụ Mô Phỏng.', 'RuanMei.jpg'),
(43, 'Hanya', 3, 7, 917, 564, 352, 110, '4', 'Một trong những Phán Quan của Sở Thập Vương Xianzhou Luofu.\r\n\r\nNhận lệnh Thập Vương, chấp chưởng Thiên Bút Tiên Tri, đọc danh sách tội nghiệt và truyền đạt kết quả phán tội.', 'Hanya.jpg'),
(44, 'Argenti', 7, 7, 1047, 687, 339, 103, '5', 'Kỵ sĩ cổ điển của Đội Kỵ Sĩ Đẹp Thuần Khiết, thành kính tín ngưỡng Aeon \"Đẹp Thuần Khiết\" Idrila đã mất tích.\r\n\r\nNgay thẳng chính trực, chu du khắp vũ trụ, ca ngợi vẻ đẹp của Idrila.', 'Argenti.jpg'),
(45, 'Huohuo', 8, 5, 1358, 509, 601, 98, '5', 'Phán Quan thực tập của Sở Thập Vương Xianzhou Luofu, bé gái Người Hồ Ly bị Tuế Dương nhập.\r\n\r\nTính cách nhút nhát, yếu đuối, sợ hãi trước những sự việc quái dị, nhưng lại gánh trọng trách dụ dỗ và trấn áp tà ma.', 'Huohuo.jpg'),
(46, 'Guinaifen', 4, 2, 882, 582, 441, 106, '4', 'Nghệ sĩ tạm trú ở Xianzhou Luofu, hoặc có thể nói là, nghệ nhân đường phố.\r\n\r\nSau khi cơm no ba bữa, sẽ theo đuổi cuộc sống mới của mình ở Luofu.', 'Guinaifen.jpg'),
(47, 'Sushang', 6, 7, 917, 564, 418, 107, '4', 'Người lính trẻ sinh ra ở Xianzhou Yaoqing, đi đến Vân Kỵ Quân để huấn luyện.\r\n\r\nMang theo thanh kiếm gia truyền được mẹ tặng, hướng về tương lai mà bản thân sẽ viết nên.', 'Sushang.jpg'),
(48, 'Topaz & Numby', 6, 2, 931, 620, 412, 110, '5', 'Topaz, cán bộ cao cấp của Bộ Đầu Tư Chiến Lược thuộc Công Ty Hành Tinh Hòa Bình, lãnh đạo nhóm theo dõi khoản nợ đặc biệt.\r\n\r\nTuổi còn trẻ mà đã trở thành một trong \"10 người có trái tim đá\", với nền tảng là \"giục trả nợ\".\r\n\r\nCộng sự Heo Đất Không Gian \"Numby\" lại có thể cảm nhận nhạy bén về sự tồn tại của \"của cải\", ngay cả những việc liên quan đến an ninh, đòi nợ, thống kê v.v... cũng không thành vấn đề.\r\n\r\nNgày nay họ đang cùng nhau tuần tra ngân hà, theo đuổi các vấn đề tranh chấp nợ nần gây ảnh hưởng đến sự phát triển của công ty.', 'Topaz&Numby.jpg'),
(49, 'Jingliu', 2, 1, 1435, 679, 485, 96, '5', 'Từng là kiếm thủ hàng đầu của Luofu, người sáng lập Vân Kỵ Quân nổi tiếng bất bại.\r\n\r\nNhưng nay tên của cô đã bị xóa nhòa, trở thành phản đồ của Xianzhou đang đi trên ranh giới giữa sự tỉnh táo và Xác Nhập Ma.', 'Jingliu.jpg'),
(50, 'Lynx', 8, 4, 1058, 493, 551, 100, '4', 'Nhà thám hiểm Cánh Đồng Tuyết của Belobog, cô em út của nhà Landau.\r\n\r\nLà người điềm tĩnh, kiên định trong hành động. Thường đến Cánh Đồng Tuyết thám hiểm một mình.', 'Lynx.jpg'),
(51, 'Dan Heng - Ẩm Nguyệt', 2, 6, 1241, 698, 363, 102, '5', 'Nguyên trạng thật sự của Dan Heng tộc Vidyadhara, mang trên người sức mạnh Ẩm Nguyệt Quân để lại.\r\n\r\nKhi tiếp nhận chiếc sừng uy nghiêm trên đỉnh trán, anh ấy phải tiếp nhận tất cả công và tội một đời của tội nhân này.', 'DanHengIL.jpg'),
(52, 'Fu Xuan', 1, 4, 1474, 465, 606, 100, '5', 'Thủ lĩnh Sở Thiên Tượng Xianzhou Luofu.\r\n\r\nDựa vào con mắt thứ ba và Ma Trận Tiên Tri để tiên đoán đường đi của Xianzhou, đoán trước tốt xấu của sự việc.', 'FuXuan.jpg'),
(53, 'Luka', 4, 7, 917, 582, 485, 103, '4', 'Võ Sĩ Quyền Anh quán quân khu tầng dưới của Belobog, là một trong những thành viên đắc lực của Lửa Ngầm.\r\n\r\nQuán quân liên tục của Câu Lạc Bộ Đấu Quyền, dùng sự nhiệt huyết của bản thân để khích lệ những đứa trẻ khu tầng dưới thực hiện khát vọng của riêng mình.', 'Luka.jpg'),
(54, 'Kafka', 4, 3, 1086, 679, 485, 100, '5', 'Thành viên của \"Thợ Săn Stellaron\", mang vẻ đẹp của sự phóng khoáng, điềm đạm và chuyên nghiệp.\r\n\r\nSử dụng Bùa Mê Ngôn Từ để khiến Nhà Khai Phá hấp thụ Stellaron.\r\n\r\nSở thích là mua và sắp xếp áo khoác.', 'Kafka.jpg'),
(55, 'Blade', 2, 5, 1415, 569, 500, 97, '5', 'Thành viên của \"Thợ Săn Stellaron\", một kiếm khách từ bỏ thân mình, trở thành lưỡi kiếm sắc nhọn.\r\n\r\nTrung thành với \"Nô Lệ Của Số Phận\", sở hữu năng lực tự chữa lành đáng sợ.', 'Blade.jpg'),
(56, 'Yukong', 3, 6, 917, 599, 374, 107, '4', 'Đứng đầu Sở Thiên Bách Xianzhou Luofu, phi công và xạ thủ nhiều năm chinh chiến.\r\n\r\nNhưng nay lại vướng bận công vụ, khó thoát ra nổi.', 'Yukong.jpg'),
(57, 'Luocha', 8, 6, 1280, 756, 363, 101, '5', 'Lúc nào cũng mang bên mình một chiếc quan tài, là một thương nhân đến từ một nơi xa xôi bên ngoài biển lớn.\r\n\r\nY thuật cao siêu.', 'Luocha.jpg'),
(58, 'Sói Bạc', 4, 4, 1047, 640, 460, 107, '5', 'Thành viên của \"Thợ Săn Stellaron\", một bậc thầy hacker. Xem vũ trụ là một trò chơi mô phỏng nhập vai tầm cỡ và vui chơi trong đó.\r\n\r\nNắm giữ \"Trình Chỉnh Sửa Aether\" có thể sửa đổi dữ liệu hiện thực.', 'SilverWolf.jpg'),
(59, 'Bailu', 8, 3, 1319, 562, 485, 98, '5', 'Tôn trưởng của tộc Vidyadhara ở Xianzhou Luofu, là thầy thuốc có biệt danh \"Long Nữ Trị Liệu\".\r\n\r\nCứu kẻ sắp chết, giúp người bị thương bằng \"cách chữa trị\" mà chỉ có y thuật độc môn và long mạch mới có thể làm được.', 'Bailu.jpg'),
(60, 'Yanqing', 6, 1, 892, 679, 412, 109, '5', 'Thị vệ của Jing Yuan, nhưng lại là thiên tài trẻ tuổi.\r\n\r\nTay lăm lăm thanh kiếm, bất khả chiến bại.', 'Yanqing.jpg'),
(61, 'Jing Yuan', 7, 3, 1164, 698, 485, 99, '5', 'Một trong Đế Cung Thất Thiên Tướng, \"Tướng Quân Thần Sách\" chỉ huy Vân Kỵ Quân Xianzhou Luofu\r\n\r\nSư phụ là kiếm thủ hàng đầu tiền nhiệm của \"Luofu\", nhưng lại không được biết đến với tài năng võ thuật.', 'JingYuan.jpg'),
(62, 'Tingyun', 3, 3, 846, 529, 396, 112, '4', 'Tiếp Độ Sứ của Sở Thiên Bách Xianzhou Luofu.\r\n\r\nCùng thương đội đi khắp thế giới, liên kết mậu dịch và đồng minh.', 'Tingyun.jpg'),
(63, 'Qingque', 7, 4, 1023, 652, 441, 98, '4', 'Nhà chiêm tinh của Sở Thiên Tượng Xianzhou Luofu, kiêm quản lý kho sách.\r\n\r\nDo trốn việc lười biếng nên sắp bị giáng chức thành người giữ cửa.', 'Qingque.jpg'),
(64, 'Nhà Khai Phá - Bảo Hộ', 1, 2, 1241, 601, 606, 95, '5', '[Nam]\r\n\r\nThiếu niên lên Đội Tàu Astral.\r\n\r\nĐể diệt trừ hiểm họa mà Stellaron mang đến, đã quyết định đồng hành cùng đoàn tàu.\r\n\r\n[Nữ]\r\n\r\nThiếu nữ lên Đội Tàu Astral.\r\n\r\nĐể diệt trừ hiểm họa mà Stellaron mang đến, đã quyết định đồng hành cùng đoàn tàu.', 'TrailblazerPreservation.jpg'),
(65, 'Hook', 2, 2, 1340, 617, 352, 94, '4', 'Sếp của đội thám hiểm \"Băng Chuột Chũi\" dưới lòng đất (tự phong).\r\n\r\nYêu tự do, xem cuộc sống là cuộc phiêu lưu dài tập.', 'Hook.jpg'),
(66, 'Sampo', 4, 5, 1023, 617, 396, 102, '4', 'Con buôn đi lại giữa tầng trên và dưới mặt đất.\r\n\r\nAnh ấy hành động như thể là bạn của tất cả mọi người, nhiệt tình hài hước, rất giỏi pha trò.', 'Sampo.jpg'),
(67, 'Clara', 2, 7, 1319, 737, 485, 90, '5', 'Thiếu nữ lang thang sống cùng robot.\r\n\r\nHướng nội, dịu dàng, có một trái tim thuần khiết.\r\n\r\nCô có một khát khao là mọi người ở Thành Phố Ngầm đều trở thành \"người nhà\" của nhau.', 'Clara.jpg'),
(68, 'Pela', 4, 1, 987, 546, 463, 105, '4', 'Sĩ quan tình báo Thiết Vệ Bờm Bạc.\r\n\r\nChăm chỉ, được các thành viên của đội thiết vệ vô cùng kính trọng.', 'Pela.jpg'),
(69, 'Natasha', 8, 7, 1164, 476, 507, 98, '4', 'Bác sĩ của Thành Phố Ngầm ở Belobog, cũng là người chăm sóc bọn trẻ.\r\n\r\nLà người dịu dàng gần gũi nhưng cũng ẩn chứa một mặt nguy hiểm.', 'Natasha.jpg'),
(70, 'Gepard', 1, 1, 1397, 543, 654, 92, '5', 'Đội trưởng Thiết Vệ Bờm Bạc, chiến sĩ hạng nhất nhì ở Belobog.\r\n\r\nTư tưởng và hành động nhất quán, tỉ mỉ cẩn thận, chưa từng buông thả.', 'Gepard.jpg'),
(71, 'Serval', 7, 3, 917, 652, 374, 104, '4', 'Thợ cơ khí Belobog, đã từng là chuyên viên nghiên cứu ở bộ phận kỹ thuật Đấng Kiến Tạo.\r\n\r\nLà chị của Gepard Landau nhưng tính cách lại khác hoàn toàn với em mình.\r\n\r\nYêu thích nghệ thuật âm nhạc cổ xưa có tên là rock \'n\' roll từ trước khi băng giá vĩnh cửu ập đến.', 'Serval.jpg'),
(72, 'Seele', 6, 4, 931, 640, 363, 115, '5', 'Trụ cột của tổ chức kháng chiến dưới lòng đất \"Lửa Ngầm\", biệt hiệu là \"Bươm Bướm\".\r\n\r\nTính tình thẳng thắn bộc trực, nhưng sâu trong tim lại là một người vô cùng tinh tế nhạy cảm.', 'Seele.jpg'),
(73, 'Bronya', 3, 5, 1241, 582, 533, 99, '5', 'Người kế thừa của Đấng Bảo Vệ Tối Cao Belobog.\r\n\r\nVừa có khí chất kiêu ngạo của công chúa vừa có tính kiên trung của một quân nhân.', 'Bronya.jpg'),
(74, 'Herta', 7, 1, 952, 582, 396, 100, '4', 'Thành viên số 83 của Câu Lạc Bộ Thiên Tài, chủ nhân thật sự của Trạm Không Gian.\r\n\r\nMột nhà khoa học cực kỳ thông minh nhưng không có lòng đồng cảm.', 'Herta.jpg'),
(75, 'Asta', 3, 2, 1023, 511, 463, 106, '4', 'Trạm trưởng của Trạm Không Gian Herta, tiểu thư con nhà danh giá.\r\n\r\nNhà nghiên cứu thiên văn ham học hỏi, rất giỏi trong việc quản lý các nhân viên với nhiều tính cách khác nhau ở Trạm Không Gian.', 'Asta.jpg'),
(76, 'Arlan', 2, 3, 1199, 599, 330, 102, '4', 'Người phụ trách bộ phận an ninh của Trạm Không Gian Herta.\r\n\r\nKhông giỏi ăn nói, hi vọng bảo vệ được những người đam mê nghiên cứu, để họ hoàn thành việc nghiên cứu một cách thuận lợi.', 'Arlan.jpg'),
(77, 'Welt', 4, 6, 1125, 620, 509, 102, '5', 'Tiền bối dày dạn kinh nghiệm của Đội Tàu Astral.\r\n\r\nSự nhiệt huyết tận sâu trong đáy lòng lại một lần nữa được thổi bùng để tận hưởng cuộc phiêu lưu đã từ lâu chưa dấn thân.\r\n\r\nThỉnh thoảng sẽ vẽ lại hành trình phiêu lưu của mình vào một quyển sổ.', 'Welt.jpg'),
(78, 'Himeko', 7, 2, 1047, 756, 436, 96, '5', 'Người sửa chữa Đội Tàu Astral.\r\n\r\nĐể nhìn thấy tận mắt vũ trụ bao la, cô đã chọn đi cùng Đội Tàu.\r\n\r\nSở thích của cô là pha cà phê thủ công.', 'Himeko.jpg'),
(79, 'Nhà Khai Phá - Hủy Diệt', 2, 7, 1203, 620, 460, 100, '5', '[Nam]\r\n\r\nThiếu niên lên Đội Tàu Astral.\r\n\r\nĐể diệt trừ hiểm họa mà Stellaron mang đến, đã quyết định đồng hành cùng đoàn tàu.\r\n\r\n[Nữ]\r\n\r\nThiếu nữ lên Đội Tàu Astral.\r\n\r\nĐể diệt trừ hiểm họa mà Stellaron mang đến, đã quyết định đồng hành cùng đoàn tàu.', 'TrailblazerDestruction.jpg'),
(80, 'Evernight', 5, 1, 1319, 543, 582, 99, '5', '\"Khách Lạ Bí Ẩn\" Evernight\r\n\r\nVùng Hồi Ức tách biệt với thế giới, ánh nến phản chiếu quá khứ, rồi lặng lẽ lụi tắt trong màn sương.\r\n\r\nĐứa con của Ký Ức đến từ bóng tối - Evernight, Hậu Duệ Chrysos che giấu Ngọn Lửa \"Thời Gian\", khơi dậy sóng thủy triều của sự \"Lãng Quên\", bảo vệ tâm nguyện của người trong gương.\r\n\r\n-\"Đừng lo lắng, tôi sẽ bảo vệ con đường \"Khai Phá\" của cô... bằng mọi giá ♭\"', 'Evernight.jpg'),
(81, 'Dan Heng - Permansor Terrae', 1, 7, 1048, 582, 776, 97, '5', '\"Hoang Long Đằng Phi\" Dan Heng - Permansor Terrae\r\n\r\nLồng ngực của Georios, thân thể của Phục Long đang nâng đỡ mặt đất vỡ nát, chịu đựng nỗi thống khổ ngàn năm.\r\n\r\nKhách Vô Danh Dan Heng, Hậu Duệ Chrysos bảo vệ Ngọn Lửa của \"Mặt Đất\", chống đỡ bát hoang sắp sụp đổ, đưa sinh linh trên mặt đất đến những vùng đất xa xôi\r\n\r\n-Trăm sông đổ về biển, núi non đồng vọng tiếng vang, con đường bất diệt sẽ trải dài vạn dặm.', 'DanHengPT.jpg'),
(82, 'Cyrene', 5, 1, 1396, 4466, 582, 101, '5', '\"Gợn Sóng Quá Khứ\" Cyrene\r\n\r\nSao băng vụt qua bầu trời đêm, dòng sông sinh mệnh gợn sóng lăn tăn, lấp lánh mười ba sắc màu.\r\n\r\nNgười con gái của Aedes Elysiae, Hậu Duệ Chrysos nuôi dưỡng \"██\", hãy gieo mầm ký ức, để những đóa hoa quá khứ bung nở vào ngày mai', 'Cyrene.jpg');

