--
-- Cấu trúc bảng cho bảng `lightcones`
--

CREATE TABLE `lightcones` (
  `id` int(11) NOT NULL,
  `name` varchar(100) NOT NULL,
  `path_id` int(11) DEFAULT NULL,
  `hp` int(11) DEFAULT NULL,
  `atk` int(11) DEFAULT NULL,
  `def` int(11) DEFAULT NULL,
  `rarity` enum('4','5') NOT NULL,
  `description` text DEFAULT NULL,
  `image` varchar(255) DEFAULT NULL,
  `path_effect` text DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `lightcones`
--

INSERT INTO `lightcones` (`id`, `name`, `path_id`, `hp`, `atk`, `def`, `rarity`, `description`, `image`, `path_effect`) VALUES
(1, 'Hướng Về Điều Không Thể Truy Hỏi', 7, 952, 635, 463, '5', '\"Thư của Hội Trí Thức đã xử lý xong.\"\r\n\r\n\"Vũ Trụ Mô Phỏng đàng vận hành bình thường...\"\r\n\r\n\"Tuyển tập tác phẩm đã được tái bản.\".\r\n\r\n\"Suỵt, nhỏ tiếng thôi, đừng làm ôn đến quý cô Herta...\"\r\n\r\n....Trong tòa tháp cao ở ranh giới vũ trụ, các Con Rối đang xử lý các việc linh tinh, đợi cô ấy xuất hiện. Thời gian trôi qua nhanh, vô số nhân vật như những ngôi sao băng lướt qua bầu trời đêm của lịch sử, tỏa sáng trong chốc lát, cuối cùng trở nên tĩnh lặng.\r\n\r\n\"Quý cô Herta đã rất lâu rồi không lộ diện...\"\r\n\r\n\"Cô ấy vẫn đang suy nghĩ sao...\"\r\n\r\n\"Hừm; tưởng đưa ra vài tiền đề chân lý là kết thúc rồi sao?\"\r\n\r\nCô ấy duỗi lưng, ngẩng đầu lên trong lúc tính toán, hình như lại có ý tưởng...\r\n\r\n\"Không có gì là không thể nghi ngờ cả, tôi phải hỏi đến cùng!\"', 'uploads/lightcones/Hướng Về Điều Không Thể Truy Hỏi.jpg', 'Khiến Tỷ Lệ Bạo Kích của người trang bị tăng 12%/14%/16%/18%/20%. Khi người trang bị thi triển Tuyệt Kỹ, sẽ khiến Chiến Kỹ của người trang bị và Sát Thương Tuyệt Kỹ gây ra tăng 60%/70%/80%/90%/100%, duy trì 3 hiệp. Sau khi người trang bị thi triển Tuyệt Kỹ, nếu Năng Lượng tiêu hao trong lần Tuyệt Kỹ này lớn hơn hoặc bằng 140 điểm, sẽ hồi 1 điểm Chiến Kỹ.'),
(2, 'Dệt Thời Gian Thành Vàng', 5, 1058, 635, 396, '5', '\"Sợi này là sự hy sinh của chiến sĩ.\"\r\n\r\n\"Sợi này là sự trưởng thành của ảnh hùng.\"\r\n\r\n\"Sợi này là sự phồn vinh của thành bang.\"\r\n\r\n...\r\n\r\nNgười thợ may kéo các sợi tơ và gom chúng lại trên đầu ngón tay cô..\r\n\r\n\"Quỹ đạo của một số anh hùng đã bị cắt đứt...\"\r\n\r\nCô kéo một số sợi tơ vàng khác lên, rồi khâu lại.\r\n\r\n\"Vận mệnh của một số anh hùng, vẫn phải mở rộng về phía trước...\"\r\n\r\nKhung cửi được ban cho thần tính đang tự vận hành, những người thợ may đang nhảy mùa duyên dáng. Thời gian ngàn năm trôi qua trên khung cửi, từ lâu cô ấy đã quen với việc chờ đợi như vậy...\r\n\r\nVì \"cảm hứng\" đem thời gian dệt thành vàng.', 'uploads/lightcones/Dệt Thời Gian Thành Vàng.jpg', 'Tốc Độ Cơ Bản của người trang bị tăng 12/14/16/18/20, người trang bị và Linh Hồn Ký Ức của người trang bị sau khi tấn công sẽ khiến người trang bị nhận 1 tầng Dệt Gấm, mỗi tầng Dệt Gấm khiến sát thương bạo kích của người trang bị và Linh Hồn Ký Ức của người trang bị tăng 9,0%/10,5%/12%/13,5%/15%, tối đa cộng dồn 6 tầng. Khi cộng dồn đến tối đa, mỗi tầng Dệt Gấm sẽ khiến Sát Thương Tấn Công Thường gây ra tăng thêm 9,0%/10,5%/12%/13,5%/15%.'),
(3, 'Nếu Thời Gian Là Một Đóa Hoa', 3, 1270, 529, 396, '5', '\"Mẹ ơi, con muốn nghe thêm một câu chuyện nữa!\"\r\n\r\n\"Con gái ngoan của mẹ, hôm nay mẹ sẽ kể câu chuyện về thời gian nhé?\"\r\n\r\n\"Thời gian... là gì vậy mẹ?\"\r\n\r\n\"Thời gian, giống như một đóa hoa.\"\r\n\r\n\"Là loài hoa bồ công anh nở rộ trên cánh đồng sao?\"\r\n\r\n\"Đúng vậy, giống như hoa bồ công anh vậy, theo thời gian rồi sẽ trưởng thành, sẽ nở rộ, sẽ kết trái, chúng ta gọi cánh hoa của nó là... \'thời khắc\'.\"\r\n\r\n\"Sau đó thì sao?\"\r\n\r\n\"Sau đó, nó sẽ khô héo thành hàng ngàn cánh hoa, để lại những hạt giống bay đi khắp nơi...\"\r\n\r\n\"Mẹ ơi, đó có phải gọi là \'tương lai\' không!\"\r\n\r\n\"Đoán xem sau này nó sẽ trở thành gì nào?\"\r\n\r\n\"Nó sẽ biến thành rất nhiều rất nhiều hoa, biến thành một biển hoa rực rỡ?\"\r\n\r\nNgười mẹ chưa kịp trả lời, ánh nắng bên ngoài cửa sổ chiếu xuống gương mặt cô bé, bóng dáng dịu dàng đó một lần nữa biến mất trong giấc mơ.\r\n\r\nCô mở mắt, ra...\r\n\r\n\"Nếu thời gian là một đóa hoa...\" Cô ấy lẩm bẩm.\r\n\r\n\"Nó nhất định sẽ biến thành một biển hoa.\" Cô gái trầm tĩnh nói.\r\n\r\n\"Đương nhiên rồi!\" Cô gái tinh nghịch đó đã hoàn thành kiệt tác và cất tiếng hò reo.', 'uploads/lightcones/Nếu Thời Gian Là Một Đóa Hoa.jpg', 'Khiến sát thương bạo kích của người trang bị tăng 36%/42%/48%/54%/60%. Sau khi người trang bị thi triển Đòn Đánh Theo Sau sẽ hồi thêm 12 Năng Lượng và nhận Chỉ Dẫn, duy trì 2 hiệp. Khi người trang bị có Chỉ Dẫn, sát thương bạo kích của toàn bộ mục tiêu phe ta tăng 48%/60%/72%/84%/96%. Khi vào chiến đấu, người trang bị hồi 21 Năng Lượng và nhận Chỉ Dẫn, duy trì 2 hiệp.'),
(4, 'Huyết Hỏa, Hãy Thiêu Đốt Con Đường Phía Trước', 2, 1375, 476, 396, '5', 'Giọt sương lạnh lẽo ngưng kết trên lưỡi đao, trượt xuống thành những vết sẹo màu máu.\r\n\r\nVùng đất hoang nhuốm mùi gỉ sét, tàn dự của ngọn lửa bùng lên trong trận chiến khốc liệt vẫn nhảy múa trên đống tro tàn.\r\n\r\n\"Mydeimos, vừa rồi đúng là một trận chiến thần thánh!\"\r\n\r\nChiến sĩ ngực trần lau vũ khí, hét lên một cách đầy phấn khích.\r\n\r\n\"Mydei, sau này em cũng muốn giống anh, trăm trận trăm thẳng!\"\r\n\r\nĐứa trẻ nhìn anh một cách sùng bái, trong tay vẫn nắm chặt thanh kiếm gãy.\r\n\r\n\"Vương tử, ngày chúng ta quay về Kremnos đã cận kề.\"\r\n\r\nÔng lão đã trải qua bao thăng trầm của cuộc đời nở nụ cười nâng ly rượu lên kính anh.\r\n\r\nAnh im lặng gật đầu, rồi bước đến rìa doanh trại.\r\n\r\nCơn đau của việc tái sinh vẫn còn chạy dọc trên da, ngày mai, anh sẽ dẫn dắt Đội Quân Đơn Độc Kremnos xuất phát một lần nữa, đi đến những vùng đất xa lạ.\r\n\r\nAnh không biết cuộc sống lưu lạc này sẽ kết thúc ở đâu, điều duy nhất anh có thể chắc chắn là...\r\n\r\nAnh sẽ gánh vác chức trách của một vị vua, cho đến khi ngọn lửa máu bị thiêu rụi.\r\n\r\nBóng đêm dần buông xuống, anh một mình uống rượu máu, nhà thơ lang thang đi ngang qua kéo dây đàn, giọng hát lạc điệu vang vọng trong không trùng...\r\n\r\n\"Quê nhà là vùng đất của những giấc mơ, không phải nơi có thể đến.\"', 'uploads/lightcones/Huyết Hỏa, Hãy Thiêu Đốt Con Đường Phía Trước.jpg', 'Giới Hạn HP của người trang bị tăng 18%/21%/24%/27%/30%, lượng trị liệu nhận được tăng 20%/25%/30%/35%/40%, thi triển Chiến Kỹ hoặc Tuyệt Kỹ sẽ tiêu hao HP bằng 6,0%/6,5%/7,0%/7,5%/8,0% Giới Hạn HP của bản thân, đồng thời khiến sát thương lần tấn công này gây ra tăng 30%/35%/40%/45%/50%, nếu lượng HP mà hiệu ứng này tiêu hao lớn hơn 500, thì sẽ khiến sát thương tăng thêm 30%/35%/40%/45%/50%. Nếu HP hiện tại không đủ, hiệu ứng này tối đa khiến HP hiện tại của người trang bị giảm đến 1.'),
(5, 'Để Lời Từ Biệt Đẹp Đẽ Hơn', 5, 1270, 529, 396, '5', 'Lại một nghi thức chia ly nữa.\r\n\r\nCô gái bước đi trên ánh trăng vỡ vụn, đan vòng hoa tiễn biệt bên bờ sông.\r\n\r\nKhúc bi ca cổ xưa đông cứng trong không khí lạnh lẽo, dòng sông tĩnh lặng chảy về miền hoa xa xôi.\r\n\r\nQuyển sách nhuốm máu, thanh kiếm gỉ sét, chiếc khăn tay thêu vần thơ... cô tiếp nhận di vật và những câu chuyện họ để lại.\r\n\r\n\"Đôi mắt ta như thấu kính, mãi hoài niệm về bầu trời sao.\"\r\nĐó là bia mộ dành cho vị học giả yểu mệnh.\r\n\r\n\"Đối với đời người hay cái chết, kỵ sĩ chỉ nhìn bằng một ánh mắt lạnh nhạt.\"\r\n\r\nĐó là châm ngôn của chiến binh vô danh.\r\n\r\n\"Sinh mệnh chỉ là cái chết lấp lánh ánh sáng.\"\r\n\r\nĐó là áng thơ cuối cùng của thi nhân theo đuổi sự vĩnh hằng, được viết nên bằng chính mạng sống của mình.\r\n\r\n...\r\n\r\n\"Mỗi đóa hoa, đều từng kiêu hãnh khoe sắc...\"\r\n\r\nCùng với tiếng nức nở của dòng sông, cô dâng lên người đã khuất những vần thơ, vòng hoa và sự tưởng niệm...\r\n\r\n\"Nếu héo úa là điều không thể tránh khỏi, ít nhất hãy để lời từ biệt của chúng ta... đẹp đẽ hơn một chút.\"', 'uploads/lightcones/Để Lời Từ Biệt Đẹp Đẽ Hơn.jpg', 'Khiến Giới Hạn HP của người trang bị tăng 30%/37,5%/45%/52,5%/60%. Khi người trang bị hoặc Linh Hồn Ký Ức của người trang bị mất HP trong hiệp của mình, người trang bị sẽ nhận Hoa Âm Ty. Hoa Âm Ty khiến người trang bị và Linh Hồn Ký Ức của họ khi gây sát thương có thể bỏ qua 30%/35%/40%/45%/50% phòng thủ của mục tiêu, duy trì 2 hiệp. Khi Linh Hồn Ký Ức của người trang bị biến mất, người trang bị sẽ ưu tiên hành động 12%/15%/18%/21%/24%. Hiệu ứng này chỉ kích hoạt tối đa 1 lần, và sẽ được làm mới số lần mỗi khi người trang bị thi triển Tuyệt Kỹ.'),
(6, 'Sinh Mệnh Đáng Bị Thiêu Rụi', 7, 952, 582, 529, '5', 'Trong thí nghiệm u tối đó, luôn có những ảo ảnh ùa về, níu kéo bước chân anh.\r\n\r\nTiếng thầm thì đầy mê hoặc vang lên, bảo rằng hãy từ bỏ đề tài nguy hiểm đó, sao phải lấy sinh mạng của mình ra đánh cược chứ.\r\n\r\nTiếng đe dọa lạnh lùng cảnh báo, kẻ bước đi trên ranh giới sinh tử cuối cùng cũng sẽ tự thiêu rụi mình.\r\n\r\nVà lời cáo buộc đầy phẫn nộ, gào thét đòi đẩy kẻ báng bổ thần linh xuống pháp trường tàn khốc.\r\n\r\n\"Vở diễn của cuộc đời đã đến hồ khép lại.\"\r\n\r\nAnh nhắm mắt, nắm chặt Ngọn Lửa của thần linh, mặc cho nỗi đau nhấn chìm linh hồn cận kề cái chết.\r\n\r\nĐôi mắt kiên định, trái tim đập rộn ràng, một linh hồn hoàn chỉnh... Anh đích thần đưa bản thân vào sự dung luyện thuận khiết và mãnh liệt ấy...\r\n\r\nMuôn vàn âm thanh sôi sục, rồi trở nên tĩnh lặng.\r\n\r\nTrong màn đêm vô tận, chân lý rực rỡ đã thành hình trong tay anh.', 'uploads/lightcones/Sinh Mệnh Đáng Bị Thiêu Rụi.jpg', 'Khi bắt đầu hiệp của người trang bị sẽ hồi 10 Năng Lượng. Nếu mục tiêu phe địch có điểm yếu được thêm vào bởi người trang bị, thì sát thương người trang bị gây ra cho kẻ đó tăng 60%/70%/80%/90%/100%. Khi mục tiêu phe địch bị người trang bị tấn công, người trang bị sẽ khiến kẻ đó giảm 12%/15%/18%/21%/24% phòng thủ, duy trì 2 hiệp. Hiệu ứng cùng loại không thể cộng dồn.'),
(7, 'Mong Cầu Vồng Mãi Ở Trên Bầu Trời', 5, 1164, 476, 529, '5', '\"Cảm xúc giống như những đám mây, còn chúng ta là bầu trời chứa đựng nó. Nếu bầu trời giăng đầy mây đen, tâm trạng sẽ trở nên u ám và mơ hồ.\"\r\n\r\nNgười thầy thuốc quan tâm lắng nghe nỗi đau của người ghé đến, an ủi bằng lời nói nhẹ mang tính trị liệu.\r\n\r\n\"Hãy nhắm mắt lại, từ từ hít thở... Tưởng tượng bản thân đang ở một nơi ấm áp, và dịu dàng...\"\r\n\r\nLời nói như có ma lực, ánh sáng rực rỡ bảy màu dưới đôi cánh của Thú Cánh lan tỏa.\r\n\r\n\"Cơn gió nhẹ thổi qua cánh đồng, mang theo hương thơm trong trẻo của hoa dại. Những đám mây ẩm ướt lững lờ bay qua, mang theo ánh mặt trời...\"\r\n\r\nVị khách dần chìm vào giấc mơ đẹp, chỉ có tiếng hít thở đều đặn và nhẹ nhàng xem như lời hồi đáp.\r\n\r\nSau khi khám xong cho bệnh nhân cuối cùng, người thầy thuốc nhẹ nhàng khóa cửa phòng bệnh.\r\n\r\nĐêm đen vô tận vẫn đang cuộn trào ở nơi xa xăm, những đống phế tích giống như vết thương trên mặt đất.\r\n\r\n\"Kiếp sau, mong bình minh sẽ chiếu sáng cho mọi người...\"\r\n\r\nTrước khi khởi hành, cô ấy đã cầu - nguyện với bầu trời...\r\n\r\n\"Nguyện cho ánh cầu vồng mãi ở trên bầu trời.\"', 'uploads/lightcones/Mong Cầu Vồng Mãi Ở Trên Bầu Trời.jpg', 'Khiến Tốc Độ của người trang bị tăng 18%/21%/24%/27%/30%. Khi người trang bị thi triển Tấn Công Thường, Chiến Kỹ và Tuyệt Kỹ sẽ tiêu hao HP của toàn thể phe ta bằng 1,0%/1,2%/1,5%/1,8%/2,0% HP hiện tại và khiến Linh Hồn Ký Ức của người trang bị sau lận tấn công kế tiếp sẽ gây thêm 1 lần Sát Thượng Kèm Theo dựa trên thuộc tính Linh Hồn Ký Ức của người trang bị bằng 250,0%/312,5%/375%/437,5%/500,0% tổng lượng HP tiêu hao cho mục tiêu bị tấn công, sau đó xóa tổng lượng HP tiêu hao. Khi Linh Hồn Ký Ức của người trang bị thi triển Kỹ Năng Linh Hồn Ký Ức, sẽ khiến sát thương toàn phe địch phải chịu tăng 18,0%/22,5%/27%/31,5%/36,0%, duy trì 2 hiệp. Hiệu ứng cùng loại không thể cộng dồn.'),
(8, 'Lời Nói Dối Bay Trong Gió', 4, 952, 582, 529, '5', 'Thiếu nữ tai mèo vươn người ra khỏi nóc nhà, lướt vào màn đêm.\r\n\r\nTrong kho báu ngàn năm yên tĩnh, thiếu nữ lướt đi nhẹ nhàng, ôm bảo vật vào lòng, rồi biến mất không một tiếng động.\r\n\r\n\"Vận mệnh ngu xuẩn, không đuổi kịp ta đâu!\"\r\n\r\nNgọn gió trong phế tích mang theo mùi hương hoang dã, thổi lay chiếc mũ của thiếu nữ.\r\n\r\nGối đầu lên tảng đá lạnh lẽo, cô nuốt xuống miếng lương khô thô cứng, tung hứng đồng vàng trên đầu ngón tay.\r\n\r\nTiếng vọng khe khẽ từ phương xa lọt vào đôi tai cô, trong Thánh Thành được ánh bình minh chiếu rọi, đám đông nhộn nhịp vẫn đang tận hưởng bữa tiệc và sự ấm áp.\r\n\r\n\"Còn bao lâu nữa... một năm, hay là một vạn năm?\"\r\n\r\nCô duỗi chiếc lưng lười nhác, vứt bỏ những suy nghĩ bất an.\r\n\r\n\"Ha, Cifera sẽ luôn cười đến cùng!\"\r\n\r\nSấm chớp màu vàng lướt qua bầu trời, lời tuyên ngôn của người thiếu nữ vẫn bay trong gió\r\n\r\n-Như hàng nghìn năm trước.', 'uploads/lightcones/Lời Nói Dối Bay Trong Gió.jpg', 'Khiến Tốc Độ của người trang bị tăng 18%/21%/24%/27%/30%. Sau khi người trang bị thi triển tấn công, có 120% xác suất cơ bản khiến mỗi một mục tiêu phe địch rơi vào trạng thái \"Mơ Hồ\", mục tiêu phe địch trong trạng thái \"Mơ Hồ\" sẽ bị giảm 16%/18%/20%/22%/24% Phòng Thủ, duy trì 2 hiệp, nếu Tốc Độ của người trang bị lớn hơn hoặc bằng 170, sẽ có 120% xác suất cơ bản khiến mỗi một mục tiêu phe địch rơi vào trạng thái \"Mất Cắp\", mục tiêu phe địch trong trạng thái \"Mất Cắp\" sẽ bị giảm 8%/9%/10%/11%/12% Phòng Thủ, duy trì 2 hiệp. \"Mơ Hồ\" hoặc \"Mất Cắp\" khi bị thi triển trùng lặp, thì chỉ có trạng thái thi triển mới nhất là có hiệu lực.'),
(9, 'Bình Minh Rực Cháy', 2, 952, 687, 396, '5', 'Lại một lần nữa đứng ở cuối hành trình, bóng người đàn ông bị ánh mặt trời rực cháy nuốt chửng.\r\n\r\n\"Dòng máu vàng đó, hy vọng cứu thế, thì ra...\"\r\n\r\nNước mắt anh ta chưa kịp rơi đã bốc hơi mất.\r\n\r\n\"Phainon, ngày mai gặp lại!\"\r\n\r\n\"Đấng Cứu Thể, nhớ sống đến cuối cùng nhé!\"\r\n\r\n\"Hãy trở thành bình minh của Amphoreus đi...\"\r\n\r\n...\r\n\r\nNguyện vọng của mọi người vang vọng trong hư không, Anh Hùng Vô Danh sụp đổ, lộ ra cái chết tàn khốc.\r\n\r\n\"Nếu lửa giận không đủ để thiêu rụi vận mệnh phi lý...\"\r\n\r\nAnh ấy hết lần này đến lần khác leo lên đỉnh núi, rồi lại hết lần này đến lần khác rơi xuống vỡ vụn...\r\n\r\n\"Vậy thì hãy để tôi cháy cùng nó!\"', 'uploads/lightcones/Bình Minh Rực Cháy.jpg', 'Khiến Tốc Độ cơ bản của người trang bị tăng 12/14/16/18/20, khi gây sát thương sẽ bỏ qua 18%/22%/27%/31%/36% Phòng Thủ của mục tiêu. Sau khi người trang bị thi triển Tuyệt Kỹ sẽ nhận được \"Mặt Trời Rực Lửa\", khi bắt đầu hiệp sẽ xóa. Khi có \"Mặt Trời Rực Lửa\", sát thương do người trang bị gây ra tăng 60%/78%/96%/114%/132%.'),
(10, 'Địa Ngục Thiêu Đốt Lý Tưởng', 6, 952, 582, 529, '5', 'Trên cánh đồng, nghiệp hỏa thiêu đốt. Không biết lý tưởng của ai đã hóa thành tro bụi. Không biết lý tưởng của ai đã bùng cháy.\r\n\r\nNgười bảo vệ len lỏi giữa làn đạn và tên bay.\r\nỞ đây không có đối tượng anh ta có thể cứu. Ở đây đầy đối tượng mà anh ta muốn cứu.\r\n\r\nCứu một người, giết một người. Cứu vạn người, giết vạn người. Số phận giẫm đạp lẫn nhau cũng là một phần trong sự tiếp nối của nhân loại.\r\n\r\nTừ lâu đã quen với mọi thứ, bước chân của người bảo vệ chưa từng do dự.\r\nÝ chí bao trùm hai chân như gió. Ý chí bao trùm hai tay như lửa.\r\n\r\nAnh ta vẫn đang tìm kiếm kỳ tích của sự cứu rỗi.', 'uploads/lightcones/Địa Ngục Thiêu Đốt Lý Tưởng.jpg', 'Khiến Tỷ Lệ Bạo Kích của người trang bị tăng 16%/20%/24%/28%/32%. Khi vào chiến đấu, nếu giới hạn Điểm Chiến Kỹ phe ta lớn hơn hoặc bằng 6, sẽ khiến Tấn Công của người trang bị tăng 40%/50%/60%/70%/80%. Sau mỗi lần người trang bị thi triển Chiến Kỹ, sẽ khiến Tấn Công của người trang bị tăng 10%/12,5%/15%/17,5%/20%, tối đa cộng dồn 4 lần.'),
(11, 'Lễ Đăng Quang Không Hồi Đáp', 2, 953, 582, 529, '5', '\"Merlin, thanh kiếm này không hề khó rút.\"\r\n\"Đúng là như vậy. Đối với ngươi, trở thành vị vua mà mọi người mong đợi, không khó chút nào. Dù sao cũng có ta hướng dẫn ngươi mà. Nhưng, Altria... khó khăn thật sự là ngươi không thể vĩnh viễn đáp ứng sự mong đợi của mọi người.\"\r\n\"Đến lúc đó, họ có thể chọn ra vị vua phù hợp hơn. Ta sẽ không có ý kiến.\"\r\n\"Nếu loại người này vốn dĩ không tồn tại thì sao?\"\r\n\"...Vậy thì ta sẽ cố gắng để đáp lại sự mong đợi của mọi người.\"\r\n\r\nBây giờ, thiếu nữ đã sớm hiểu rằng đây sẽ là một bi kịch đến nhường nào. Nếu lúc này là thời khắc ban đầu ấy, liệu cô có lựa chọn từ bỏ?\r\n\r\nVị vua không nói gì. Cô chỉ nắm chặt thanh kiếm.\r\nVà một lần nữa, âm thanh rút kiếm lại phát ra từ phiến đá.', 'uploads/lightcones/Lễ Đăng Quang Không Hồi Đáp.jpg', 'Khiến Sát Thương Bạo Kích của người trang bị tăng 36%/45%/54%/63%/72%. Khi thi triển Tuyệt Kỹ, sẽ khiến Tấn Công của người trang bị tăng 40%/50%/60%/70%/80%, nếu giới hạn Năng Lượng của người trang bị lớn hơn hoặc bằng 300 điểm, sẽ cố định hồi cho người trang bị lượng Năng Lượng bằng 10% Giới Hạn Năng Lượng của người trang bị, đồng thời một lần nữa khiến Tấn Công của người trang bị tăng 40%/50%/60%/70%/80%, duy trì 2 hiệp.'),
(12, 'Trang Tiếp Theo Của Câu Chuyện', 5, 1058, 370, 396, '4', 'Gió thổi lay động cánh đồng lúa mì, cô bé ôm cuốn sổ tay trắng, thong thả bước đi.\r\n\r\nBên ngoài Aedes Elysiae, nơi thành bang vừa lạ lẫm vừa quen thuộc, những người bạn muôn màu muôn vẻ sẽ gặp gỡ nhau, và một hành trình như mộng như ca sắp bắt đầu...\r\n\r\n\"Lần này, hãy cùng nhau...\"\r\n\r\nỞ cuối cánh đồng lúa mì, cô bé chờ đợi đã lâu khép trang sách lại, vươn tay về phía \"tương lai\"...\r\n\r\n\"Viết nên chương mới cho câu chuyện nhé ♪\"', 'uploads/lightcones/Trang Tiếp Theo Của Câu Chuyện.jpg', 'Khiến Giới Hạn HP của người trang bị tăng 16%/20%/24%/28%/32%. Sau khi Linh Hồn Ký Ức của người trang bị tấn công, sẽ khiến Lượng Trị Liệu của người trang bị và Linh Hồn Ký Ức tăng 12%/15%/18%/21%/24%, duy trì 1 hiệp.'),
(13, 'Mong Hành Trình Mãi Mãi Bình An', 1, 1058, 370, 529, '4', 'Cậu bé chạy nhảy dưới cơn mưa như sao rơi, reo hò vì cơn mưa ngọt ngào đã đến sau một thời gian dài hạn hán.\r\n\r\nCơn mưa ấy kéo dài rất lâu, đoàn xe dừng lại giữa vùng đất xanh mướt.\r\n\r\n\"Mẹ ơi, chị ơi, chúng ta sẽ không phải chịu đói chịu rét nữa rồi!\"\r\n\r\nLửa trại của người Avgin sưởi ấm cả đêm dài, cậu bé nghe câu chuyện của chị gái, chờ đón bình minh.\r\n\r\n\"Kakavasha, em cũng sẽ mang theo may mắn được Mẹ Gaiathra ban tặng, để làm nên những điều mà chúng ta chưa từng làm được... Mong hành trình của em mãi mãi bình an, quỷ kế không bao giờ bị vạch trần...\"\r\n\r\nÁnh nắng vàng óng từ giấc mơ phản chiếu vào hiện thực tĩnh lặng, nơi giao dịch và những con số lại hiện về trong tầm mắt.\r\n\r\nCậu buông bàn tay đang nắm chặt...\r\n\r\nDường như không nắm bắt được gì, nhưng dường như vẫn còn chút hơi ấm.', 'uploads/lightcones/Mong Hành Trình Mãi Mãi Bình An.jpg', 'Khiến lượng Khiên mà người trang bị cung cấp tăng 12%/15%/18%/21%/24%, khi mục tiêu phe ta có Khiên, sát thương gây ra tăng 12%/14%/16%/18%/20%.'),
(14, 'Cho Đến Ngày Mai Của Ngày Mai', 8, 1058, 476, 396, '4', '\"Tribios, ngày mai gặp lại...\"\r\n\r\nKhi mẹ đi xa, cô bé lau đi những giọt nước mắt trên khuôn mặt.\r\n\r\nCô chờ đợi ngày này qua ngày khác, cho đến khi cô trở thành một người như mẹ mình. Cô đứng trước cánh cửa, nắm chặt ngọn lửa trong tay, bên tai vang lên lời nói của mẹ.\r\n\r\n\"Nơi đó, không có gió tuyết, rét buốt và mưa giông...\"\r\n\r\n\"Nhưng nếu ngày mai không gặp được...\"\r\n\r\nCô ngã xuống đất, để lại bóng dáng non nớt như sao băng lướt qua bầu trời...\r\n\r\n\"Vậy thì ngày mai của ngày mai gặp lại nhé!\"', 'uploads/lightcones/Cho Đến Ngày Mai Của Ngày Mai.jpg', 'Khiến Lượng Trị Liệu của người trang bị tăng 12%/15%/18%/21%/24%. Khi Phần Trăm HP của mục tiêu phe ta hiện tại lớn hơn hoặc bằng 50%, sát thương gây ra tăng 12%/14%/16%/18%/20%.'),
(15, 'Khi Đuổi Theo Gió', 3, 1058, 476, 396, '4', '\"Ha, viên này cho cô bé xui xẻo, chắc đủ để bịt miệng cha mẹ mắc nợ của cô bé, bớt đánh vài trận cũng được.\"\r\n\r\n\"Viên này cho bà lão bán kẹo dưa hấu vị chua xoay sở tạm thời, bệnh vặt gì cũng phải chữa cho xong, mình không muốn một ngày nào đó không còn kẹo ngon ăn nữa...\"\r\n\r\nCô bé khom lưng, cẩn thận đếm từng viên châu báu trong túi vải, đột nhiên hắt hơi một cái.\r\n\r\n\"Ai đang nghĩ đến mình thế nhỉ?\"\r\n\r\n\"Cifera, em ở đây à.\"\r\n\r\nSợi tơ mảnh quấn quanh sàu lừng cô, như đang do dự không biết nên bắt đầu từ đâu.\r\n\r\n\"Sau này, khi đuổi theo gió...\"\r\n\r\nSợi tơ vàng lạnh lẽo quấn lấy cổ tay cô, khiến cô không thể cử động.\r\n\r\n\"Cũng đừng quên chăm sóc bản thân mình.\"\r\n\r\nCô chạy trốn thật xa, khi quay lại mới phát hiện, góc áo choàng bị rách của mình đã được vá lại như mới.', 'uploads/lightcones/Khi Đuổi Theo Gió.jpg', 'Sau khi vào chiến đấu, Sát Thương Phá Vỡ của toàn bộ phe ta gây ra tăng 16%/18%/20%/22%/24%. Kỹ năng cùng loại sẽ không có hiệu lực trùng lặp.'),
(16, 'Hoa Sẽ Không Quên', 5, 1058, 529, 330, '4', 'Mùa xuân, họ gieo hạt.\r\n\r\nMùa hè, họ tưới tiêu cho cánh đồng.\r\n\r\nMùa thu, họ thu hoạch trái cây.\r\n\r\nMùa đông, họ ca hát trên tuyết.\r\n\r\n...\r\n\r\nMảnh đất vốn cằn cỗi giờ đã nở hoa, những cánh bướm bị tiếng cười trong trẻo như chuông bạc thu hút, đậu lên đầu ngón tay.\r\n\r\n\"Chị ơi, sau này chúng ta cũng sẽ cùng nhau biến nơi này thành biển hoa khổng lồ nhé!\"\r\n\r\n\"Nhìn xem, lời hứa của chúng ta...\"\r\n\r\nBướm bay về phía bên kia bờ, cô gái trầm lặng khẽ vuốt nhẹ cánh hoa ấm áp, như thể em gái vẫn còn ở bên cạnh.\r\n\r\n\"Chị, và những đóa hoa mà chúng ta chăm sóc... đều chưa bao giờ quên.\"', 'uploads/lightcones/Hoa Sẽ Không Quên.jpg', 'Khiến Sát Thương Bạo Kích của người trang bị tăng 24%/28%/32%/36%/40%. Sát Thương Bạo Kích do Linh Hồn Ký Ức của người trang bị gây ra tăng thêm 24%/30%/36%/42%/48%.'),
(17, 'Vì Sao Biển Cả Ca Vang', 4, 952, 635, 463, '5', 'Sóng biển dâng trào, tiếng sóng vang vọng tận chân trời.\r\n\r\nCá bơi ngược dòng giữa biển cả trống vắng, bọt sóng xưa cuộn trào, khúc nhạc ấy dường như vẫn còn vang vọng.\r\n\r\n\"Công chúa cao quý, hãy cùng chúng thần thưởng thức bữa tiệc này!\"\r\n\r\nĐể đáp lại lời kêu gọi từ biển sâu, cô nhảy múa cùng Thủy Triều Đen, cho đến khi trên sân khấu lộng lẫy chỉ còn lại một mình cô.\r\n\r\n\"Kiếm sĩ của biển cả, sao cô lại im lặng, sao cô lại ca hát?\"\r\n\r\nMỗi bước chân trên mặt đất, cô chịu đựng đau đớn, hiếm khi cất tiếng hát, chỉ có lưỡi đao nhuốm máu tấu lên khúc bi ca của sinh mệnh.\r\n\r\n\"Kiếm Kỳ Tước, hãy theo ta, cùng chinh phục biển sao!\"\r\n\r\nKhi cô tìm thấy ánh đèn rực cháy này, cuối cùng cô cũng cầm lấy cung đàn, ngỡ rằng đó là khởi đầu của bữa tiệc.\r\n\r\n...\r\n\r\n\"Helektra... Helektra... Khi khúc nhạc kết thúc, cô còn muốn bơi đi đâu?\"\r\n\r\nKhi hy vọng phù phiếm tan vỡ, cô luôn đáp lại những câu hỏi bằng một giai điệu cô độc, nhưng lại khiến tất cả người nghe say đắm mê mẩn...\r\n\r\nHoặc có lẽ... dù là ở nhân gian hay biển sâu, người thực sự có thể nghe thấy, chỉ có duy nhất người đang tấu nhạc mà thôi.', 'uploads/lightcones/Vì Sao Biển Cả Ca Vang.jpg', 'Khiến Chính Xác Hiệu Ứng của người trang bị tăng 40%/45%/50%/55%/60%, khi mục tiêu phe địch rơi vào Hiệu Ứng Xấu do người trang bị thi triển, có 80% xác suất cơ bản khiến kẻ đó rơi vào trạng thái Mê Hồn, duy trì 3 hiệp, hiệu ứng cùng loại không thể cộng dồn. Trong trạng thái Mê Hồn, cứ mỗi Hiệu Ứng Xấu do người trang bị thi triển, Sát Thương Duy Trì phải chịu tăng 5%/6.25%/7.5%/8.75%/10%, hiệu ứng này tối đa cộng dồn 6 tầng, khi bị mục tiêu phe ta tấn công, Tốc Độ của người tấn công tăng 10%/12.5%/15%/17.5%/20%, duy trì 3 hiệp. Khi người trang bị rơi vào trạng thái không thể chiến đấu, sẽ xóa tất cả \"Mê Hồn\".'),
(18, 'Thời Đại Máu Vàng Khắc Ghi', 3, 952, 635, 463, '5', '\"Lịch sử ngàn năm sau sẽ nhớ về Cerydra như thế nào?\"\r\n\r\nĐã từng có người hỏi cô như thế.\r\n\r\nVới một số người, cô là một \"Hoàng Đế\" hiển hách. Là bạo chúa lạnh lùng và tàn nhẫn, \"Người Đội Vương Miện Cháy\" đầy tham vọng, là vị nữ hoàng dùng máu lửa thiêu rụi thời đại cũ...\r\n\r\nCó kẻ phản bác, cổ chỉ là một \"quân cờ\" đáng thương. Vị quân vương bị xa lánh ấy chỉ là tù nhân của tham vọng, được định sẵn sẽ mất hết tất cả trong ván cờ với thần linh.\r\n\r\nLại có người nói, sâu thẳm trong tâm hồn còn u tối hơn cả đại dương kia - cô chỉ là một con người bình thường mang tên \"Cerydra\"...\r\n\r\nLà người biết khóc thương cho đồng đội đã ngã xuống, từng sợ hãi, do dự trước vận mệnh nặng nề, chẳng thể bước tiếp...\r\n\r\n\"Hừm, không cần lịch sử ghi nhớ về ta...\"\r\n\r\nNuốt chua xót vào tim, cô vẫn bước tiếp trên con đường Săn Đuổi Lửa trải đầy hy sinh...\r\n\r\n\"Chính dòng máu vàng rực cháy của chúng ta sẽ khắc ghi nó!\"', 'uploads/lightcones/Thời Đại Máu Vàng Khắc Ghi.jpg', 'Tăng 64%/80%/96%/112%/128% Tấn Công của người trang bị. Sau khi thi triển Tuyệt Kỹ tấn công, sẽ hồi 1 Điểm Chiến Kỹ, sau khi người trang bị thi triển Chiến Kỹ lên 1 nhân vật phe ta, sẽ khiến sát thương Chiến Kỹ mục tiêu gây ra tăng 54,0%/67,5%/81%/94,5%/108%, duy trì 3 hiệp.'),
(19, 'Món Ăn Vĩnh Hằng', 3, 952, 476, 330, '4', 'Ầm ầm...\r\n\r\nBên ngoài Khu Rừng Lạc Lối, mưa gió không dứt.\r\n\r\nỤc ục...\r\n\r\nNồi nấu thần kỳ của các yêu tinh, ngọn lửa vẫn cháy mãi không tắt.\r\n\r\n\"Rắc gia vị đặc biệt...\"\r\n\r\n\"Cho rau tươi vào!\"\r\n\r\n\"Lần này, nhất định sẽ nghiên cứu thành công!\"\r\n\r\nNồi nấu lại sôi sùng sục, các yêu tinh háo hức nhìn chằm chằm.\r\n\r\n\"Sắp xong rồi, sắp xong rồi!\"\r\n\r\nMột tia sáng mạnh phát ra từ đáy nồi...\r\n\r\nNhìn kìa, mọi người đều vây lại!\r\n\r\n...\r\n\r\nTrong bữa tiệc tràn ngập hương thơm, thực khách nâng ly chúc mừng thời gian diệu kỳ...\r\n\r\nMong cho vùng đất trong mơ này luôn ấm áp và mãi vui vẻ!', 'uploads/lightcones/Món Ăn Vĩnh Hằng.jpg', 'Tăng 16%/20%/24%/28%/32% Tấn Công của người trang bị. Sau khi người trang bị thi triển Chiến Kỹ, Tấn Công tăng 8%/10%/12%/14%/16%, hiệu ứng này tối đa cộng dồn 3 tầng.'),
(20, 'Gửi Ánh Sao Của Đêm Dài', 5, 1164, 529, 463, '5', 'Trong khe nứt của thời gian, chỉ có nhịp tim cô vang vọng.\r\n\r\nNhư bao ngày đêm trước đây... canh gác trong đêm tối, say ngủ trong đêm tôi.\r\n\r\nCô trân trọng những ký ức được gửi gắm.\r\n\r\nThiếu nữ xuất phát từ điểm khởi đầu trống rỗng, dùng màu sắc rực rỡ tô vẽ từng chặng dừng chân đã đi qua. Đoàn tàu bày biện ấm áp, những đồng đội với tính cách khác biệt, những hành tinh lấp lánh tuyệt đẹp... như một giấc mơ đẹp đến nỗi khiến người ta lưu luyến.\r\n\r\n\"Hành trình thật sự của March 7th bây giờ mới bắt đầu!\"\r\n\r\nLời tuyên bố của thiếu nữ như đang ở bên tai.\r\n\r\nCô nhìn về phía bầu trời xa xăm, một chùm sáng sắp đến gần...\r\n\r\nĐó là ánh đèn của Đội Tàu, như một ngôi sao băng.\r\n\r\n\"Chúc ngủ ngon, March... Khi cô thức dậy, hãy tiếp tục hành trình khai phá nhé ♭\"\r\n\r\nThủy triều ký ức dâng trào, cô chôn giấu ước nguyện sâu trong tim, những tiếng lòng nhỏ bé và ánh sáng ấy\r\n\r\n...Cô đều chưa từng quên.', 'uploads/lightcones/Gửi Ánh Sao Của Đêm Dài.jpg', 'Khiến Giới Hạn HP của người trang bị tăng 30%/37%/45%/52%/60%. Khi Linh Hồn Ký Ức của người trang bị thi triển kỹ năng, người trang bị sẽ nhận \"Màn Đêm\". Khi người trang bị có \"Màn Đêm\", sát thương toàn bộ Linh Hồn Ký Ức phe ta gây ra sẽ bỏ qua 20%/22%/25%/27%/30% Phòng Thủ của mục tiêu, sát thương người trang bị và Linh Hồn Ký Ức của người trang bị gây ra tăng 30%/37%/45%/52%/60%, khi Linh Hồn Ký Ức của người trang bị biến mất sẽ hồi 8/10/12/14/16 điểm Năng Lượng cho người trang bị, hiệu ứng cùng loại không thể cộng dồn.'),
(21, 'Dù Núi Sông Vạn Dặm', 1, 1058, 582, 463, '5', 'Tại vùng đất viễn cổ mênh mông vô tận, thiếu niên đơn độc bước đi trên hành trình kiếm tìm.\r\n\r\nAnh như trở về những ngày lang bạt, vượt qua sóng gió, trải qua mưa tuyết, chiến đấu với cự thú, giằng co với sóng dữ.\r\n\r\nNhưng trong khoảnh khắc thở hổn hển ấy, anh không hề cô đơn. Dù núi sông vạn dặm, anh vẫn nhìn thấy ánh sáng phía trước...\r\n\r\nĐó là niềm hy vọng của sinh linh vạn vật.\r\n\r\nMặt đất tan vỡ không thể gánh nổi sự tái sinh, chúng vây quanh anh, tìm kiếm sự an ủi và chở che.\r\n\r\nĐó là những lời nói mà đồng đội để lại trong tim anh, như hành trình bất tận, gắn kết họ bền chặt.\r\n\r\nQuá khứ làm anh phiền muộn cũng trôi theo gió, nhưng con đường đã qua, cuối cùng sẽ trở thành trợ lực cho sự lột xác mới.\r\n\r\n\"Tôi sẽ bảo vệ con đường khai phá.\" Thiếu niên tay cầm thương nhảy vọt lên, tiến về phía ánh sáng...\r\n\r\n\"Và tôi cũng sẽ bảo vệ tất cả những hy vọng sắp tan biến!\"', 'uploads/lightcones/Dù Núi Sông Vạn Dặm.jpg', 'Khiến tấn công của người trang bị tăng 64%/80%/96%/112%/128%. Khi người trang bị thi triển Tuyệt Kỹ, sẽ hồi phục HP tương đương với 10%/12%/15%/17%/20% Tấn Công của người trang bị cho toàn bộ phe ta, hồi thêm HP tương đương với 10%/12%/15%/17%/20% Tấn Công của người trang bị cho nhân vật có HP thấp nhất hiện tại, đồng thời khiến toàn bộ phe ta nhận được \"Cảnh Vệ\", \"Cảnh Vệ\" duy trì 3 hiệp. Sát thương của mục tiêu nhận được \"Cảnh Vệ\" gây ra tăng 24%/30%/36%/42%/48%, nếu mục tiêu có Vật Triệu Hồi thì sát thương gây ra tăng thêm 12%/15%/18%/21%/24%.'),
(22, 'Nguyện Yêu Như Lúc Này, Mãi Mãi', 5, 1270, 476, 463, '5', 'Bầu trời trong xanh phản chiếu một ngày mai tươi sáng.\r\n\r\nMột bước, hai bước... con đường thiếu nữ đi qua thật đài, nhưng may mắn thay những câu chuyện trân quý ấy vẫn chưa bị thời gian lãng quên.\r\n\r\nGiờ đây, dưới ngòi bút chung của mọi người, bản sử thi đã hoàn thành và chào đón hồi kết.\r\n\r\nNhững vì sao, trăm hoa, chim chóc, một thế giới mới tươi đẹp.\r\n\r\nVoan mỏng, vòng hoa, tà váy dài, ngọn gió mang đến chúc phúc.\r\n\r\nHành trình theo đuổi suốt ba mươi triệu kiếp hóa thành một khoảnh khắc hạnh phúc, đủ để vượt qua ngàn vạn lời nói vĩnh hằng.\r\n\r\n\"Hì, hứa rồi nhé...\"\r\n\r\nThiếu nữ quay lại, mỉm cười dịu dàng.\r\n\r\n\"Một người ở lại quá khứ, một người bước đến tương lai... nhưng trái tim chúng ta, chưa bao giờ rời ха\"\r\n\r\nCô nhón chân lên, bước vào quá khứ...\r\n\r\nVà ký ức, hóa thành gợn sóng của quá khứ, trôi về phía ngày mai.', 'uploads/lightcones/Nguyện Yêu Như Lúc Này, Mãi Mãi.jpg', 'Khiến Tốc Độ của người trang bị tăng 18%/21%/24%/27%/30%. Khi Linh Hồn Ký Ức của người trang bị thi triển Kỹ Năng Linh Hồn Ký Ức lên 1 đồng đội, sẽ nhận được \"Khoảng Trống\": Sát thương toàn bộ phe địch phải chịu tăng 10%/12%/14%/16%/18%. Khi Linh Hồn Ký Ức của người trang bị thi triển Kỹ Năng Linh Hồn Ký Ức lên phe địch, sẽ nhận được \"Dòng Thơ\": Sát Thương Bạo Kích của toàn bộ phe ta tăng 16%/19%/22%/25%/28%. Khi Linh Hồn Ký Ức của người trang bị đồng thời có \"Khoảng Trống\" và \"Dòng Thơ\", hiệu ứng của \"Khoảng Trống\" và \"Dòng Thơ\" tăng 60%/65%/70%/75%/80%.'),
(23, 'Bay Đến Ngày Mai Màu Hồng', 5, 847, 476, 397, '4', 'Gió nhẹ thổi tan những đám mây, để lộ ra hình dáng của vũ trụ. Ánh sáng vàng lấp lánh, sử thi sẽ được truyền tụng nơi bầu trời sao.\r\n\r\n\"Sao băng, gặp gỡ, ước hẹn!\"\r\n\r\nTiểu yêu tinh nhìn lên bầu trời, như lần đầu gặp gỡ, như một cuộc trùng phùng.\r\n\r\n\"Mem, chờ đợi. Khai phá, thế giới!\"\r\n\r\nĐôi mắt tiểu yêu tinh được ánh sao chiếu rọi... lấp lánh, như ngày mai màu hồng.', 'uploads/lightcones/Bay Đến Ngày Mai Màu Hồng.jpg', 'Khiến Sát Thương Bạo Kích của người trang bị tăng 12%/15%/18%/21%/24%. Khi Nhà Khai Phá - Ký Ức trang bị, khiến Sát Thương toàn bộ mục tiêu phe ta gây ra tăng 8%/10%/12%/14%/16%, Sát Thương Tấn Công Thường Cường Hóa \"Cùng Nhau Viết Nên Ngày Mai Nhé!\" gây ra tăng 60%/70%/80%/90%/100%.'),
(24, 'Kinh Doanh Tầm Cỡ Vũ Trụ', 7, 952, 476, 330, '4', 'Trong không gian hội trường ảo được tạo ra từ dữ liệu, các đại diện tham gia trao đổi ý kiến với nhau.\r\n\r\nHai bóng dáng di chuyển giữa các gian hàng, phân phát mọi thứ cần thiết cho các bên.\r\n\r\n\"Ủa, đây có phải là...\"\r\n\r\n\"Tôi từng thấy anh trên tivi đó, Pom!\"\r\n\r\nHọ nói về trò chơi, ẩm thực, thân hình của nhau và tất cả những thứ tốt đẹp trong vũ trụ.\r\n\r\nRõ ràng họ đều ít khi gặp mặt nhưng lại thân thiết như những người bạn cũ...\r\n\r\n\"Phải rồi, có muốn cân nhắc về đề nghị của tôi không...\"\r\n\r\n\"Đội tàu không phải hàng hóa đâu, Pom!\"', 'uploads/lightcones/Kinh Doanh Tầm Cỡ Vũ Trụ.jpg', 'Khiến tấn công của người trang bị tăng 8%/10%/12%/14%/16%. Mỗi khi mục tiêu phe địch có 1 Điểm Yếu thuộc tính khác nhau, thì sát thương người trang bị gây ra cho kẻ đó tăng 4%/5%/6%/7%/8%, tối đa tính 7 Điểm Yếu.'),
(25, 'Ký Ức Không Bao Giờ Hạ Màn', 5, 1058, 529, 396, '5', 'Dù là phồn hoa, hoang vu, ngắn ngủi, hay dài đằng đẵng... mỗi một hành tinh, mỗi một khoảnh khắc đều đang xảy ra vô số sự kiện.\r\n\r\nLịch sử ngưng kết đó vừa chạm vào liền tan chảy trong tay, hóa thành nước mắt chảy xuống từ đôi mắt của thế giới...\r\n\r\nCó người nói, đó là giọt nước mắt nặng nề đến nhường nào, thời gian ngưng tụ đủ để khiến sự vĩnh hằng phai màu. Có người nói, đó là một câu chuyện đẹp đẽ ra sao, nỗi buồn trong trẻo đến thế, niềm vui thanh thản đến thế, không hề xen lẫn một chút giả dối nào.', 'uploads/lightcones/Ký Ức Không Bao Giờ Hạ Màn.jpg', 'Khiến Tốc Độ của người trang bị tăng 6%/7,5%/9%/10,5%/12%. Sau khi người trang bị thi triển Chiến Kỹ, khiến sát thương toàn bộ phe ta gây ra tăng 8%/10%/12%/14%/16%, duy trì 3 hiệp.'),
(26, 'Vệt Máu Ngày Cũ', 2, 1058, 529, 330, '4', 'Cơn bão đang ập đến từ đằng xa, những xúc tu vươn lên từ biển âm ty như muốn đùa giỡn với sinh linh non nớt đang lênh đênh giữa sóng.\r\n\r\nTàn chi của hải thú giáng xuống, tạo nên những cột sóng cao vút, rồi vỡ nát như đá rơi vào vực sâu đại dương.\r\n\r\nHọ nói, tiếng gầm gừ của cuộc chiến kéo dài suốt mười ngày đêm, át cả tiếng sấm sét.\r\n\r\nHọ nói, dù có mười ngàn cái mạng, cũng chẳng đủ để vượt qua sự bào mòn của biển âm ty.\r\n\r\nCho đến khi lòng dũng cảm gột rửa sự non nớt, và sự lạnh lùng gột rửa sự yếu đuối...\r\n\r\nBóng hình mảnh khảnh ấy lảo đảo bước lên bờ, đúng lúc bình minh vừa ló rạng, và nước biển nhuộm màu như máu của quá khứ.', 'uploads/lightcones/Vệt Máu Ngày Cũ.jpg', 'Khiến Tỷ Lệ Bạo Kích của người trang bị tăng 12%/14%/16%/18%/20%. Sát thương Chiến Kỹ và Tuyệt Kỹ do người trang bị gây ra tăng 24%/28%/32%/36%/40%.'),
(27, 'Giấc Mơ Vấn Vương Hương Lúa', 7, 952, 529, 396, '4', 'Ngọn gió nhẹ lướt qua đầu ngón tay thiếu niên, mang theo bức thư còn chưa khô mực bay về phương ха.\r\n\r\n\"Tương lai thuộc về mình sẽ trông như thế nào...\"\r\n\r\nKhát vọng bừng lên trong tâm trí và bay về phía chân trời...\r\n\r\nLúa theo gió nhảy múa, gợn lên từng đợt sóng lăn tăn, như đang hồi đáp lại tấm lòng non nớt mà chân thành ấy.\r\n\r\n\"Dù có chuyện gì xảy ra; dù trong tay chỉ còn một thanh kiếm, mình cũng muốn bảo vệ mảnh đất này!\"\r\n\r\nThiếu niên thầm thề trong lòng...\r\n\r\nLời thề đó như dấu ẩn khắc sâu vĩnh viễn vào tim.', 'uploads/lightcones/Giấc Mơ Vấn Vương Hương Lúa.jpg', 'Khiến Tỷ Lệ Bạo Kích của người trang bị tăng 12%/14%/16%/18%/20%. Sát thương Tuyệt Kỹ và Đòn Đánh Theo Sau của người trang bị gây ra tăng 24%/28%/32%/36%/40%.'),
(28, 'Kỳ Nghỉ Phiêu Lưu Nhà Tắm Hơi', 4, 1058, 529, 330, '4', '\"Hyacine bé nhỏ, chúng ta cùng đi thử nhà tắm đặc sắc mới mở nào!\"\r\n\r\nCô bé sau khi hoàn tất việc khám chữa bệnh tại Okhema, đã tò mò bước vào bể tắm muôn màu muôn vẻ cùng bọn trẻ.\r\n\r\n\"Thử mát xa bằng nước trái cây nhé?\"\r\n\r\nNhìn đôi tay đỏ tươi đó, cô bé ôm chặt lấy Nhóc Ica đang háo hức thử nghiệm, lùi lại một bước -\r\n\r\n\"Tôi vẫn thích uống hơn...\"\r\n\r\n\"Thú Đại Địa giẫm lưng, ưu đãi có hạn!\"\r\n\r\nCon Thú Thồ Hàng hiếu kỳ cúi đầu, hơi thở thổi bay mái tóc của cô bé.\r\n\r\n\"Lần... lần sau nhất định!\"\r\n\r\n\"Bể tắm thảo dược, cường thân kiện thể, các vị?\"\r\n\r\nNgửi thấy mùi thảo dược thơm dịu, cô bé rụt rè bước vào bể nước.\r\n\r\n\"Cái này chắc là rất tốt cho sức khỏe...\"\r\n\r\n\"Doot, doot doot!\"\r\n\r\nNhóc Ica vui vẻ. bơi ngửa trong nước.\r\n\r\n\"Mấy ngày qua, nó cũng mệt lắm rồi...\"\r\n\r\nTrong kỳ nghỉ hiếm có này, cô bé uống nước ép nhỏ, trò chuyện với đứa trẻ tóc đỏ về bí quyết ngủ ngon và cách làm bánh ngọt.\r\n\r\n\"Nguy rồi, Nhóc... Nhóc Ica!\"\r\n\r\nTiếng kêu bất ngờ của đứa trẻ đã phá vỡ sự yên tĩnh của bể tắm.\r\n\r\n\"Doot ...\"\r\n\r\n\"Nhóc Ica, hãy cố lên!\"', 'uploads/lightcones/Kỳ Nghỉ Phiêu Lưu Nhà Tắm Hơi.jpg', 'Khiến Sát Thương của người trang bị tăng 16%/20%/24%/28%/32%. Sau khi người trang bị tấn công, có 100% xác suất cơ bản khiến mục tiêu bị tấn công rơi vào trạng thái Trọng Thương, sát thương phải chịu tăng 10%/11%/13%/14%/16%, duy trì 2 hiệp. Hiệu ứng cùng loại không thể cộng dồn.'),
(29, 'Gặp Lại Ở Điểm Cuối Cùng', 6, 952, 529, 396, '4', 'Giọt nước mắt không cam lòng lặng lẽ lăn dài trên má, nơi trung tâm trận pháp đổ nát chỉ còn lại bóng tối của cái chết, cùng với sự tĩnh lặng và trống rỗng vô tận.\r\n\r\nTrong cơn mê man, từ bóng tối vọng lại một tiếng thở dài, đó là sự rung động của \"linh hồn\".\r\n\r\n\"Là chị sao? Chị...\"\r\n\r\nCậu bé giơ tay lên, trong mắt thoáng lên một tia sáng. Bóng dáng thân thương ấy chỉ xuất hiện trong khoảnh khắc, nhưng như thể đã dừng lại trong tim cậu suốt cả ngàn năm.\r\n\r\n\"Em hiểu rồi... cảm ơn chị đã đến để nói lời từ biệt cuối cùng.\"\r\n\r\nCậu chậm rãi đứng dậy, ánh ban mai đầu tiên chiếu lên gương mặt kiên cường còn vương những giọt nước mắt chưa khô.\r\n\r\n\"Tìm kiếm chắc chắn là một con đường dài cô độc... Chúng ta hãy gặp lại nhau ở cuối hành trình...\"', 'uploads/lightcones/Gặp Lại Ở Điểm Cuối Cùng.jpg', 'Khiển Sát Thương Bạo Kích của người trang bị tăng 24%/28%/32%/36%/40%. Sát thương Chiến Kỹ và Đòn Đánh Theo Sau của người trang bị gây ra tăng 24%/28%/32%/36%/40%.'),
(30, 'Chiến Thắng Trong Chớp Mắt', 5, 846, 476, 396, '4', 'Tiếng gào thét của thân quyến Titan quét qua chiến trường, hai - bóng người nhanh như chớp, chém giết để mở ra một con đường máu.\r\n\r\n\"Một mình tôi cũng có thể xử lý chúng.\"\r\n\r\nMỗi lần chiến sĩ ra đòn, gai máu lan tràn xuyên qua cơ thể kẻ địch.\r\n\r\n\"Chỉ là sức mạnh thô bạo thôi mà, xem kiếm của tôi đây!\"\r\n\r\nThanh kiếm trắng bạc vung ra những đường cong đẹp mắt, quái vật bay lả tả như lá rụng.\r\n\r\n\"So tốc độ không?\"\r\n\r\nHai người cùng tấn công đối diện...\r\n\r\nTiếng gào thét truyền tới, quái vật đánh lén sau lưng lập tức mất mạng.\r\n\r\nHai giọng nói va vào nhau...\r\n\r\n\"Là tôi đã cho nó đòn cuối cùng!\"', 'uploads/lightcones/Chiến Thắng Trong Chớp Mắt.jpg', 'Tăng 12%/15%/18%/21%/24% Sát Thương Bạo Kích cho người trang bị. Khi Linh Hồn Ký Ức của người trang bị thi triển kỹ năng với mục tiêu phe ta, sẽ khiến Sát Thương toàn bộ mục tiêu phe ta gây ra tăng 8%/10%/12%/14%/16%, duy trì 3 hiệp.'),
(31, 'Lời Chào Hỏi Của Thiên Tài', 5, 952, 476, 330, '4', '\"Chúc mừng chúc mừng, thí nghiệm lại thất bại rồi đúng không?\"\r\n\r\n...\r\n\r\n\"Tôi đã nói rồi, phương pháp của cô có thiếu sót.\"\r\n\r\n...\r\n\r\n\"Nói cho cô biết, gần đây tôi có đột phá rất quan trọng~\"\r\n\r\nCô gái lạnh lùng ngẩng đầu lên từ cuốn sách...\r\n\r\n\"Xin lỗi, vừa nói gì cơ?\"\r\n\r\nCô ấy xua tay..\r\n\r\n\"Không thể nói là tuyệt vời cỡ nào, nhưng cũng có thể ảnh hưởng hơn nửa ngân hà.\"\r\n\r\nCô gái gấp sách lại.\r\n\r\n\"Cứ nói nghe thử xem.\"\r\n\r\n\"Hừm, trí tuệ thiên tài của tôi... không phải muốn nghe là nghe được đâu...\"\r\n\r\n\"Hiểu. Tôi mới làm một ít bánh ngọt, hay là vừa ăn vừa nói đi.\"', 'uploads/lightcones/Lời Chào Hỏi Của Thiên Tài.jpg', 'Khiến Tấn Công của người trang bị tăng 16%/20%/24%/28%/32%. Sau khi người trang bị thi triển Tuyệt Kỹ, khiến sát thương Tấn Công Thường mà người trang bị và Linh Hồn Ký Ức gây ra tăng 20%/25%/30%/35%/40%, duy trì 3 hiệp.'),
(32, 'Đổ Mồ Hôi Nhiều, Rơi Nước Mắt Ít', 5, 1058, 529, 198, '4', 'Tiếng va đập nặng nề vang lên khắp phòng tập, sau một cú đá như sấm sét, bao cát gần như gã làm đôi.\r\n\r\n\"Tướng quân luyện tập như vậy, không sợ chịu không nổi sao?\"\r\n\r\nBinh sĩ bên cạnh hỏi.\r\n\r\nCô ấy mỉm cười lắc lắc đầu, rồi treo một bao cát mới.\r\n\r\n\"Cái này có là gì đâu!\"\r\n\r\nGiọt mồ hôi phản chiếu ánh trăng, trôi xuống như trân châu lấp lánh, - lời nhắc nhở của tướng quân đã mất chợt hiện lên trong đầu... \"Càng đổ nhiều mồ hôi khi luyện tập thì càng ít rơi nước mắt khi chiến đấu.\".', 'uploads/lightcones/Đổ Mồ Hôi Nhiều, Rơi Nước Mắt Ít.jpg', 'Khiến Tỷ Lệ Bạo Kích của người trang bị tăng 12%/14%/16%/18%/20%. Khi Linh Hồn Ký Ức của người trang bị trong trận, Sát Thương người trang bị và Linh Hồn Ký Ức gây ra tăng 24%/27%/30%/33%/36%.'),
(33, 'Đường Dài Vẫn Có Lối Về', 4, 952, 476, 661, '5', '\"Cô ấy... là ai?\" Người thiếu nữ vuốt ve bản thân xa lạ.\r\n\r\nCô ấy nhớ lại ngọn lửa của ngày hôm ấy đã nuốt chửng cô như thế nào, chiếc quạt khảm ngọc đã hóa thành tro bụi ra sao.\r\n\r\nBản thân từng đi trên con đường dài, tìm kiếm ánh sáng ở phía trước.\r\n\r\nTiếng nói ở bên ngoài đang gọi cô, đưa cô tiến về phía trước, dẫn dắt cô băng qua màn đêm dày đặc.\r\n\r\nGiọng nói trong lòng đang nhắc nhở cô, những lời hứa chưa hoàn thành, những cuộc hẹn chưa kịp đến, những ước mơ chưa thành hiện thực, tất cả vẫn đang chờ cô...\r\n\r\nChúng chưa từng biến mất trong ngọn lửa, thay vào đó, lại càng tỏa sáng rực rỡ hơn giữa ngọn lửa.\r\n\r\n\"Đó chính là tôi...\"\r\n\r\nCô ấy đứng ở điểm cuối, không quay đầu lưu luyến nữa...\r\n\r\n\"Tôi của trước đây, cũng là tôi hoàn toàn mới.\"', 'uploads/lightcones/Đường Dài Vẫn Có Lối Về.jpg', 'Tấn Công Kích Phá của người trang bị tăng 60%/70%/80%/90%/100%. Khi điểm yếu của mục tiêu phe địch bị phá vỡ sẽ có 100% xác suất cơ bản khiến kẻ đó rơi vào trạng thái Lửa Đốt, Sát Thương Phá Vỡ phải chịu tăng 18%/21%/24%/27%/30%, duy trì 2 hiệp, hiệu ứng này có thể cộng dồn 2 tầng.'),
(34, 'Chuyến Bay Trở Về Mặt Đất', 3, 1164, 476, 529, '5', 'Ảo tưởng đã tan vỡ, anh muốn làm mặt trời của vạn vật, nhưng lại rơi từ trên trời xuống.\r\n\r\n\"Đã cảm nhận được thế nào là đau khổ chưa?\"\r\n\r\nMột giọng nói khác vang lên trong lòng anh ấy.\r\n\r\n\"Đó là những gì tôi đáng phải chịu.\"\r\n\r\nAnh ngã xuống cõi đời và tự trả lời mình bằng bước chân nặng nề.\r\n\r\nAnh bước trên mặt đất và một lần nữa chứng kiến sự đau khổ bao trùm thế gian, như suối nguồn bất tận.\r\n\r\nAnh bước qua những bụi gai chặn đường và nghe tiếng kêu gào của kẻ phàm trần, anh đã trải qua thống khổ, và vì thế, anh đã có được sự bình yên.\r\n\r\n\"Rơi xuống vốn là tên gọi khác của việc bay lượn.\"\r\n\r\nVà vào ngày thứ tám, anh tự ban cho mình \"sự khởi hành\".', 'uploads/lightcones/Chuyến Bay Trở Về Mặt Đất.jpg', 'Sau khi người trang bị thi triển Chiến Kỹ hoặc Tuyệt Kỹ lên 1 nhân vật phe ta, thì người trang bị sẽ hồi 6/6.5/7/7.5/8 điểm Năng Lượng, đồng thời khiến mục tiêu kỹ năng nhận 1 tầng Thánh Ca, duy trì 3 hiệp; tối đa cộng dồn 3 tầng, mỗi tầng Thánh Ca khiến sát thương mà người sở hữu gây ra tăng 15%/17%/19%/21%/24%. Sau mỗi 2 lần người trang bị thi triển Chiến Kỹ hoặc Tuyệt Kỹ cho 1 nhân vật phe ta, sẽ hồi 1 Điểm Chiến Kỹ.'),
(35, 'Bí Kíp Nhẫn Pháp-Dazzling Phá Ma', 7, 952, 582, 529, '5', '\"Hây a...!\"\r\n\r\nBóng dáng cô gái bước đi giữa lũ quát vật, dư ảnh của cô giống như một dải màu sắc rực rỡ trải rộng khắp mặt đất không trăng.\r\n\r\nTrong khói bụi, đám Tà Nhẫn rên rỉ đau đớn, ngạc nhiên tột cùng.\r\n\r\n\"Ngươi... là ai...\"\r\n\r\n\"Tại hạ là ninja có Nhẫn Hiệu là Rappa, nhất tâm bất loạn, phá tà hiển chính.\"\r\n\r\nCác Tà Nhẫn nhìn nhau ngơ ngác, cố gắng tháo chạy.\r\n\r\n\"Đã sẳn sàng nói lời trăn trối chưa...\"\r\n\r\nShuriken xoay tròn như ánh sáng nhảy múa trong không khí, luồng hơi thở nguy hiểm ập đến.\r\n\r\n\"Bí Thuật-Dazzling Sát Trận Hủy Diệt!\"\r\n\r\nVụ nổ phía sau cô xé toạc màn đêm, phơi bài thành phổ đổ nát. Cô hít vào một hơi thật sâu, nhảy vào trong ánh đèn neon, nơi đang huyên náo những giai điêu heavy metal.\r\n\r\n\"Đám tà ác này, thật sự rất xấu xa. Cuộc Săn Bắn-Tráng Lệ của Nhẫn Hiệp-Dazzling vẫn cần phải tiếp tục.\"', 'uploads/lightcones/Bí Kíp Nhẫn Pháp-Dazzling Phá Ma.jpg', 'Tăng 60%/70%/80%/90%/100% Tấn Công Kích Phá của người trang bị. Khi vào chiến đấu, lập tức hồi 30/32.5/35/37.5/40 điểm Năng Lượng. Và sau khi người trang bị thi triển Tuyệt Kỹ, nhận Lôi Độn. Sau khi thi triển 2 lần Tấn Công Thường, người trang bị Ưu Tiên Hành Động 50%/55%/60%/65%/70%, và xóa Lôi Độn. Sau khi người trang bị thi triển Tuyệt Kỹ sẽ làm mới Lôi Độn.'),
(36, 'Dựng Phim Trong Mơ', 8, 952, 423, 396, '4', '\"Thành phố của những giấc mơ, lồng ấp tội lỗi, hay miền đất hứa...\"\r\n\r\nNắm bắt ký ức của mọi người, anh ấy âm thầm quan sát những đoạn trích chấn động đó, phá vỡ chúng, sắp xếp và ghép chúng lại với nhau.\r\n\r\n\"Ký ức của hầu hết mọi người đều tầm thường và nhàm chán, trong cuộc đời chỉ có vài khoảnh khắc quý giá, còn lại thì không cần thiết phải tồn tại.\"\r\n\r\nNhững hình ảnh lần lượt chảy qua tay anh, dần biến thành những hình ảnh tuyệt đẹp...\r\n\r\n\"Chúng ta không mang theo ký ức, chúng ta chỉ tạo ra quá khứ.\"', 'uploads/lightcones/Dựng Phim Trong Mơ.jpg', 'Tăng 8%/9%/10%/11%/12% Tốc Độ của người trang bị, sau khi tấn công mục tiêu phe địch ở trạng thái Phá Vỡ Điểm Yếu, sẽ hồi 3/3.5/4/4.5/5 điểm Năng Lượng, hiệu ứng này mỗi hiệp tối đa kích hoạt 2 lần.'),
(37, 'Nhẫn Sự Lục-Săn Bắn Âm Luật', 2, 1058, 476, 264, '4', 'Nhẫn Pháp vũ trụ luôn thay đổi, hãy lắng nghe tiếng vọng của cuộc Săn Bắn Âm Luật...\r\n\r\nTiếng guitar vang lên, âm bass lay động trái tim và vũ điệu của âm thanh điện tử ngay lập tức làm cháy cả khán đài!\r\n\r\nMồ hôi không nói dối, nó ghi lại mọi thăng trầm trên suốt chặng đường, và biến thành những nốt nhạc ngân vang trên bàn phím.\r\n\r\nDazzling hát vang, bất kể đau thương, không hề lùi bước.\r\n\r\n\"Dazzling!\" \"Dazzling!\" \"Dazzling!\"\r\n\r\nThế giới này vốn đã phi lý và hoang dã rồi, sao không hát đi cho thỏa thích?', 'uploads/lightcones/Nhẫn Sự Lục-Săn Bắn Âm Luật.jpg', 'Tăng 12%/15%/18%/21%/24% Giới Hạn HP của người trang bị. Sau khi tổn thất hoặc hồi HP của bản thân, Sát Thương Bạo Kích tăng 18%/22.5%/27%/31.5%/36%, duy trì 2 hiệp, hiệu ứng này chỉ có thể kích hoạt 1 lần.'),
(38, 'Ta Sẽ, Tuần Tra Săn Bắn', 6, 952, 635, 463, '5', '\"Những nô lệ vừa đáng thương lại vừa nực cười...\"\r\n\r\nQuân đội dân Trù Phú hùng dũng áp sát, Độc Sói như thủy triều tràn về phía quân Thanh Khâu, mang theo ý đồ muốn đánh thức nỗi sợ hãi chôn sâu trong lòng các binh sĩ, biến thành đủ loại ảo giác đáng sợ, khiến họ buông vũ khí, do dự không tiến lên.\r\n\r\nNhưng trận tuyến tựa như tường đồng vách sắt, không bị dao động bởi lời khinh thường của người Boris. Giữa những người lính, một bóng người từ từ bước ra..\r\n\r\nCô ấy chậm rãi di chuyển cơ thể, những lá cờ sau lưng cô xào xạc dưới sát khí vô hình.\r\n\r\n\"Tướng quân, tướng quân xuất hiện rồi...\"\r\n\r\n\"Tướng Quân Feixiao...\r\n\r\n\"Tướng Quân Thiên Kích!\"\r\n\r\nTiếng reo hò khẽ vang lên, rồi hòa vào nhau biến thành tiếng rền vang chấn động bầu trời.\r\n\r\n\"Luôn luôn bay cao, mãi mãi chiến thắng!\"\r\n\r\nCô ấy đứng đó, một mình, nhưng lại giống như là cả một đội quân.', 'uploads/lightcones/Ta Sẽ, Tuần Tra Săn Bắn.jpg', 'Tăng 15%/17,5%/20%/22,5%/25,0% Tỷ Lệ Bạo Kích của người trang bị. Khi người trang bị thi triển Đòn Đánh Theo Sau sẽ nhận 1 tầng Lưu Quang, tối đa cộng dồn 2 tầng. Mỗi tầng Lưu Quang khiến sát thương Tuyệt Kỹ mà người trang bị gây ra bỏ qua 27%/30%/33%/36%/39% phòng thủ của mục tiêu. Khi hiệp của người trang bị kết thúc sẽ xóa bỏ 1 tầng Lưu Quang.'),
(39, 'Mùi Hương Vẹn Nguyên', 8, 1058, 529, 529, '5', '\"Thứ nhất, chỉnh đốn thói quen làm việc.\"\r\n\r\n\"Thứ hai, chấp hành nghiêm ngặt quy định thưởng phạt.\"\r\n\r\n\"Đương nhiên, điều quan trọng nhất vẫn là nhanh chóng điều tra rõ những mối quan hệ lợi ích đan xen đó...\"\r\n\r\nVốn đang thưởng thức cảnh sắc mặt hồ, suy nghĩ của cô lại vô tình trôi về phía công việc, áp lực và phiền muộn cứ thế thi nhau kéo đến.\r\n\r\nCô vội vàng đốt cho mình một nén hương an thần mang theo bên mình.\r\n\r\n\"Thế giới thật đẹp đẽ, nhưng tôi lại quá nóng nảy... Ngày nghỉ, không nên nghĩ đến những chuyện phiền phức đó nữa...\"', 'uploads/lightcones/Mùi Hương Vẹn Nguyên.jpg', 'Khiến Tấn Công Kích Phá của người trang bị tăng 60%/70%/80%/90%/100%. Sau khi người trang bị thi triển Tuyệt Kỹ tấn công kẻ địch, sẽ khiến kẻ đó rơi vào trạng thái Vong Ưu, duy trì 2 hiệp. Sát thương phải chịu của kẻ địch trong trạng thái Vong Ưu tăng 10%/12%/14%/16%/18%, nếu Tấn Công Kích Phá hiện tại của người trang bị lớn hơn hoặc bằng 150%, hiệu ứng tăng sát thương phải chịu sẽ tăng thêm 8%/10%/12%/14%/16%.'),
(40, 'Bóng Tối Đồng Hành', 6, 846, 476, 396, '4', '\"Dấu chân.\"\r\n\r\n\"Vết xước.\"\r\n\r\n\"Mùi.\"\r\n\r\nSau khi xử lý ổn thỏa, anh ấy nhặt chiếc lông quạ lên, nhẹ nhàng nhảy lên mái nhà.\r\n\r\nGiữa thành phố rộng lớn, chiếc bóng đen kịt lướt qua một góc không ai để ý.\r\n\r\n\"Tôi sẽ hòa vào bóng tối và canh chừng mọi người.\"', 'uploads/lightcones/Bóng Tối Đồng Hành.jpg', 'Tăng 28%/35%/42%/49%/56% Tấn Công Kích Phá của người trang bị. Khi vào chiến đấu hoặc sau khi gây Sát Thương Phá Vỡ, Tốc Độ tăng 8%/9%/10%/11%/12%, duy trì 2 hiệp, hiệu ứng này mỗi hiệp chỉ có thể kích hoạt 1 lần.'),
(41, 'Điệu Múa Dưới Hoàng Hôn', 2, 1058, 582, 463, '5', 'Trong ánh hoàng hôn rực rỡ, một bóng người nhanh nhẹn chạy trên nóc nhà.\r\n\r\n\"Lili, Guoguo, xem chúng ta ai đến đích trước nhé!\"\r\n\r\nNhảy lên mái hiên, né tránh lính gác, nương người theo gió, cô gái nhanh nhẹn như con thú nhỏ, đuổi theo vệt nắng cuối ngày.\r\n\r\nGió đêm mang theo chút hơi nóng lướt qua gương mặt cô gái, ánh tà dương rực rỡ phủ lên chú mèo một tầng sắc màu lung linh.\r\n\r\n\"Các bạn thắng rồi!\"\r\n\r\nCô lau mồ hôi trên trán, ôm bé mèo vào lòng. Những vì sao mọc lên trên nền trời, nụ cười thuần khiết của cô nở rộ xinh đẹp, tựa như tầng tầng lớp lớp những áng mây ráng chiều trôi lửng lơ trên đường chân trời.', 'uploads/lightcones/Điệu Múa Dưới Hoàng Hôn.jpg', 'Tăng mạnh xác suất người trang bị chịu tấn công, Sát Thương Bạo Kích tăng 36%/42%/48%/54%/60%. Sau khi người trang bị thi triển Tuyệt Kỹ, sẽ nhận được 1 tầng Hỏa Vũ, duy trì 2 hiệp, tối đa cộng dồn 2 tầng. Mỗi tầng Hỏa Vũ khiến sát thương do Đòn Đánh Theo Sau của người trang bị gây ra tăng 36%/42%/48%/54%/60%.');
INSERT INTO `lightcones` (`id`, `name`, `path_id`, `hp`, `atk`, `def`, `rarity`, `description`, `image`, `path_effect`) VALUES
(42, 'Vô Số Mùa Xuân Ấy', 4, 952, 582, 529, '5', 'Sau những cơn mưa phùn bất chợt, đợt thủy triều mùa xuân đầu tiên bắt đầu dâng trào, cỏ cây xanh mơn mởn đua nhau đâm chồi nảy lộc.\r\n\r\n\"Đây đã là mùa xuân thứ mấy rồi?\"\r\n\r\nAnh nhớ đến những chiến sĩ trẻ tuổi lần đầu tiên xông pha chiến trường. Năm này qua tháng nọ, những con người khác nhau, nhưng đều tràn đầy sức sống và hy vọng như nhau.\r\n\r\nNhững cành liễu héo úa chờ đợi người về, nhưng cuối cùng, thứ mà họ mang về mảnh đất quê hương... lại thường là băng sương không bao giờ tan chảy.\r\n\r\nTiếng chim ríu rít, anh nhắm mắt lại, để mặc ánh mặt trời xuyên qua kẽ lá chiếu lên gương mặt.\r\n\r\n\"Vậy nên, người ở lại vẫn luôn là kẻ cô đơn nhất.\"', 'uploads/lightcones/Vô Số Mùa Xuân Ấy.jpg', 'Khiến Chính Xác Hiệu Ứng của người trang bị tăng 60%/70%/80%/90%/100%, sau khi người trang bị thi triển Tấn Công Thường, Chiến Kỹ, Tuyệt Kỹ tấn công kẻ địch, sẽ có 60% xác suất cơ bản khiến kẻ đó rơi vào trạng thái Tháo Giáp. Trong trạng thái Tháo Giáp, sát thương kẻ địch phải chịu tăng 10%/12%/14%/16%/18%, duy trì 2 hiệp. Nếu mục tiêu ở trạng thái Sát Thương Duy Trì do người trang bị thi triển, thì có 60% xác suất cơ bản khiến trạng thái Tháo Giáp mà người trang bị thi triển tăng cấp thành trạng thái Đường Cùng, khiến sát thương kẻ địch phải chịu tăng thêm 14%/16%/18%/20%/22%, duy trì 2 hiệp, trong thời gian này người trang bị không thể thi triển Tháo Giáp cho mục tiêu này.'),
(43, 'Tuổi Hoa Chờ Nở Rộ', 3, 952, 423, 396, '4', 'Trong ánh nắng ban mai dịu dàng lan tỏa, giữa khu rừng hoa tĩnh lặng, một bóng dáng chuyển động mượt mà bất chợt làm những chú chim giật mình vỗ cánh bay lên.\r\n\r\nMiệng niệm thầm kiếm chiêu, thân thể múa nương theo thế kiếm, chiêu thức vốn vụng về ngày càng trở nên thành thạo. Nơi kiếm khí chạm tới, những cánh hoa rơi như mưa, xoay tròn hỗn loạn rồi rơi xuống. Bất tri bất giác, làn sương mát lành đã thấm ướt mái tóc cô.\r\n\r\n\"Kiếm pháp tuyệt vời!\"\r\nCô kinh ngạc, thuận thế xoay thanh kiếm trong tay...\r\nSau lưng, thiếu nữ/thiếu niên tóc xám buông máy ảnh trong tay xuống.\r\n\"Ký ức đẹp sẽ không tan biến theo làn gió.\"', 'uploads/lightcones/Tuổi Hoa Chờ Nở Rộ.jpg', 'Khiến Tấn Công của người trang bị tăng 16%/20%/24%/28%/32%. Khi vào Chiến Đấu, nếu có từ 2 nhân vật phe ta trở lên có cùng vận mệnh bất kỳ, sẽ khiến Sát Thương Bạo Kích của những nhân vật này tăng 16%/20%/24%/28%/32%. Kỹ năng cùng loại không thể cộng dồn hiệu quả.'),
(44, 'Ngoại Suy Vô Tận', 7, 1058, 529, 396, '5', 'Vĩnh viễn lơ lửng trong hư không,\r\nDữ liệu lấp lánh tràn vào tâm trí Ngài như dòng nước lũ.\r\nBiến mọi thứ trong quá khứ và tương lai thành ký hiệu,\r\nNgài đã suy luận thời điểm kết thúc ngay thời khắc bắt đầu.\r\nTri thức, đáp án, chân tướng...\r\nÁnh sáng rực rỡ trỗi dậy từ màn sương mù thông tin,\r\nVà rồi mọi chuyện đều rõ ràng với Ngài.', 'uploads/lightcones/Ngoại Suy Vô Tận.jpg', 'Khiến Tấn Công của người trang bị tăng 8%/9%/10%/11%/12%. Sau khi thi triển tấn công, mỗi khi đánh trúng 1 kẻ địch sẽ tăng thêm 4%/5%/6%/7%/8% Tấn Công, hiệu ứng này tối đa cộng dồn 5 lần, duy trì cho đến sau lần tấn công tiếp theo, nếu đánh trúng 3 kẻ địch trở lên, sẽ tăng 8%/10%/12%/14%/16% Tốc Độ của bản thân, duy trì 1 hiệp.'),
(45, 'Sau Sự Im Lặng Hài Hòa', 7, 847, 476, 396, '4', 'Mộng đẹp vĩnh hằng hằng mong ước đã tan biến trong chớp mắt. Khi rơi xuống mặt đất, anh không hề bi thương, chỉ muốn nhắm mắt lại.\r\n\r\nHành trình cũ đã đứt đoạn, tương lai vẫn chìm trong sương mù.\r\n\"Sau khi tỉnh dậy, thế giới vẫn đang trải qua vòng lặp đau đớn, không thứ gì có thể thoát được, giống như mọi khi...\"\r\nAnh gánh quá khứ trên vai, quay lưng lại với quê hương, tiếp tục kéo lê bước chân hướng tới thiên đường không tồn tại đó.\r\n\r\nVậy tại sao con người lại ngủ say? Tại sao con người phải thức dậy?\r\n...Hết hành tinh này đến hành tinh khác lướt qua ngoài cửa sổ, câu trả lời này giờ đây cần được trao cho ý nghĩa mới.\r\n\"Có lẽ... Có lẽ ta vẫn có thể...\"', 'uploads/lightcones/Sau Sự Im Lặng Hài Hòa.jpg', 'Tăng 28%/35%/42%/49%/56% Tấn Công Kích Phá của người trang bị. Sau khi người trang bị thi triển Tuyệt Kỹ, Tốc Độ tăng 8%/10%/12%/14%/16, duy trì 2 hiệp.'),
(46, 'Hy Vọng Là Vô Giá', 7, 952, 582, 529, '5', '\"Thưa quý cô Bonajade, nếu không có cô liên tục gửi vật tư và khoản quyên góp đến, e rằng cô nhi viện đã...\"\r\nCô nhẹ nhàng xoa đầu bọn trẻ, cắt ngang lời viện trưởng.\r\n\"Chỉ cần có tôi ở đây, tương lai không cần phải lo lắng... Còn những lời này, không cần phải nói cho lũ trẻ nghe, nó chỉ khiến bọn trẻ lo lắng thêm.\"\r\n\r\nBọn trẻ ngẩng đầu ríu rít kể với cô về những điều thú vị, những lo lắng và cả những mơ ước gần đây của chúng.\r\n\"Quý cô Bonajade, khi nào thì chị sẽ đến nữa ạ?\"\r\n\"Nhớ đừng làm việc quá sức nhé, phải đi ngủ sớm nhé...\"\r\n\"Quý cô Bonajade, khi lớn lên em nhất định sẽ trở thành người giống như chị!\"\r\nCô nhìn vào đôi mắt trong veo của bọn trẻ, chợt sững sờ một lúc.\r\n\"Người giống như tôi...\"\r\nĐứa trẻ đưa cho cô quả táo, \"Vâng! Là người sẽ mang lại ánh sáng và hy vọng cho người khác!\"\r\n\r\nNghèo đói, lỗi lầm, buồn phiền, đau khổ... Cô đi lại giữa các thiên hà, thu tiền thế chấp từ người khác và đền đáp lại bằng giá trị tương đương.\r\nSự sống tồn tại vì tham vọng, bôn ba vì tham vọng, cũng chết đi vì tham vọng... Đây là quy luật không thể chống lại, cũng là quy luật tất yếu của cuộc sống.\r\nMột nhà từ thiện nói một đằng làm một nẻo, một thương nhân độc ác thế chấp cả linh hồn... Cô được người đời gán cho nhiều thân phận, và cũng chỉ có chính bản thân cô mới hiểu được giá trị đạo đức đằng sau những hành động này.', 'uploads/lightcones/Hy Vọng Là Vô Giá.jpg', 'Khiến Tỷ Lệ Bạo Kích của người trang bị tăng 16%/19%/22%/25%/28%. Khi Sát Thương Bạo Kích của người trang bị trong trận lớn hơn 120%, mỗi 20% vượt quá thì Sát Thương Đòn Đánh Theo Sau gây ra tăng 12%/14%/16%/18%/20%, hiệu ứng này có thể cộng dồn 4 tầng. Khi bắt đầu chiến đấu hoặc sau khi người trang bị dùng Tấn Công Thường sẽ khiến Tuyệt Kỹ hoặc sát thương Đòn Đánh Theo Sau gây ra bỏ qua 20%/24%/28%/32%/36% Phòng Thủ của mục tiêu, duy trì 2 hiệp.'),
(47, 'Giấc Mộng Sẽ Đi Về Đâu', 2, 1164, 476, 529, '5', 'Vùng ven tinh hệ Asdana. Biển ký ức cuộn trào những con sóng khổng lồ, cô giống như một đốm lửa, có thể bị gió bão dập tắt bất cứ lúc nào.\r\n\"Một người không thể nằm mơ muốn bước vào Cõi Mộng Liên Kết Trực Giác, cái giá phải trả thậm chí là cái chết.\"\r\nGiọng nói lo lắng của đồng đội vẫn vang vọng bên tai cô. Cô hít một hơi thật sâu, bước vào Vùng Hồi Ức.\r\nKhi thiết bị chuyên dụng chìm sâu vào Vùng Hồi Ức, từng đốm sáng nhỏ lập lòe tỏa ra nơi khe sâu rồi bị bóng tối vô tận nuốt chửng trong chốc lát.\r\n\r\n\"Vì sao phải lao đầu vào cái chết?\"\r\nTrong biển ký ức ngày càng sâu thẳm, những ánh sáng yếu ớt phát ra từ sinh vật nơi Vùng Ký Ức cứ như ánh mắt ở cõi xa xăm, nhìn chằm chằm vào cô một cách lạnh lùng.\r\n\r\nGiấc mơ đối với cô vẫn là một chuyện quá xa vời. Cô đờ đẫn nhìn vào bóng tối vô biên, dưới áp lực nặng nề của Chất Ký Ức, cơ thể và linh hồn đã đạt đến giới hạn của sự sụp đổ. Ngay cả khi cảm giác được ý thức của mình đang trở nên xa dần, trong đầu vẫn chỉ có những ký ức còn sót lại đó lặp đi lặp lại, hết lần này đến lần khác...\r\nĐàn côn trùng bao trùm cả bầu trời phá vỡ tiền tuyến, cô ấy bay thẳng vào đàn côn trùng trong sự bao bọc của lửa, tro của đàn côn trùng rơi xuống như tuyết vỡ, bên dưới chỉ còn lại những thi thể cháy đen của các kỵ sĩ.\r\nCô ấy còn chưa kịp tự tay dâng tặng bó hoa tưởng niệm cho từng Thiết Kỵ đã hi sinh thân mình. Sinh mệnh của họ nở rộ trong khoảnh khắc rồi lập tức tắt lịm, giống như một chuỗi mã gen chỉ vừa có con số chứ chưa kịp', 'uploads/lightcones/Giấc Mộng Sẽ Đi Về Đâu.jpg', 'Giúp Tấn Công Kích Phá của người mang tăng 60%/70%/80%/90%/100%. Khi người mang gây Sát Thương Phá Vỡ cho mục tiêu địch, sẽ khiến kẻ địch rơi vào trạng thái Thảm Bại, duy trì 2 hiệp. Ở trạng thái Thảm Bại, Sát Thương Phá mục tiêu phải chịu do người mang gây ra tăng 24%/28%/32%/36%/40%, Tốc Độ giảm 20%.'),
(48, 'Vũ Điệu Vô Biên', 4, 952, 476, 330, '4', 'Bước nhảy nhẹ nhàng, ánh sáng và bóng tối lặng lẽ đan xen. Cô nắm lấy tay đối phương, mỉm cười nhìn đôi mắt lạnh nhạt đó.\r\n\"Cảnh binh xinh đẹp... Chào mừng đến với vũ đài của \"Ký Ức\". Ở khoảng cách này... Cô lại càng thêm quyến rũ.\"\r\n\r\nXung quanh dần tối đen, một chùm sáng từ mái vòm tràn xuống, đuổi theo bước nhảy không ngừng thay đổi của hai người.\r\n\"Có điều, có một số người vốn dĩ nên nhảy cùng chúng ta, nhưng họ mãi không đến...\"\r\n\r\nVào khoảnh khắc xoay tròn, cô tiến lại gần gương mặt không chút biểu cảm đó, nắm chặt bàn tay lạnh lẽo, khẽ hỏi...\r\n\"Có thể nói cho tôi biết, cô đã làm gì không?\"\r\n\r\nTheo bước nhảy ngày càng dồn dập, những mảnh vụn quá khứ tràn vào đầu cô từ ngón tay.\r\nTrong quá khứ ngày một xa vời, cô nghe thấy tiếng mưa, nghe thấy tiếng thở than trước khi sự sống kết thúc, sau đó cô nhìn thấy vũ công trước mặt đang cầm chiếc ô giấy đỏ như máu trong cơn mưa mịt mờ, và trong tầm mắt của cô...\r\n\r\nVô số thi thể của người chết đuối đang trôi nổi giữa dòng nước lũ.\r\n\r\nCô hoảng hốt muốn rút tay ra, và ngã về phía sau.\r\nXung quanh lại sáng lên, tiếng huyên náo của buổi tiệc lại xuất hiện, đối phương nhẹ nhàng đỡ lưng cô, dường như hoàn toàn không biết vừa xảy ra chuyện gì.\r\n\"Người Lưu Giữ Ký Ức, điệu nhảy rất đẹp, muốn nhảy lại lần nữa không?\"', 'uploads/lightcones/Vũ Điệu Vô Biên.jpg', 'Tăng 8%/10%/12%/14%/16% tỷ lệ Bạo Kích của người trang bị. Sát Thương Bạo Kích mà người trang bị gây cho kẻ địch ở trạng thái Giảm Phòng Thủ hoặc Giảm Tốc tăng 24%/30%/36%/42%/48%.'),
(49, 'Vì Hành Trình Ngày Mai', 3, 952, 476, 330, '4', 'Kim la bàn không ngừng xoay tròn, cuối cùng chỉ về thời khắc trong mơ.\r\nNgoài cửa sổ, tinh hệ Asdana tràn ngập Chất Ký Ức tỏa ra ánh sáng mờ ảo, nhà tù vùng ven nằm ở đó vẫn đang chìm trong chiến tranh.\r\n\r\n\"Mọi người nói xem, thế giới trong mơ sẽ như thế nào? Tôi nhất định phải vẽ một tấm bản đồ hoàn chỉnh cẩn thận!\"\r\nCô gái cất dụng cụ đo vẽ bản đồ lại, ngắm nhìn thế giới mới trong tầm tay đầy khao khát.\r\n\"Nơi đó rất hỗn loạn, hai người sau khi đến đừng chạy lung tung. Nếu còn bị người khác bắt cóc như lần trước, tôi...\"\r\nNgười đàn ông ăn mặc như cao bồi lau chùi vũ khí xong, cầm một ly đồ uống đi về phía sofa, lắc đầu bất lực.\r\n\"Vùng Đất Trong Mơ, có vô số khả năng, chúng ta nhất định sẽ làm nên nghiệp lớn... Tàu trưởng, nếu chúng tôi yêu thích nơi này và không quay lại, tàu trưởng sẽ nhớ chúng tôi chứ?\"\r\nKhách Vô Danh ăn mặc như quý tộc nháy mắt với Pom-Pom, mỉm cười nói.\r\n\"Không được tùy tiện đùa cợt tàu trưởng! Tàu trưởng có phải muốn nói câu này không?\"Cô gái nở nụ cười xảo quyệt.\r\n\"Mọi người! Thật đáng ghét!\"\r\nBong bóng trôi nổi trong đồ uống, toa tàu tràn ngập tiếng cười vui vẻ.\r\n...\r\n\r\nBóng lưng trẻ tuổi của Khách Vô Danh dần bị Chất Ký Ức nuốt chửng.', 'uploads/lightcones/Vì Hành Trình Ngày Mai.jpg', 'Tăng 16%/20%/24%/28%/32% Tấn Công cho người trang bị. Sau khi người trang bị thi triển Tuyệt Kỹ, sát thương gây ra tăng 18%/21%/24%/27%/30%, duy trì 1 hiệp.'),
(50, 'Hướng Đến Sinh Mệnh Thứ Hai', 6, 1058, 582, 463, '5', 'Hắn như rơi xuống biển sâu, mọi thứ rời xa hắn, chỉ còn lại suy nghĩ trống rỗng đang nhảy nhót và giãy giụa.\r\n\r\nSợ hãi, bất an, cô độc, tăm tối, phẫn nộ... Những cảm xúc đó không tan biến theo cơ thể, mà ngược lại còn được lưu giữ trong cơ thể máy móc bằng một hình thức khác, và chúng càng lúc càng nặng nề hơn.\r\n\r\nHắn nghe thấy tiếng gọi của người đã khuất, ngửi thấy mùi cháy khét, tiếng động cơ ong ong quanh tai, dòng máu màu xanh chảy vào trái tim đói khát của hắn...Một số ký ức tàn nhẫn lại hiện lên, thù hận thấu xương hóa thành ánh sáng mờ ảo trong bóng tối, hắn đi theo hướng ánh sáng, dùng hết sức lực để nổi lên khỏi mặt nước.\r\n\r\nHắn mở mắt, một chùm tia lửa lóe qua, hắn nghe thấy lời chúc mừng của bác sĩ...\r\n\"Chào mừng đến với thế giới này một lần nữa.\"\r\nHắn nắm chặt hai tay, bây giờ chúng làm bằng sắt, lạnh lẽo...\r\nHắn sẽ không sống vì bản thân nữa.', 'uploads/lightcones/Hướng Đến Sinh Mệnh Thứ Hai.jpg', 'Tăng 60%/70%/80%/90%/100% Tấn Công Kích Phá của người trang bị, Sát Thương Phá Vỡ gây ra sẽ bỏ qua 20%/23%/26%/29%/32% Phòng Thủ của mục tiêu. Trong chiến đấu, khi Tấn Công Kích Phá của người trang bị lớn hơn hoặc bằng 150%, Tốc Độ tăng 12%/14%/16%/18%/20%.'),
(51, 'Sắc Đêm Rực Sáng', 3, 952, 635, 463, '5', 'Năm tháng trôi qua trong gương, cô gái non nớt đã trở thành ca sĩ nổi tiếng.\r\n\r\nCô nhìn mình trong gương, thời gian đã thay đổi tất cả, chỉ có đôi mắt màu xanh lục vẫn tĩnh lặng như mặt hồ, dường như đang nhắc nhở về niềm tin không bao giờ thay đổi.\r\n\r\n\"Hôm nay trông có vẻ rất hào hứng nhỉ.\"\r\n\r\n\"Đúng vậy... Dù sao cũng có \"buổi biểu diễn\" rất quan trọng.\"\r\n\r\n\"Ừm, buổi diễn tập cho Đại Nhạc Hội Hòa Hợp sắp bắt đầu rồi, mời cô đến phòng chuẩn bị.\"\r\n\r\n...\r\n\r\nCô bước ra khỏi phòng trang điểm, và dần dần rời xa nhà hát rực rỡ ánh đèn phía sau.\r\n\r\n\"Sân khấu của tôi không ở nơi này.\"\r\n\r\nCô nhắm mắt lại, mặc cho màn đêm nuốt chửng cái bóng của mình.\r\n\r\n\"Từ trước đến giờ, tiếng hát của tôi chỉ dành cho...\"\r\n\r\n\"Dành cho những người đã mất đi hy vọng.\"', 'uploads/lightcones/Sắc Đêm Rực Sáng.jpg', 'Mỗi khi nhân vật phe ta tấn công, sẽ khiến người trang bị nhận 1 tầng Ca Tụng, mỗi tầng Ca Tụng sẽ tăng 3%/3,5%/4%/4,5%/5% hiệu suất hồi Năng Lượng của người trang bị, tối đa cộng dồn 5 tầng. Khi người trang bị thi triển Tuyệt Kỹ, sẽ xóa bỏ Ca Tụng và nhận Độc Tấu Tự Do, Độc Tấu Tự Do khiến Tấn Công của người trang bị tăng 48%/60%/72%/84%/96%, khiến sát thương của toàn phe ta gây ra tăng 24%/28%/32%/36%/40%, duy trì 1 hiệp.'),
(52, 'Buổi Hòa Nhạc Hai Người', 1, 953, 463, 370, '4', '\"Anh à, đây là...\"\r\n\r\n\"Đã lâu rồi em không được hát một cách vui vẻ nữa nhỉ... Anh đã chuẩn bị sân khấu cho em rồi, chỉ là... có chút sơ sài.\"\r\n\r\n\"Nhưng giáo viên cảm thấy em không phù hợp...\"\r\n\r\n\"Không, anh thấy rất hay! Sau này em nhất định sẽ thực hiện được ước mơ của mình, đứng hát trên sân khấu lớn hơn nữa...\"\r\n\r\n...\r\n\r\nRất nhiều năm sau đó, cô đứng trên sân khấu tráng lệ, nhìn những người hâm mộ hò reo bên dưới, nhưng lại ngẩn người trong giây lát...\r\n\r\nTrên con đường theo đuổi giấc mơ dài đằng đẵng, cô luôn nhớ tới ánh mắt tin tưởng đó và buổi \"hòa nhạc\" đầu tiên của mình.\r\n\r\nKhông có ánh đèn rực rỡ, không có trang phục hoa lệ, chỉ có một sân khấu của một đứa trẻ, dựng lên để cổ vũ một đứa trẻ khác theo đuổi ước mơ.\r\n\r\n\"Bây giờ, giấc mơ của chúng ta đều đã thành hiện thực. Nhưng... đó vẫn còn là giấc mơ của \"chúng ta\" sao...\"\r\n\r\nCô ấy nắm chặt micro. Trước khi bài hát tiếp theo bắt đầu, cô thậm chí còn không kịp cảm thấy đau buồn.', 'uploads/lightcones/Buổi Hòa Nhạc Hai Người.jpg', 'Tăng 16%/20%/24%/28%/32% Phòng Thủ của người trang bị. Mỗi khi trên trận có 1 nhân vật sở hữu Khiên, sát thương do người trang bị gây ra tăng 4%/5%/6%/7%/8%.'),
(53, 'Bước Theo Bến Bờ Thời Gian', 4, 1058, 635, 396, '5', 'Lưu chuyển trong ranh giới hẹp giữa \"tồn tại\" và \"hư vô\", những đợt thủy triều lạnh như băng vĩnh viễn vỗ vào hai bên bờ, đưa tất cả trở về điểm kết thúc.\r\n\r\nTại nơi này, kẻ đuối nước kêu cứu, than thở bằng khúc bi ca, tận hưởng vui vẻ, ngọt ngào, chịu đựng đau đớn... tất cả những thứ đó đều được phản ánh trong làn nước... Cô ấy không thể trải qua những chuyện này, nhưng có thể cảm nhận được...\r\n\r\nTrước khi đến được điểm cuối, vẫn còn rất nhiều con đường phải đi, rất nhiều giọng nói phải lắng nghe, rất nhiều chuyện cần phải thay đổi.\r\n\r\nCô ấy đưa tay ra, cứu người đuối nước khỏi sự mê hoặc của hư vô, ảo ảnh lần lượt vỡ vụn sau lưng cô.\r\n\r\nCô ấy tiếp tục tiến lên trong thế giới vô tận này, vì một điểm tận cùng không tồn tại.\r\n\r\n...Và ở đây, mưa không bao giờ ngừng rơi.', 'uploads/lightcones/Bước Theo Bến Bờ Thời Gian.jpg', 'Tăng 36%/42%/48%/54%/60% Sát Thương Bạo Kích của người trang bị. Khi người trang bị đánh trúng kẻ địch, sẽ khiến kẻ địch rơi vào trạng thái Bóng Hư Ảo, duy trì 1 hiệp. Mỗi lần người trang bị tấn công, đối với mỗi mục tiêu chỉ được kích hoạt 1 lần. Sát Thương mà người trang bị gây ra cho mục tiêu đang ở trạng thái Bóng Hư Ảo tăng 24%/28%/32%/36%/40%, sát thương do Tuyệt Kỹ gây ra tăng thêm 24%/28%/32%/36%/40%.'),
(54, 'Vận Mệnh Chưa Từng Công Bằng', 1, 1058, 423, 661, '5', 'Mở lá bài cuối cùng, buồn bã, phẫn nộ, bất lực, điềm tĩnh, trăm nghìn cảm xúc hiện lên trên gương mặt đối thủ.\r\n\r\n\"Đến cũng đã đến rồi, sao không làm cho nó kích thích hơn?\"\r\n\r\nAnh ta đặt toàn bộ xu đặt cược, những người tự cho mình là kẻ xui xẻo lẩm bẩm nguyền rủa rồi rút lui, âm thầm chờ đợi sự thất bại của anh ấy.\r\n\r\nThắng thua, danh vọng, may mắn... anh ta không quan tâm đến những điều đó, anh ta chỉ say mê những khoảnh khắc khiến cuộc sống rung động...\r\n\r\nGiữa vực sâu và thiên đường, chỉ cách nhau một sự lựa chọn.\r\n\r\nNém xu đặt cược, khẽ thở dài, sự hài lòng hư ảo này sẽ biến mất trong chớp mắt sau khi mọi thứ lắng xuống.\r\n\r\n\"Được ăn cả, ngã về không, nhưng tôi không có quyền được lựa chọn...\"', 'uploads/lightcones/Vận Mệnh Chưa Từng Công Bằng.jpg', 'Tăng 40%/46%/52%/58%/64% Phòng Thủ của người trang bị, khi người trang bị cung cấp khiên cho phe ta, Sát Thương Bạo Kích của người trang bị sẽ tăng 40%/46%/52%/58%/64%, duy trì 2 hiệp. Khi người trang bị phát động Đòn Đánh Theo Sau tấn công kẻ địch, có 100%/115%/130%/145%/160% xác suất cơ bản khiến sát thương phải chịu của kẻ địch bị tấn công tăng 10%/11,5%/13%/14,5%/16%, duy trì 2 hiệp.'),
(55, 'Hun Đúc Ký Ức Thời Gian', 4, 1058, 582, 463, '5', 'Thắp sáng ngọn nến, lật ngửa lá bài, hít vào hương thơm...\r\nNhững ký ức vụn vỡ hóa hình hài trong ánh sáng dao động.\r\nBóng ma bò ra từ ngọn lửa, kể với cô về quá khứ khó quên.\r\nChia cắt thời gian, ngưng tụ khoảnh khắc, cô biến những thời khắc quý giá nhất thành những viên pha lê vĩnh cửu.\r\n\r\n\"Mất mát quá nhiều, lưu giữ quá ít... Chúng ta nuôi dưỡng ký ức để đối phó với thời gian tàn khốc.\"', 'uploads/lightcones/Hun Đúc Ký Ức Thời Gian.jpg', 'Tăng 40%/45%/50%/55%/60% Chính Xác Hiệu Ứng của người trang bị. Khi người trang bị gây sát thương cho kẻ địch đang trong trạng thái Bào Mòn, Thiêu Đốt, Sốc Điện, Chảy Máu, sẽ lần lượt nhận 1 tầng Tiên Tri, tối đa cộng dồn 4 tầng. Trong 1 trận đấu, mỗi loại trạng thái Sát Thương Duy Trì chỉ có thể cộng dồn hiệu ứng Tiên Tri 1 lần. Mỗi tầng Tiên Tri tăng 5%/6%/7%/8%/9% Tấn Công của người trang bị, Sát Thương Duy Trì gây ra bỏ qua 7.2%/7.9%/8.6%/9.3%/10% Phòng Thủ của mục tiêu.'),
(56, 'Trò Đùa Trần Thế', 3, 1164, 529, 463, '5', 'Giống như pháo hoa có thể nhấp nháy, trong một phút cô ấy cũng có thể biến đổi thành hàng chục sắc thái khác nhau.\r\n\r\nKhi buổi trình diễn pháo hoa sắp kết thúc, cô ấy ngồi ở một nơi cao, nhìn chằm chằm xuống đám người đông đúc dưới chân mình một cách chán chường.\r\n\r\nTừng ý nghĩ lần lượt xuất hiện rồi biến mất, sự ranh mãnh lướt qua trong đôi mắt tựa như kính vạn hoa.\r\n\r\n\"Quyết định rồi, hôm nay chơi vậy đi!\"\r\n\r\nCô ấy nhảy chân sáo, len lỏi vào đám đông thoăn thoắt như con thoi, chiếc đuôi màu vàng đỏ lúc ẩn lúc hiện đến khi khuất tầm mắt.\r\n\r\n\"Đừng bỏ lỡ nha, màn pháo hoa đẹp nhất chỉ vừa bắt đầu!\"', 'uploads/lightcones/Trò Đùa Trần Thế.jpg', 'Tăng 32%/39%/46%/53%/60% Sát Thương Bạo Kích của người trang bị. Khi bắt đầu chiến đấu, sẽ khiến người trang bị nhận Mặt Nạ, duy trì 3 hiệp. Khi người trang bị đang có Mặt Nạ, Tỷ Lệ Bạo Kích của đồng đội tăng 10%/11%/12%/13%/14%, Sát Thương Bạo Kích tăng 28%/35%/42%/49%/56%. Người trang bị mỗi khi hồi phục 1 Điểm Chiến Kỹ sẽ nhận được 1 tầng Ngọn Lửa Sắc Màu, Điểm Chiến Kỹ dư ra khi hồi phục cũng được tính vào. Sau khi Ngọn Lửa Sắc Màu đạt 4 tầng, sẽ xóa tất cả Ngọn Lửa Sắc Màu và nhận Mặt Nạ, duy trì 4 hiệp.'),
(57, 'Ước Hẹn Khắc Ghi', 2, 952, 476, 330, '4', 'Quá khứ phủ bụi mờ, trách nhiệm bảo vệ quê nhà, và ước mơ khai phá thế giới...\r\n...Đôi bàn tay thô ráp, chuyển những điều này cho chàng thiếu niên chưa hiểu sự đời.\r\n\r\n\"Không lâu nữa, chúng ta sẽ có thể đến một thế giới rộng lớn hơn phải không?\"\r\nĐứa trẻ vui sướng nhận lấy tín vật được truyền lại, tuổi đời còn quá nhỏ, cậu ấy vẫn chưa cảm nhận được tín vật đó mang đầy trọng trách.\r\n\r\n\"Chắc chắn rồi. Trước khi điều đó diễn ra, Misha hãy bảo vệ nơi này nhé!\"\r\n\r\nCậu ấy khắc ghi lời hứa đó trong lòng, mãi mãi...', 'uploads/lightcones/Ước Hẹn Khắc Ghi.jpg', 'Tăng 28%/35%/42%/49%/56% Tấn Công Kích Phá của người trang bị. Khi người trang bị thi triển Tuyệt Kỹ, Tỷ Lệ Bạo Kích tăng 15%/18.75%/22.5%/26.25%/30%, duy trì 2 hiệp.'),
(58, 'Vai Diễn Bắt Đầu', 4, 1058, 476, 264, '4', '...Ai đã thấy anh ta chết?\r\n...Là tôi, bằng đôi mắt nhỏ bé này!\r\n...Ai đã lấy đi máu của anh ta?\r\n...Là tôi, bằng cái đĩa nhỏ này!\r\n...Ai đã đào mộ cho anh ta?\r\n...Là tôi, bằng cái đục và cái xẻng của tôi!\r\n\r\nXem trí giả suy tính mọi chiến thuật, vương giả tối cao dùng quyền thế xoa dịu, người anh hùng tự coi mình là chính nghĩa, trong khi Kẻ Ngốc lại cười vui không ngớt...\r\nBây giờ, mời quý cô Sparkle mang đến cho chúng ta vở kịch cuối năm - \"Penacony, Đêm Kinh Hoàng!\"', 'uploads/lightcones/Vai Diễn Bắt Đầu.jpg', 'Sau khi người trang bị thi triển Hiệu Ứng Xấu lên kẻ địch, sẽ nhận 1 tầng Vai Diễn, mỗi tầng Vai Diễn tăng 6%/7%/8%/9%/10% sát thương do người trang bị gây ra, tối đa cộng dồn 3 tầng, duy trì 1 hiệp. Khi Chính Xác Hiệu Ứng của người trang bị lớn hơn hoặc bằng 80%, Tấn Công sẽ tăng 20%/24%/28%/32%/36%.'),
(59, 'Ngày Ngân Hà Sụp Đổ', 7, 952, 476, 330, '4', '\"Bánh Tart Phô Mai Nóng Chảy! Bạn chịu trách nhiệm mê hoặc người bảo vệ hành tinh!\"\r\n\"Lọ Lem Bánh Đậu! Bạn chịu trách nhiệm hạ gục binh lính hành tinh!\"\r\n\"Mai Ngâm Muối! Bạn chịu trách nhiệm xử lý người đứng đầu hành tinh!\"\r\n\"Đại Vương, ngài sẽ làm gì!\"\r\n\"Tôi sẽ đảm nhận việc nhập vai chú mèo dễ thương meo meo mê hoặc loài người ngốc nghếch này!\"\r\n\r\nHôm đó, tất cả các nền văn minh trong ngân hà cuối cùng cũng nhớ lại nỗi sợ hãi khi bị chúng thống trị...', 'uploads/lightcones/Ngày Ngân Hà Sụp Đổ.jpg', 'Khiến Tấn Công của người trang bị tăng 16%/18%/20%/22%/24%. Sau khi người trang bị tấn công, nếu có từ 2 kẻ địch bị tấn công trở lên mang Điểm Yếu thuộc tính tương ứng, thì Sát Thương Bạo Kích của người trang bị tăng 20%/25%/30%/35%/40%, duy trì 2 hiệp.'),
(60, 'Dây Thêu Dệt Vận Mệnh', 1, 952, 370, 463, '4', 'Vận mệnh luôn không ngừng dẫn dắt mọi người mà không cần lý do, còn cô ấy thì ẩn nấp trong bóng tối, khắc ghi lại những khoảnh khắc quan trọng nhất.\r\n\r\nXếp bài, gom bài, xào bài, rút bài... cô ấy mô phỏng bàn tay vận mệnh để điều khiển mọi biến số của con người, tìm kiếm quỹ đạo thật sự dưới lớp vỏ hỗn loạn.\r\n\r\nCuối cùng, khi những nhân vật đều vào đúng vị trí của mình, cô lướt qua bề mặt bài ngưng kết lại và ghép nối ra chân tướng bí ẩn.\r\n\"Vẫn còn thiếu một lá... thế thì, để tôi bổ sung vậy.\"', 'uploads/lightcones/Dây Thêu Dệt Vận Mệnh.jpg', 'Tăng 12%/14%/16%/18%/20% Kháng Hiệu Ứng của người trang bị. Mỗi khi người trang bị có 100 điểm Phòng Thủ, thì sát thương gây ra tăng 0.8%/0.9%/1.0%/1.1%/1.2%, tối đa tăng 32%/36%/40%/44%/48% sát thương gây ra.'),
(61, 'Ở Nơi Xa Ngọn Lửa', 2, 1058, 476, 264, '4', '\"Lấy được đồ rồi, việc chặn hậu giao cho anh đấy.\"\r\nÂm thanh của đồng đội truyền đến qua máy truyền tin.\r\n\r\nAnh nhìn quanh, trong các công trình lớn bị bỏ hoang, kẻ thù còn sót lại vẫn đang di chuyển trong kết cấu phức tạp, càng lúc càng tiến gần anh hơn.\r\n\"Chờ một lát.\"\r\n\r\nSau khoảnh khắc im lặng, một vụ nổ kinh thiên động địa như tai họa tận thế ập đến. Sóng áp suất đẩy khói bụi tứ tán khắp nơi, ánh sáng chói lọi khiến bầu trời đêm trở nên sáng như ban ngày.\r\n\"Anh biết bên trong đều là thuốc nổ chúng đã cài sẵn chứ?\"\r\n\r\nAnh ấy cởi chiếc áo choàng đang tung bay, giống như vừa hoàn thành một cuộc dạo chơi nhàn nhã.\r\n\"Tất nhiên.\"\r\n\"Cho nên tôi chỉ cần một que diêm để đốt chúng.\"', 'uploads/lightcones/Ở Nơi Xa Ngọn Lửa.jpg', 'Khi người trang bị trong 1 lần bị tấn công tích lũy lượng HP tổn thất vượt quá 25% Giới Hạn HP, hoặc tiêu hao HP bản thân trong 1 lần vượt quá 25% Giới Hạn HP, sẽ hồi ngay HP bằng 15% Giới Hạn HP của người trang bị, đồng thời tăng 25%/31.25%/37.5%/43.75%/50% Sát Thương do người trang bị gây ra, duy trì 2 hiệp. Hiệu ứng này mỗi 3 hiệp chỉ có thể kích hoạt 1 lần.'),
(62, 'Người Chiến Thắng Cuối Cùng', 6, 952, 476, 330, '4', '\"Anh không tin tôi à?\"\r\nAnh ta nhìn người đàn ông trước mặt một cách đầy thách thức, rút ra một khẩu súng, trút bỏ hết đạn, chỉ chừa lại một viên.\r\n\r\n\"Có vẻ như, để hợp tác vui vẻ, tôi phải giải thích phong cách làm việc của mình cho anh hiểu.\"\r\nAnh ta đặt khẩu súng vào tay đối phương, xoay xoay ổ đạn, rồi chĩa nòng súng về phía mình.\r\n\r\nAnh ta bóp cò liên tục, sau ba tiếng nổ trống không, nụ cười không chút biến sắc.\r\n\"Cuộc sống là một ván cược lớn, và tôi luôn là người chiến thắng cuối cùng.\"', 'uploads/lightcones/Người Chiến Thắng Cuối Cùng.jpg', 'Khiến Tấn Công của người trang bị tăng 12%/14%/16%/18%/20%. Sau khi người trang bị gây ra bạo kích lên kẻ địch, sẽ nhận 1 tầng May Mắn, tối đa cộng dồn 4 tầng. Mỗi tầng May Mắn khiến Sát Thương Bạo Kích của người trang bị tăng 8%/9%/10%/11%/12%, May Mắn sẽ mất khi kết thúc hiệp của người trang bị.'),
(63, 'Chuyến Thám Hiểm Thị Trấn Mộng Đẹp', 3, 952, 423, 396, '4', '\"Quý khán giả thân mến, Cậu Bé Đồng Hồ và đồng đội lại sắp gặp gỡ mọi người rồi!\"\r\n\r\n\"Trong tập trước, Ông Chủ Đá phát hiện ra Thị Trấn Mộng Đẹp ẩn chứa nguồn tài nguyên vô cùng phong phú nên đã bắt đầu tranh giành quyền thống trị thị trấn này. Cậu Bé Đồng Hồ hợp sức cùng Người Anh Em Hanu và Chim Giấy đuổi Ông Chủ Đá cùng đám thuộc hạ đáng ghét khỏi Thị Trấn Mộng Đẹp.\"\r\n\r\n\"Ông Chủ Đá không để mình bị hạ gục một cách dễ dàng như vậy, bèn tập hợp một đám côn đồ tuyên bố sẽ san bằng Thị Trấn Mộng Đẹp. Cậu Bé Đồng Hồ trong tập này sẽ phải đối mặt với nguy cơ lớn nhất trong lịch sử!\"\r\n\r\n\"Cậu Bé Đồng Hồ sẽ dùng những kế hoạch nào để đột phá vòng vây? Sẽ lại có những đồng minh nào mới gia nhập? Hãy đón xem tập mới nhất của Thị Trấn Mộng Đẹp Phiêu Lưu Ký!\"', 'uploads/lightcones/Chuyến Thám Hiểm Thị Trấn Mộng Đẹp.jpg', 'Sau khi người trang bị thi triển một trong các loại kỹ năng Tấn Công Thường/Chiến Kỹ/Tuyệt Kỹ, sẽ kèm theo Trái Tim Trẻ Thơ lên toàn bộ phe ta, Trái Tim Trẻ Thơ tăng 12%/14%/16%/18%/20% sát thương do kỹ năng loại tương ứng của mục tiêu phe ta gây ra. Trái Tim Trẻ Thơ chỉ có hiệu lực với loại kỹ năng mà người trang bị vừa mới sử dụng, và không thể cộng dồn.'),
(64, 'Đâu Là Thật?', 8, 1058, 423, 330, '4', '\"Nếu như tất cả đồng hồ đều chỉ sai thời gian...\"\r\n\r\n\"Nếu như những người hứa hẹn đều phản bội lại lời thề ước...\"\r\n\r\n\"Nếu những người bước vào cõi mộng chưa bao giờ thực sự ngủ...\"\r\n\r\n\"Nếu người chết trở về, nhận ra rằng tên của họ đã bị cướp mất...\"\r\n\r\n\"Nếu... nếu...\"\r\n\r\nAnh ta thổi tắt ngọn lửa, chỉ còn lại âm thanh đồng hồ tích tắc không ngừng trong bóng tối.', 'uploads/lightcones/Đâu Là Thật.jpg', 'Tăng 24%/30%/36%/42%/48% Tấn Công Kích Phá của người trang bị. Sau khi Tấn Công Thường, người trang bị sẽ hồi một lượng HP bằng 2%/2.5%/3%/3.5%/4% Giới Hạn HP + 800.'),
(65, 'Lễ Rửa Tội Thuần Khiết', 6, 952, 582, 529, '5', 'Ánh nắng chiều dịu nhẹ, hoa lá thơm nồng.\r\nMặc dù anh luôn chú trọng đến sự sạch sẽ, nhưng cũng sẽ khó tránh khỏi bị vấy bẩn bụi trần bởi dòng tư duy trì trệ.\r\n\r\n\"Chữa lành tâm trí cũng quan trọng như việc giữ gìn sức khỏe.\"\r\nSự ồn ào náo động của kẻ ngốc rồi cũng nhạt dần theo cái mệt mỏi, anh chìm vào trong nước, để mặc cho mạch suy nghĩ trôi đi, và tâm trí cũng dần dần thông thoáng.\r\n\r\n\"Chân lý sẽ tránh xa ô uế, chỉ cho phép sự thuần khiết tiếp cận.\"', 'uploads/lightcones/Lễ Rửa Tội Thuần Khiết.jpg', 'Tăng 20%/23%/26%/29%/32% Sát Thương Bạo Kích của người trang bị. Mỗi khi kẻ địch phải chịu 1 Hiệu Ứng Xấu, sẽ tăng thêm 8%/9%/10%/11%/12% Sát Thương Bạo Kích mà người trang bị gây lên kẻ này, tối đa cộng dồn 3 tầng. Khi thi triển Tuyệt Kỹ tấn công kẻ địch, khiến người trang bị nhận hiệu ứng Biện Luận, tăng 36%/42%/48%/54%/60% sát thương gây ra, Đòn Đánh Theo Sau bỏ qua 24%/28%/32%/36%/40% phòng thủ của mục tiêu, hiệu ứng này duy trì 2 hiệp.'),
(66, 'Tôi Trong Gương Ngày Ấy', 3, 1058, 529, 529, '5', 'Cô từng thích lưu giữ khoảnh khắc hoa mai sắp héo tàn ở lại bên mình.\r\nRất nhiều năm trôi qua, bao sự ồn ào, ngọt ngào, bao sự sống đã được sinh ra dưới đôi tay cô, nhưng hoa mai vẫn tàn héo như số phận đã định.\r\nRất nhiều năm trôi qua, dung mạo trong gương không hề thay đổi, nhưng trong thước đo của vũ trụ, có phải cô cũng chỉ giữ được sự tươi tắn trong khoảnh khắc ngắn ngủi mà thôi?\r\n\r\n\"Nếu mọi thứ đều khó thoát khỏi kiếp nạn diệt vong, thì sự kiến tạo còn có ý nghĩa gì chứ?\"\r\nKhông biết cô đã trầm mặc bao lâu, gió lay hoa khẽ rơi, và câu trả lời của người trong gương vẫn không hề thay đổi.\r\n\"Vì một ngày nào đó, sẽ không giữ được hoa mai nữa.\"\r\nCô xoa nhẹ tấm gương lạnh lẽo, tự lẩm bẩm như thế một mình không biết bao nhiêu lần rồi.', 'uploads/lightcones/Tôi Trong Gương Ngày Ấy.jpg', 'Khiến Tấn Công Kích Phá của người trang bị tăng 60%/70%/80%/90%/100%. Sau khi người trang bị thi triển Tuyệt Kỹ, sẽ khiến sát thương của toàn bộ phe ta tăng 24%/28%/32%/36%/40%, duy trì 3 hiệp, hơn nữa nếu Tấn Công Kích Phá của người trang bị lớn hơn hoặc bằng 150%, sẽ hồi 1 điểm Chiến Kỹ. Khi bắt đầu mỗi đợt, toàn bộ phe ta sẽ hồi ngay 10/13/15/18/20 Năng Lượng, kỹ năng cùng loại sẽ không thể cộng dồn.'),
(67, 'Đêm Kinh Hoàng', 8, 1164, 476, 529, '5', 'Thiếu nữ trốn sau cánh cửa, không dám bước lên thêm bước nào.\r\n\r\nCó lẽ bởi vì mục tiêu của đêm nay đã ở ngay trước mắt, nên cô có chút căng thẳng.\r\n\r\n\"Không sợ, không sợ, ta không sợ...\"\r\n\r\nTrong bóng tối, sự yên lặng như đang thét gào.\r\n\r\nThiếu nữ run rẩy, nắm chặt \"vũ khí\" trong tay, nhưng vẫn không dám đến gần \"mục tiêu\".\r\n\r\n\"Không có gì phải... phải... sợ!\"\r\n\r\nLão Đuôi phía sau rốt cuộc đã không chịu nổi nữa, đột nhiên sáng lên.\r\n\r\n\"Nhanh lên đi, lấy hàng chuyển phát nhanh thôi mà!\"', 'uploads/lightcones/Đêm Kinh Hoàng.jpg', 'Tăng 12%/14%/16%/18%/20% hiệu suất hồi phục Năng Lượng của người trang bị. Khi mục tiêu phe ta thi triển Tuyệt Kỹ, người trang bị sẽ hồi HP bằng 10% Giới Hạn HP cho mục tiêu phe ta có Phần Trăm HP thấp nhất hiện tại. Khi người trang bị trị liệu cho mục tiêu phe ta, sẽ khiến tấn công của mục tiêu này tăng 2.4%, hiệu ứng này tối đa cộng dồn 5 tầng, duy trì 2 hiệp.'),
(68, 'Hi, Tôi Ở Đây', 8, 952, 423, 396, '4', 'Ba cô gái đi trong đêm tối, cảnh vật xung quanh lúc này có thêm phần ma quái.\r\n\r\n\"Huohuo đi nhanh lên, nếu không bọn tội không đợi cô đâu đấy!\"\r\n\r\nCô ấy giơ chiếc điện thoại lên bước nhanh về phía trước, dường như không bị ảnh hưởng bởi bầu không khí quái dị xung quanh.\r\n\r\n\"Tôi... Tôi ở đây!\"\r\n\r\nCô gái đứng phía trước rụt rè trả lời.\r\n\r\n\"Ủa? Thì ra cô ở phía trước à, vậy sau lưng tôi và Shangshang là...\"\r\n\r\nCái kết hoàn hảo nhất của cảnh tượng lúc này... nhất định chính là tiếng hét của cô gái.', 'uploads/lightcones/Hi, Tôi Ở Đây.jpg', 'Khiến Giới Hạn HP của người trang bị tăng 8%/9%/10%/11%/12%. Khi người trang bị thi triển Chiến Kỹ, lượng trị liệu tăng 16%/19%/22%/25%/28%, duy trì 2 hiệp.'),
(69, 'Khoảnh Khắc Nơi Đáy Mắt', 7, 1058, 582, 463, '5', 'Anh đã từng nghe những bài hát ca ngợi ánh sáng đó:\r\nHoa hồng nở rộ, là để đáp lại nụ cười của ngài.\r\nÁnh sáng lấp lánh, là vì muốn chiếu sáng khuôn mặt ngài.\r\nKhúc nhạc ngân lên, là để đánh thức đôi mắt ngài.\r\n\r\nNhưng tia sáng ấy sẽ không quay lại nữa, anh vẫn bôn ba ở tận nơi xa.\r\nChỉ cần chờ đợi, chỉ cần tôn thờ, chỉ cần gieo trồng vẻ đẹp thuần khiết vào trong vũ trụ.\r\nCho dù mọi thứ chỉ là khoảnh khắc thoáng qua nơi đáy mắt, thì anh cũng đã có được câu trả lời mà mình mong đợi.', 'uploads/lightcones/Khoảnh Khắc Nơi Đáy Mắt.jpg', 'Khiến sát thương bạo kích của người trang bị tăng 36%/42%/48%/54%/60%. Khi người trang bị thi triển Tuyệt Kỹ, sẽ tăng sát thương Tuyệt Kỹ mà người trang bị gây ra theo giới hạn năng lượng của người trang bị: mỗi điểm năng lượng tăng 0.36%/0.42%/0.48%/0.54%/0.6%, tối đa 180 điểm năng lượng.'),
(70, 'Thân Này Là Kiếm', 2, 1164, 582, 396, '5', 'Thân kiếm vỡ vụn, rơi vào trong cái lạnh thấu xương.\r\n\r\nVũ khí tầm thường cũng là vật vô dụng, giới hạn của nó... chỉ bằng một cái nhìn là có thể nhìn thấu. \"Tôi còn cần kiếm để làm gì chứ?\" Cô ấy không luyến tiếc thứ gì. không có thứ gì có thể chứa trong tầm mắt.\r\n\r\n\"Kể từ thời khắc này, thân này là kiếm\"\r\n\r\nNếu muốn vượt qua thứ gọi là giới hạn, nếu muốn đột phá hơn người\r\n\r\n—Chỉ có một cách duy nhất là, xem bản thân mình là \"Củi\".', 'uploads/lightcones/Thân Này Là Kiếm.jpg', '﻿Tăng 20%/23%/26%/29%/32% Sát Thương Bạo Kích của người trang bị. Sau khi đồng đội bị tấn công hoặc tiêu hao HP, người trang bị sẽ nhận 1 tầng Nguyệt Thực, tối đa cộng dồn 3 tầng. Mỗi tầng Nguyệt Thực khiến sát thương gây ra ở lần tấn công kế tiếp của người trang bị tăng 14%/16.5%/19%/21.5%/24%. Khi cộng dồn đủ 3 tầng sẽ khiến lần tấn công này bỏ qua 12%/14%/16%/18%/20% phòng thủ của mục tiêu. Hiệu ứng này sẽ bị giải trừ sau khi người trang bị thi triển tấn công.'),
(71, 'Phiền Não, Hạnh Phúc', 6, 1058, 582, 463, '5', 'Đối với một nhân viên văn phòng đủ tiêu chuẩn, sẽ không cho phép xảy ra việc \"bị áp đảo\".\r\nCho dù phải đối diện với áp lực hay là làm việc mệt mỏi, đều phải đứng thẳng người vượt mọi khó khăn!\r\nTrừ phi... bị \"bọn chúng\" đè bẹp xuống đất.\r\n\r\n\"Ôi, đừng vội! Tôi chỉ có hai tay thôi mà!\"\r\nCô gái vừa kết thúc tăng ca về nhà, đã bị một đám sinh vật lông mềm mại đè bẹp xuống đất.\r\nChúng có lẽ không hiểu được lời của cô, chỉ lo áp sát chiếc đầu tròn trịa vào bàn tay cô.\r\nMọi mệt mỏi dần dần tan biến, thay vào đó là cảm giác thoải mái dễ chịu từ những cuộn lông mềm.', 'uploads/lightcones/Phiền Não, Hạnh Phúc.jpg', 'Tăng 18%/21%/24%/27%/30% Tỷ Lệ Bạo Kích của người trang bị, tăng 30%/35%/40%/45%/50% sát thương do Đòn Đánh Theo Sau gây ra. Sau khi người trang bị thi triển Đòn Đánh Theo Sau, khiến mục tiêu rơi vào trạng thái Ôn Hòa, hiệu ứng này tối đa cộng dồn 2 tầng. Khi mục tiêu phe ta đánh trúng kẻ địch ở trạng thái Ôn Hòa, mỗi tầng Ôn Hòa sẽ tăng 12%/14%/16%/18%/20% Sát Thương Bạo Kích gây ra.'),
(72, 'Một Mình Chữa Trị', 4, 1058, 529, 396, '5', 'Bí ẩn tự có, không hình không dạng.\r\n\r\nVạn vật hư vô, như mơ như ảo.\r\n\r\nKẻ tự hủy diệt, muốn sinh ắt sinh.\r\n\r\nSắp tắt lại bùng, lắp đầy khi cạn.\r\n\r\nCứu rỗi hỗn độn, linh dược ảo ảnh.\r\n\r\nBóng tối tinh tú sau lưng, phí công giam đầy trong lọ.\r\n\r\n\"Nghịch lý như thế, mà chúng ta lại tự hào vì nó.\"', 'uploads/lightcones/Một Mình Chữa Trị.jpg', 'Tăng 20%/25%/30%/35%/40% Tấn Công Kích Phá của người trang bị. Khi người trang bị thi triển Tuyệt Kỹ, sẽ khiến Sát Thương Duy Trì của người trang bị gây ra tăng 24%/30%/36%/42%/48%, duy trì 2 hiệp. Khi kẻ địch rơi vào hiệu ứng Sát Thương Duy Trì mà người thi triển bị tiêu diệt, người trang bị hồi phục 4/5/6/7/8 năng lượng.'),
(73, 'Đôi Mắt Đã Nhắm Lại', 1, 1270, 423, 529, '5', 'Cơn đau ở giữa trán sẽ theo cô ấy đến hết đời, nhưng vẫn không bằng một phần nghìn những gì cô ấy đã trải qua trong quá khứ.\r\n\r\nCô ấy cho rằng bản thân tính toán như thần, cho đến khi người bạn cũ ra đi.\r\n\r\nBây giờ, cô ấy \"thấy\" thế giới đang dần chuyển biến, vật đổi sao dời...\r\n\r\nNhưng cô ấy đã nhắm đôi mắt lại, bởi vì, tương lai đã được định đoạt\r\n\r\n——trong \"con mắt thứ ba\".', 'uploads/lightcones/Đôi Mắt Đã Nhắm Lại.jpg', 'Tăng 24%/28%/32%/36%/40% Giới Hạn HP của người trang bị, hiệu suất hồi phục Năng Lượng tăng 12%/14%/16%/18%/20%. Khi HP của người trang bị giảm thấp, khiến toàn thể phe ta tăng 9%/10,5%/12%/13,5%/15% sát thương gây ra, duy trì 2 hiệp.\r\n\r\nKhi bắt đầu mỗi đợt, sẽ hồi HP cho toàn phe ta bằng 80%/85%/90%/95%/100% lượng HP mỗi nhân vật đã mất.'),
(74, 'Rực Rỡ Hơn Mặt Trời', 2, 1058, 635, 396, '5', 'Kể từ khi được sinh ra, đôi mắt của cậu ấy chỉ nhìn thấy lao tù tăm tối,\r\nnhững tội lỗi không liên quan trói buộc cậu ấy ở nơi này, những ký ức không liên quan nhấn chìm cậu ấy.\r\nCậu ấy vùng vẫy, cố hết sức để hít thở,\r\nthử bắt lấy một tia sáng hiếm hoi trong đại dương vô tận này.\r\n\r\nCho đến khi tướng quân bước vào khu nhà giam không nhìn thấy bầu trời này,\r\nvà anh ấy nhìn thấy thứ còn lấp lánh hơn cả ánh sáng mặt trời, đó chính là ánh mắt của người thiếu niên ấy.', 'uploads/lightcones/Rực Rỡ Hơn Mặt Trời.jpg', 'Khiến Tỷ Lệ Bạo Kích của người trang bị tăng 18%/21%/24%/27%/30%. Khi người trang bị thi triển tấn công thường, sẽ nhận 1 tầng Rồng Gầm, duy trì 2 hiệp. Mỗi tầng Rồng Gầm sẽ tăng 18%/21%/24%/27%/30% tấn công của người trang bị, hiệu suất hồi năng lượng tăng 6%/7%/8%/9%/10%. Rồng Gầm tối đa cộng dồn 2 lần.'),
(75, 'Chỉ Cần Chờ Đợi', 4, 1058, 582, 463, '5', '\"Hay là ngươi vẫn còn chuyện chưa nói cho ta biết?\"\r\n\r\nCô ta nhìn thấy đối phương cứng họng, nuốt nước bọt đánh ực một cái.\r\nNgười này sợ cô ta, cho nên cô ta vốn không cần phải dùng đến năng lực.\r\nTrên thực tế, cô ta nói gì cũng chẳng quan trọng nữa.\r\nBất kỳ từ nào cô ta thốt ra cũng đủ mang đến nỗi khiếp sợ kinh người...\r\nĐối phương không còn con đường nào khác để lựa chọn ngoài việc nói ra tất cả những gì mà cô ta muốn biết.\r\n\r\nCòn cô ta chỉ việc chờ đợi, giống như con nhện đã giăng sẵn mạng nhện rồi vậy.', 'uploads/lightcones/Chỉ Cần Chờ Đợi.jpg', 'Sát thương người trang bị gây ra tăng 24%/28%/32%/36%/40%. Sau mỗi lần người trang bị thi triển tấn công, tốc độ tăng 4.8%/5.6%/6.4%/7.2%/8%, tối đa cộng dồn 3 tầng. Khi người trang bị đánh trúng kẻ địch, nếu mục tiêu này không ở trạng thái Xói Mòn sẽ có 100% xác suất cơ bản khiến kẻ đó rơi vào trạng thái Xói Mòn. Khi kẻ địch đã ở trạng thái này sẽ được xem là rơi vào Sốc Điện. Ở trạng thái Xói Mòn, kẻ địch mỗi khi bắt đầu hiệp sẽ chịu Sát Thương Lôi Duy Trì bằng 60%/70%/80%/90%/100% Tấn Công của người trang bị, duy trì 1 hiệp.'),
(76, 'Bến Bờ Xa Diệu Vợi', 2, 1270, 582, 330, '5', 'Anh ấy mỗi năm đều đến đây, mang theo vài thanh kiếm mới.\r\n\r\nVà anh ấy đều đã nhìn mặt lần cuối những chủ nhân của tất cả các thanh kiếm ấy.\r\n\r\nMỗi khi rời đi, anh ấy lại đau đáu suy nghĩ...\r\n\r\nKiếm của mình, bao giờ mới bị người khác mang đến đây...', 'uploads/lightcones/Bến Bờ Xa Diệu Vợi.jpg', 'Khiến Tỷ Lệ Bạo Kích của người trang bị tăng 18%/21%/24%/27%/30%. Sau khi người trang bị bị tấn công hoặc tiêu hao HP của bản thân, sát thương gây ra sẽ tăng 24%/28%/32%/36%/40%, hiệu ứng này sẽ giải trừ sau khi người trang bị thực hiện tấn công.'),
(77, 'Trước Khi Bắt Đầu Nhiệm Vụ Tân Thủ', 4, 952, 476, 330, '4', 'Còn bao lâu nữa thì hành động?\"\r\n\r\n\"Không ngờ lại hỏi tôi câu này... người mới xảy ra chuyện gì sao?\"\r\n\r\n\"Không có.\"\r\n\r\n\"Tuy Elio mời cô ấy là vì nguyên nhân khác, nhưng chí ít cô ấy cũng phải sống qua được nhiệm vụ tân thủ.\"\r\n\r\n\"Cô ấy rất ung dung.\"\r\n\r\n\"Vậy thì tôi cũng yên tâm rồi. Xem ra Blade cũng đã là một tiền bối vô cùng đáng tin cậy rồi.\"\r\n\r\n\"...\"', 'uploads/lightcones/Trước Khi Bắt Đầu Nhiệm Vụ Tân Thủ.jpg', 'Tăng 20%/25%/30%/35%/40% Chính Xác Hiệu Ứng của người trang bị. Khi người trang bị tấn công kẻ địch bị giảm Phòng Thủ, sẽ hồi 4/5/6/7/8 điểm Năng Lượng.'),
(78, 'Tiếng Vọng Của Quan Tài', 8, 1164, 582, 396, '5', 'Bên cạnh khóm hoa diên vĩ màu trắng, anh ấy gõ vào quan tài.\r\n\r\n\"Thỉnh thoảng tôi cảm thấy như đang đánh giá thấp anh khi thực hiện giao dịch này với anh vậy.\"\r\n\r\nKhông ai trả lời, chỉ có những chiếc gai nhọn đâm thẳng vào tim anh ấy không chút do dự.\r\n\r\nNhưng anh cũng vừa cười vừa bắt lấy những chiếc gai ấy.\r\n\r\n\"Vậy đó, chúng ta đều lợi dụng lẫn nhau, nhưng lại xem thường đối phương.\"', 'uploads/lightcones/Tiếng Vọng Của Quan Tài.jpg', 'Tăng 24% Tấn Công của người trang bị, sau khi thi triển Tuyệt Kỹ khiến toàn phe ta tăng 12 điểm Tốc Độ, duy trì 1 hiệp. Đồng thời mỗi khi người trang bị đánh trúng 1 kẻ địch khác nhau, sẽ hồi 3 điểm Năng Lượng, mỗi lần tấn công tối đa hồi Năng Lượng 3 lần bằng cách này.'),
(79, 'Cơn Mưa Tầm Tã', 4, 1058, 582, 463, '5', 'Cô ấy bước ra khỏi mái hiên, gương chiếc ô, vừa ngâm nga vừa luồn lách vào góc hẻm nhỏ.\r\n\r\nMột lúc sau, tiếng mưa dừng hẳn.\r\n\r\nCô ấy vò vò đầu tóc, lướt lướt màn hình, vài giọt mưa lại lăn xuống khoảng không gian nhỏ hẹp trên đầu.\r\n\r\nNghe tiếng tí tách của từng hạt mưa trên chiếc ô, phối hợp với âm thanh của game, tâm trạng của cô tốt vô cùng.\r\n\r\n\"Chơi game trong ngày mưa sảng khoái thật.\"', 'uploads/lightcones/Cơn Mưa Tầm Tã.jpg', 'Tăng 24%/28%/32%/36%/40% Chính Xác Hiệu Ứng của người trang bị. Khi người trang bị gây sát thương cho kẻ địch đang có 3 Hiệu Ứng Xấu hoặc hơn, thì Tỷ Lệ Bạo Kích sẽ tăng 12%/14%/16%/18%/20%. Sau khi người trang bị thi triển Tấn Công Thường, Chiến Kỹ, Tuyệt Kỹ sẽ có 100% xác suất cơ bản thi triển Mã Aether lên 1 mục tiêu bị tấn công ngẫu nhiên chưa có Mã Aether. Sát thương phải chịu mà mục tiêu có Mã Aether tăng 12%/14%/16%/18%/20%, duy trì 1 hiệp.'),
(80, 'Quá Khứ Tương Lai', 3, 952, 423, 396, '4', 'Hai cánh diều giấy trong tay đứt rời, giống như sự ra đi của người trân quý nhất ấy.\r\n\r\n\"Nếu chỉ có bạn cứu được chúng tối, thì sự hy sinh của chúng tôi...\"\r\n\r\nHỏa pháo nhuộm đỏ bầu trời, còn trời cao vẫn hùng vĩ như xưa.\r\n\r\nCô ngẩng đầu nhìn lên, trong mắt đầy sự phẫn nộ.', 'uploads/lightcones/Quá Khứ Tương Lai.jpg', 'Sau khi người trang bị thi triển Chiến Kỹ, sẽ khiến sát thương gây ra bởi mục tiêu khác của phe ta trong hành động tiếp theo tăng 16%/20%/24%/28%/32%, duy trì 1 hiệp.'),
(81, 'Làn Nước Xuân Xuất Hiện', 6, 846, 476, 396, '4', 'Thiếu niên luyện kiếm nghỉ ngơi một lát.\r\n\r\nÁnh nắng rọi vào vùng cổ của anh có chút bỏng rát, anh bước xuống làn nước, lúc chưa ngập quá bàn chân, mặc dù lạnh, nhưng tiếng chuông kêu vang, tiếng chim líu lo, người ở phương xa biết rằng mùa xuân đã đến.', 'uploads/lightcones/Làn Nước Xuân Xuất Hiện.jpg', 'Sau khi vào chiến đấu, tăng 8%/9%/10%/11%/12% tốc độ của người trang bị, sát thương gây ra tăng 12%/15%/18%/21%/24%. Sau khi người trang bị chịu sát thương, hiệu ứng này sẽ mất hiệu lực, kết thúc hiệp kế tiếp sẽ hồi phục lại hiệu ứng này.'),
(82, 'Chúng Ta Là Lửa Ngầm', 1, 740, 476, 463, '4', 'Cô ấy đã quen với việc người ta mất đi quê nhà, cũng quen với việc người ta mất đi sinh mạng, nhưng khi có một mình, có khóc cũng vô dụng.\r\n\r\n\"Cột chiếc khăn tơ đỏ, chia sẻ nỗi đau với nhau.\"\r\n\r\n\"Chúng ta là người một nhà... chúng ta là Lửa Ngầm.\"\r\n\r\nKhi bàn tay rộng mở xoa lên đầu cô ấy, cô ấy cuối cùng cũng không kìm nén được mà rơi lệ.', 'uploads/lightcones/Chúng Ta Là Lửa Ngầm.jpg', 'Khi bắt đầu chiến đấu sẽ khiến sát thương toàn phe ta phải chịu giảm 8%/10%/12%/14%/16%, duy trì 5 hiệp. Đồng thời hồi HP cho toàn phe ta tương đương 30%/35%/40%/45%/50% HP đã mất của mỗi người.'),
(83, 'Ký Hiệu Kéo Dài', 4, 952, 476, 330, '4', 'Chiếc đĩa hát xoay đều xoay đều, tiếng reo hò hòa quyện trong bầu không khí tưng bừng.\r\n\r\nCô ấy có vẻ như không có sự phòng bị, đắm chìm trong giai diệu hài hòa mê người:\r\n\r\nĐầu tiên là sự thành khẩn và mong đợi,\r\n\r\nkế đến là bộc lộ chân thành quyết liệt,\r\n\r\nnhịp điệu cao trào mỗi lúc một tăng...\r\n\r\nCho đến khi đĩa hát ngừng xoay, tiếng reo hò khàn đặc vụt tắt.\r\n\r\n\"Kết thúc nhiệm vụ.\"', 'uploads/lightcones/Ký Hiệu Kéo Dài.jpg', 'Tăng 16%/20%/24%/28%/32% Tấn Công Kích Phá của người trang bị, sát thương gây ra cho địch ở trạng thái ở Sốc Điện hoặc Bào Mòn tăng 16%/20%/24%/28%/32%, hiệu ứng này cũng có hiệu lực với sát thương duy trì.'),
(84, 'Đổi Ngang Giá', 8, 952, 423, 396, '4', 'Sau khi được Long Nữ xoa bóp bằng sức mạnh dữ dội, cô gái Người Hồ Ly giơ cánh tay ra, nắn khuôn mặt tròn như quả trứng của cô ấy. Nhìn thấy đứa bé nhỏ nhắn đang chu mỏ bực bội, cô ấy bất giác cười tươi.\r\n\r\n\"Đây gọi là đổi ngang giá mà.\"', 'uploads/lightcones/Đổi Ngang Giá.jpg', 'Khi bắt đầu hiệp của người trang bị, sẽ hồi 8/10/12/14/16 Năng Lượng cho 1 mục tiêu khác ngẫu nhiên của phe ta có Năng Lượng hiện tại dưới 50%.'),
(85, 'Sự Nghỉ Ngơi Của Thiên Tài', 7, 846, 476, 396, '4', 'Cô ấy gọi món bánh trôi màu xanh.\r\n\r\n\"Kết thúc thử nghiệm, có thể nghỉ ngơi rồi. Tôi không thể bỏ lỡ món điểm tâm vừa mới ra lò này.\"\r\n\r\nCô ấy nhìn sang người thiếu nữ con rối đang cau mày trầm tư.\r\n\r\n\"Với cô thể này của cô chỉ có thể nhìn thôi, tôi sẽ gói một hộp về cho cô.\"\r\n\r\nCô ấy nhìn thấy gương mặt quý tộc của robot lộ vẻ lúng túng, cầm chiếc ly lên.\r\n\r\n\"Yên tâm đi, đó là dầu máy.\"', 'uploads/lightcones/Sự Nghỉ Ngơi Của Thiên Tài.jpg', 'Khiến Tấn Công của người trang bị tăng 16%/20%/24%/28%/32%, sau khi người trang bị tiêu diệt kẻ địch, Sát thương Bạo Kích tăng 24%/30%/36%/42%/48%, duy trì 3 hiệp.'),
(86, 'Dưới Bầu Trời Xanh', 2, 952, 476, 330, '4', 'Những đọt lúa đung đưa dưới bầu trời xanh, cô gái tỉ mỉ đan chiếc vòng hoa, đội lên trán cô em thân thương nhất.\r\n\r\nHọ lúc đó cao ngang nhau, họ lúc đó vẫn cười như thế.', 'uploads/lightcones/Dưới Bầu Trời Xanh.jpg', 'Khiến Tấn Công của người trang bị tăng 16%/20%/24%/28%/32%, sau khi người trang bị tiêu diệt kẻ địch, Tỷ Lệ Bạo Kích tăng 12%/15%/18%/21%/24%, duy trì 3 hiệp.'),
(87, 'Múa! Múa! Múa!', 3, 952, 423, 396, '4', '\"Vì sao Hook không thể nhảy múa xoay vòng giống như Clara nhỉ...\"\r\n\r\nCô ấy nắm chặt tay, miệng lầu bầu, đội má tròn trịa ửng hồng trong chiếc nón lông.\r\n\r\n\"Ngài Svarog có thể giúp Hook không?\"\r\n\r\nRobot nghiêm trang không nói một lời nào chìa cánh tay máy móc của mình ra.\r\n\r\n\"Quay muốn xỉu rồi! Đáng ghét... thả ta xuống mau!\"', 'uploads/lightcones/Múa! Múa! Múa!.jpg', 'Sau khi người trang bị thi triển Tuyệt Kỹ, toàn phe ta sẽ ưu tiên hành động 16%/18%/20%/22%/24%.'),
(88, 'Hãy Theo Dõi Nào!', 6, 952, 476, 330, '4', '\"Nào nào! Các khán giả thân mến, tín hiệu ở đây tốt không?\"\r\n\r\nCô gái tóc cam từ từ nhấc một chiếc búa tạ trong khi hét về phía ống kính một cách nhiệt tình.\r\n\r\n\"Mọi người có ai từng thấy thiếu nữ dùng ngực đập vỡ tảng đá chưa!\"\r\n\r\n\"Tối làm Vân Kỵ Quân không phải để làm việc này đâu đấy!\"\r\n\r\nCô gái tóc nâu mắt nở đom đóm, trong khi cô gái tóc cam nở nụ cười ranh mãnh.\r\n\r\n\"Làm ơn đi... Đây cũng là hạnh phúc khi giúp đỡ người khác đấy!\"', 'uploads/lightcones/Hãy Theo Dõi Nào!.jpg', 'Tăng 24%/30%/36%/42%/48% sát thương tấn công thường và Chiến Kỹ của người trang bị, khi Năng Lượng hiện tại của người trang bị bằng giới hạn Năng Lượng, thì hiệu quả này sẽ tăng thêm 24%/30%/36%/42%/48%.'),
(89, 'Xu Hướng Thị Trường Vũ Trụ', 1, 1058, 370, 396, '4', 'Giá trị của hành tinh đang được tính toàn lại...\r\n\r\n\"Dự án này có phải đã nói lên rằng, lợi ích qua lại của việc truy hồi nợ?\"\r\n\r\n\"Nhưng chúng ta chỉ cần truyền đạt một thông tin, chính là tuyên bố sự tồn tại của chúng ta.\"\r\n\r\n\"Nếu như nó có giá trị riêng, thì sẽ đem thêm giá trị đến cho công ty.\"', 'uploads/lightcones/Xu Hướng Thị Trường Vũ Trụ.jpg', 'Khiến Phòng Thủ của người trang bị tăng 16%/20%/24%/28%/32%. Sau khi người trang bị chịu tấn công sẽ có 100%/105%/110%/115%/120% xác suất cơ bản khiến kẻ địch rơi vào trạng thái Thiêu Đốt, mỗi hiệp gây sát thương liên tục bằng 40%/50%/60%/70%/80% Phòng Thủ của người trang bị, duy trì 2 hiệp.'),
(90, 'Sự Quyết Tâm Như Giọt Mồ Hôi Lấp Lánh', 4, 952, 476, 330, '4', 'Ánh đèn trên võ đài lấp lánh, nhưng không lấp lánh bằng bằng những giọt mồ hôi chảy dọc sống lưng.\r\n\r\nAnh ấy đã quyết tâm dùng nắm đấm của mình để bảo vệ những người quan trọng.\r\n\r\nVà cũng chính họ là những người đã mang đến cho anh sự quyết tâm ấy.\r\n\r\n...Cho dù là trên hay dưới võ đài, đều đang đứng sau lưng của anh.', 'uploads/lightcones/Sự Quyết Tâm Như Giọt Mồ Hôi Lấp Lánh.jpg', 'Khi người trang bị đánh trúng mục tiêu địch, nếu mục tiêu này không ở trạng thái Đánh Hạ, sẽ có 60%/70%/80%/90%/100% xác suất cơ bản khiến địch rơi vào trạng thái Đánh Hạ. Kẻ địch ở trạng thái Đánh Hạ sẽ giảm 12%/13%/14%/15%/16% Phòng Thủ, duy trì 1 hiệp.'),
(91, 'Vừa Đúng Lúc', 8, 952, 423, 396, '4', 'Đường phố náo nhiệt, anh ấy dạo bước, ánh nắng cũng hòa quyện theo từng bước chân.\r\n\r\nRồi anh dừng chân, chọn một chiếc dù giấy dùng che mát.\r\n\r\n-Cũng để che đi ánh nhìn đó.', 'uploads/lightcones/Vừa Đúng Lúc.jpg', 'Kháng Hiệu Ứng của người trang bị tăng 16%/20%/24%/28%/32%, đồng thời tăng lượng trị liệu của người trang bị, trị số tăng tương đương 33%/36%/39%/42%/45% Kháng Hiệu Ứng, tối đa tăng trị liệu 15%/18%/21%/24%/27%.'),
(92, 'Chất Liệu Ký Ức', 1, 1058, 423, 529, '5', 'Vũ trụ như một hoang địa chứa đầy rác.\r\n\r\nNếu ngẫu nhiên có ngọc quý, cũng không ai nhặt.\r\n\r\nNgài nhìn xuyên qua những khe hở của quá khứ, và chọn ra hạt giống của Ký Ức.\r\n\r\nNếu sự sống mới muốn nảy mầm, thì hạt giống của nó sẽ phải chết.\r\n\r\nNhững viên ngọc hồng, lam, trắng được đem đến, lấp lánh nhẹ nhàng trong vườn ươm của ngài, nhưng vũ trụ thì vẫn lặng im bí ẩn.', 'uploads/lightcones/Chất Liệu Ký Ức.jpg', 'Khiến Kháng Hiệu Ứng của người trang bị tăng 8%/10%/12%/14%/16%, sau khi người trang bị bị tấn công, nếu bản thân chưa có khiên, sẽ nhận 1 khiên bằng 16%/20%/24%/28%/32% Giới Hạn HP của người trang bị, duy trì 2 hiệp. Hiệu ứng này mỗi 3 hiệp chỉ có thể kích hoạt 1 lần. Nếu người trang bị đã có khiên, sẽ khiến sát thương phải chịu của bản thân giảm 12%/15%/18%/21%/24%.'),
(93, 'Du Hành Trên Biển Sao', 6, 952, 529, 463, '5', 'Du hành giữa biển sao, tốc độ của ngài ấy như sắc cầu vồng vụt qua.\r\n\r\nĐuổi theo Tà Vật bất diệt, tìm kiếm thuốc giải quý báu, tìm kiếm con đường giải thoát.\r\n\r\nHành trình của ngài như lời thề vĩnh viễn không thể bị phá vỡ, vĩnh viễn không có hồi kết.', 'uploads/lightcones/Du Hành Trên Biển Sao.jpg', '\r\nKhiến Tỷ Lệ Bạo Kích của người trang bị tăng 8%/10%/12%/14%/16%, Tỷ Lệ Bạo Kích mà người trang bị gây cho kẻ địch có phần trăm HP từ 50% trở xuống tăng thêm 8%/10%/12%/14%/16%. Sau khi người trang bị tiêu diệt kẻ địch, Tấn Công tăng 20%/25%/30%/35%/40%, duy trì 2 hiệp.');
INSERT INTO `lightcones` (`id`, `name`, `path_id`, `hp`, `atk`, `def`, `rarity`, `description`, `image`, `path_effect`) VALUES
(94, 'Sự Sụp Đổ Của Aeon', 2, 1058, 529, 396, '5', 'Bắt đầu từ một tia sáng.\r\n\r\nHọ rơi vào mối đe dọa hủy diệt đang dâng cao.\r\n\r\nHọ không thể không ngừng việc tự sao chép, bao bọc lấy nhau sau những giây phút tranh giành khủng khiếp,\r\n\r\ncố gắng dùng quyền sinh sôi để đổi lấy khả năng sinh tồn.\r\n\r\nHọ bắt tay nhau, chưa bao giờ đoàn kết như thế.\r\n\r\nNhưng một khi vận mệnh kết thúc, họ cũng đi đến cái chết thật sự.', 'uploads/lightcones/Sự Sụp Đổ Của Aeon.jpg', 'Khi người trang bị tấn công, sẽ tăng 8%/10%/12%/14%/16% Tấn Công của người trang bị trong trận hiện tại, hiệu ứng này tối đa cộng dồn 4 tầng. Sau khi người trang bị phá vỡ điểm yếu của kẻ địch, sát thương gây ra tăng 12%/15%/18%/21%/24%, duy trì 2 hiệp.'),
(95, 'Thời Gian Không Ngừng Lại', 8, 1270, 476, 463, '5', 'Cô ấy đã đứng rất lâu bên cửa sổ.\r\n\r\nKhi bào chế thuốc ấm từ bạc hà, kinh giới, thì hoa đã nở bên ngoài cửa.\r\n\r\nKhi bào chế thuốc cay nồng từ cây hương nhu, cây gừng, thì trời đã mưa bên ngoài cửa.\r\n\r\nKhi bào chế thuốc vị chua từ thược dược, ô mai, thì lá đã rơi bên ngoài cửa.\r\n\r\nKhi bào chế thuốc lạnh đắng từ hoàng cầm, tri mẫu, thì tuyết đã rơi bên ngoài cửa.\r\n\r\nNăm tháng trôi qua, thời gian không ngừng lại, nhưng cánh cửa này, người ngồi bên cửa này, vẫn không hề thay đổi.', 'uploads/lightcones/Thời Gian Không Ngừng Lại.jpg', 'Khiến Giới Hạn HP của người trang bị tăng 18%/21%/24%/7%/30%, lượng trị liệu tăng 12%/14%/16%/18%/20%. Khi người trang bị cung cấp trị liệu cho mục tiêu phe ta, sẽ ghi lại lượng trị liệu. Sau khi mục tiêu bất kỳ của phe ta tấn công, sẽ căn cứ 36%/42%/48%/54%/60% lượng trị liệu được ghi, gây sát thương kèm theo cho 1 kẻ địch bị tấn công ngẫu nhiên dựa trên thuộc tính người trang bị. Sát thương này không chịu ảnh hưởng bởi các buff khác, mỗi hiệp tối đa tổng kết 1 lần.'),
(96, 'Giấc Ngủ Say Mềm', 6, 1058, 482, 463, '5', 'Dù đã ngủ ngon lành trên vai tướng quân, nhưng cậu bé vẫn nắm chặt thanh kiếm.\r\n\r\nAnh ấy vỗ nhè nhẹ vào má cậu bé, chỉ nghe thấy tiếng làu bàu.\r\n\r\n\"Tướng quân... cuối cùng... tôi cũng thắng rồi...\"\r\n\r\nAnh ấy ngẩng người trong chốc lát, đột nhiên ý thức được, so với cậu bé đang nỗ lực trưởng thành mỗi ngày,\r\n\r\nbản thân đã trải qua những năm tháng dài đằng đẵng, đã sớm quen với việc \"bất biến\".\r\n\r\n\"Sẽ nhanh thôi, cậu sẽ chiến thắng tất cả. Sau thời khắc đó, mới là sự khởi đầu...\"', 'uploads/lightcones/Giấc Ngủ Say Mềm.jpg', '\r\nKhiến người trang bị tăng 30%/35%/40%/45%/50% Sát Thương Bạo Kích. Khi sát thương Tấn Công Thường hoặc Chiến Kỹ của người trang bị chưa tạo thành Bạo Kích, sẽ khiến Tỷ Lệ Bạo Kích của bản thân tăng 36%/42%/48%/54%/60%, duy trì 1 hiệp. Hiệu ứng này mỗi 3 hiệp có thể kích hoạt 1 lần.'),
(97, 'Trước Buổi Bình Minh', 7, 1058, 0, 463, '5', '\"Tướng quân, chuyện này xin hãy cẩn trọng... sử sách sau này sẽ đánh giá ngài như thế nào...\"\r\n\r\nTướng quân khép hờ mắt lại, lắng nghe thuộc hạ bẩm báo.\r\n\r\n\"Lịch sử tự khắc sẽ biết phán đoán, nhưng ta không hề có hứng thú với sự kết luận của lịch sử.\"\r\n\r\n\"Nếu thành công, thì mọi chuyện đúng như ta đã dự tính, vô cùng hài lòng.\"\r\n\r\n\"Nếu thất bại thì xem như ta quá rảnh rỗi, mê muội mất cả ý chí.\"\r\n\r\nMột con chim tước nhảy xuống bờ vai của anh, anh bèn giơ tay lên đón lấy.\r\n\r\n\"Ta chỉ làm theo phán đoán của ta mà thôi.\"', 'uploads/lightcones/Trước Buổi Bình Minh.jpg', 'Tăng 36%/42%/48%/54%/60% Sát Thương Bạo Kích của người trang bị. Tăng 18%/21%/34%/27%/30% sát thương do Chiến Kỹ và Tuyệt Kỹ của người trang bị gây ra. Sau khi người trang bị thi triển Chiến Kỹ hoặc Tuyệt Kỹ, sẽ nhận hiệu ứng \"Thân Mộng\". Khi thi triển Đòn Đánh Theo Sau, tiêu hao \"Thân Mộng\", tăng 48%/56%/64%/72%/80% sát thương do Đòn Đánh Theo Sau gây ra.'),
(98, 'Giây Phút Thắng Lợi', 1, 1058, 476, 595, '5', '\"Thua rồi muốn khóc thì cứ khóc đi, Gepard.\"\r\n\r\n\"Nhưng có khóc cũng phải đứng dậy, cho đến khi thắng được chị... cho dù cần đến năm năm hay mười năm.\"\r\n\r\nVài năm sau, cậu bé ấy không còn khóc nhè nữa, cũng không còn thua chị mình nữa.\r\n\r\nTrên chiến trường ngày nay, cho dù ở bất kỳ tâm thái nào, Đội Trưởng Thiết Vệ Bờm Bạc dũng mãnh can trường cũng luôn cố thủ đến giờ phút cuối cùng\r\n\r\n——Chỉ để chớp lấy thời khắc tiêu diệt kẻ địch một cách triệt để.', 'uploads/lightcones/Giây Phút Thắng Lợi.jpg', 'Tăng 24%/38%/32%/36%/40% Phòng Thủ của người trang bị, tăng 24%/28%/32%/36%/40% Chính Xác Hiệu Ứng, đồng thời cũng tăng xác suất bản thân bị tấn công. Sau khi người trang bị bị tấn công, Phòng Thủ sẽ tăng thêm 24%/28%/32%/36%/40%, duy trì đến khi kết thúc hiệp của mình.'),
(99, 'Nhân Danh Thế Giới', 4, 1058, 582, 463, '5', '\"Welt... sao...\"\r\n\r\n\"Cái tên này... hay lắm sao?\"\r\n\r\n\"Vậy thì...\"\r\n\r\n\"Từ bây giờ, anh chính là Welt.\"\r\n\r\n\"Cốt lõi của luật giả... và cả sứ mệnh bảo vệ thế giới này...\"\r\n\r\n\"Đều trông cậy vào anh rồi, Welt ạ.\"', 'uploads/lightcones/Nhân Danh Thế Giới.jpg', 'Khiến sát thương mà người trang bị gây cho kẻ địch đang trong Hiệu Ứng Xấu tăng 24%/28%/32%/36%/40%. Khi người trang bị thi triển Chiến Kỹ, Chính Xác Hiệu Ứng của lần tấn công này của người trang bị sẽ tăng 18%/21%/24%/27%/30%, Tấn Công tăng 24%/28%/33%/36%/40%.'),
(100, 'Cuộc Chiến Chưa Nguôi', 3, 1164, 529, 463, '5', '\"Hãy nhớ, đấu với bọn quái vật đó, không thể chiến thắng bằng cách lùi bước.\"\r\n\r\n\"Phía sau của chúng ta chính là Belobog, chúng ta chính là phòng tuyến sắt thép cuối cùng...\"\r\n\r\n\"Tuyệt đối không được lùi bước!\"\r\n\r\nCô ấy ngẩng đầu lên, phía trước là ngày đông lạnh lẽo, là mặt đất lạnh băng, là bão tuyết mênh mông vô tận.\r\n\r\nMọi thứ đều như thường lệ.\r\n\r\nDuy chỉ có bóng lưng mà cô ấy luôn dõi theo, là không còn nữa.', 'uploads/lightcones/Cuộc Chiến Chưa Nguôi.jpg', 'Tăng 10%/12%/14%/16%/18% hiệu suất hồi Năng Lượng của người trang bị, đồng thời hồi 1 điểm Chiến Kỹ cho mục tiêu đang thi triển Tuyệt Kỹ của phe ta. Mỗi khi thi triển 2 lần Tuyệt Kỹ sẽ kích hoạt hiệu ứng này 1 lần. Sau khi người trang bị thi triển Chiến Kỹ sẽ khiến sát thương gây ra bởi mục tiêu khác của phe ta trong hành động tiếp theo tăng 30%/35%/40%/45%/50%, duy trì 1 hiệp.'),
(101, 'Vật Không Thể Thay Thế', 2, 1164, 582, 396, '5', '\"Svarog, nếu một ngày nào đó tôi không còn nữa, thì kho dữ liệu này phải nhờ đến anh rồi.\"\r\n\r\n(Là ai... vì sao trong ký ức của tôi lại có lời nói này...)\r\n\r\n\"Svarog, anh có thứ gì không thể thay thế không?\"\r\n\r\n\"Không thể thay thế... linh kiện bị hỏng... chỉ cần thay quách đi là xong...)\r\n\r\n\"Svarog, sẽ có một ngày anh tìm được.\"\r\n\r\n(Thứ đó... chỉ là một rắc rối...)\r\n\r\n\"Đến lúc đó... chắc anh sẽ rời khỏi đây.\"', 'uploads/lightcones/Vật Không Thể Thay Thế.jpg', 'Tăng 24%/28%/32%/36%/40% Tấn Công của người trang bị. Sau khi người trang bị tiêu diệt kẻ địch hoặc bị tấn công sẽ hồi ngay HP bằng 8%/9%/10%/11%/12% Tấn Công của người trang bị, đồng thời sát thương gây ra tăng 24%/28%/32%/36%/40%, duy trì đến hết hiệp sau của bản thân. Hiệu ứng này không thể cộng dồn, mỗi hiệp chỉ kích hoạt 1 lần.'),
(102, 'Trong Đêm Tối', 6, 1058, 582, 463, '5', 'Người thiếu nữ để lộ một nụ cười nhẹ đến nỗi không dễ gì phát hiện.\r\n\r\n\"Vì sao vậy?\"\r\n\r\n\"Giờ này phút này nơi này, rõ ràng chỉ có một mình ta...\"\r\n\r\n\"Nhưng lại cảm thấy... vô cùng náo nhiệt.\"', 'uploads/lightcones/Trong Đêm Tối.jpg', 'Tăng 18%/21%/24%/27%/30% Tỷ Lệ Bạo Kích của người trang bị. Khi Tốc Độ trong chiến đấu của người trang bị đạt 100 trở lên, mỗi khi vượt trên 10 điểm sẽ tăng 6%/7%/8%/9%/10% sát thương Tấn Công Thường và Chiến Kỹ, đồng thời tăng 12%/14%/16%/18%/20% Sát Thương Bạo Kích của Tuyệt Kỹ, hiệu ứng này có thể cộng dồn 6 lần.'),
(103, 'Chuyến Tàu Đêm Trên Dải Ngân Hà', 7, 1164, 582, 396, '5', 'Nếu vẫn còn lo lắng thì chỉ cần ngước đầu nhìn lên một lần nữa,\r\n\r\nkhi chòm sao dày đặc nhìn chăm chú dịu dàng, thì trái tim đã cưỡi gió bay lên.\r\n\r\nTừng nỗi nhớ, từng giọng nói thì thầm,\r\n\r\nđều được truyền tải trong sinh mệnh, sẽ không còn mất đi nữa.', 'uploads/lightcones/Chuyến Tàu Đêm Trên Dải Ngân Hà.jpg', 'Trong trận mỗi lần có 1 kẻ địch, sẽ khiến Tấn Công của người trang bị tăng 9%/10,5%/12%/13,5%/15%, hiệu ứng này tối đa cộng dồn 5 lần. Khi có kẻ địch bị phá vỡ Điểm Yếu, sát thương của người trang bị gây ra sẽ tăng 30%/35%/40%/45%/50%, duy trì 1 hiệp.'),
(104, 'Hôm Nay Là Ngày Yên Lành', 7, 846, 529, 330, '4', 'Vừa tìm ra một cách chơi bài mới, mượn một quyển sách vào buổi trưa.\r\n\r\nBấm đốt ngón tay tính thử, những công việc còn lại có thể từ từ làm, vừa khéo kịp giờ tan ca.\r\n\r\nCô ấy thong dong điềm tĩnh, trốn vào một góc.\r\n\r\nThời gian nhàn nhã tươi đẹp này, nhất quyết không thể để bất kỳ ai phá vỡ.\r\n\r\n- Cho dù là Nhà Chiêm Tinh Fu cũng không thể.\r\n\r\nChỉ là lỡ như, nếu có lỡ như...\r\n\r\n\"Thì sẽ cho Nhà Chiêm Tinh Fu cái Bánh Trôi Sữa Ngọt này. Ăn đồ ngọt, sẽ xua tan cơn giận!\"', 'uploads/lightcones/Hôm Nay Là Ngày Yên Lành.jpg', 'Sau khi vào chiến đấu, dựa theo giới hạn Năng Lượng của người trang bị để tăng sát thương mà người trang bị gây ra: Mỗi điểm Năng Lượng tăng 0,2%/0,25%/0,3%/0,35%/0,4%, tối đa tính 160 điểm.'),
(105, 'Không Nơi Ẩn Náu', 2, 952, 529, 264, '4', 'Anh ta không phải lần đầu gặp người đàn ông này.\r\n\r\nKhông biết bắt đầu từ bao giờ, người đàn ông giống như một cái bóng đi theo anh ta.\r\n\r\nCho dù dùng giáo dài đâm xuyên bao nhiêu lần, cũng sẽ xuất hiện lại.\r\n\r\nAnh ta không thua, nhưng cũng không thắng được.\r\n\r\nAnh ta muốn chạy trốn, nhưng cũng không có nơi để chạy.', 'uploads/lightcones/Không Nơi Ẩn Náu.jpg', 'Tăng 24%/30%/36%/42%/48% Tấn Công của người trang bị. Khi người trang bị tiêu diệt kẻ địch sẽ hồi một lượng HP bằng 12%/15%/18%/21%/24% Tấn Công của bản thân.'),
(106, 'Khắc Lên Ánh Trăng', 3, 952, 476, 330, '4', 'Anh ta vừa nhìn đã thấy vừa ý món đồ bằng vàng đó, đang muốn mua thì thiếu nữ Người Hồ Ly kéo anh vào một góc, nói nhỏ:\r\n\r\n\"Mời ân công quay lại vào buổi tối, lúc đố quyết định vẫn chưa muộn.\"\r\n\r\nBuổi tối anh ta đến đúng hẹn.\r\n\r\nNgười mở cửa lại là thiếu nữ Người Hồ Ly đó, nở nụ cười dịu dàng.\r\n\r\n\"Mời ân công theo tôi.\"\r\n\r\nChỉ nhìn thấy ánh trăng chiếu vào đáy chậu chạm hoa.\r\n\r\nThợ thủ công đã dùng độ nông sâu của nét vẽ phác họa nên độ nông sâu của cánh hoa, nếu không phải dưới ánh trăng sáng thì sẽ không thể thấy rõ từng nét đến như thế.\r\n\r\nLúc này anh ta mới nghĩ ra vì sao vật này lại được gọi là \"Khắc Lên Ánh Trăng\".', 'uploads/lightcones/Khắc Lên Ánh Trăng.jpg', 'Khi bắt đầu chiến đấu và khi người trang bị bắt đầu hiệp, sẽ có 1 hiệu ứng ngẫu nhiên phát huy hiệu lực. Hiệu ứng này khi phát huy hiệu lực, sẽ thay thế hiệu ứng của lần trước và không trùng với lần trước. Hiệu ứng bao gồm: Tăng 10%/12.5%/15%/17.5%/20% Tấn Công của toàn phe ta; Tăng 12%/15%/18%/21%/24% Sát Thương Bạo Kích của toàn phe ta; Tăng 6%/7,5%/9%/10,5%/12% hiệu suất Hồi Năng Lượng của toàn phe ta. Hiệu ứng cùng loại không thể cộng dồn, khi người trang bị rơi vào trạng thái không thể chiến đấu sẽ giải trừ.'),
(107, 'U Tối Trở Lại', 6, 846, 529, 330, '4', 'Trong chiếc hòm đen kịt một màu, cô ấy có thể nghe những gì mà chị nghe thấy.\r\n\r\nGiờ đây đã ở bên cạnh chị, nhưng cô ấy không nghe thấy gì nữa.\r\n\r\nChỉ có âm thanh trong lòng mỗi lúc một vang vọng.\r\n\r\n\"Thân thể của chị lạnh lẽo.\"\r\n\r\n\"Thân thể của chị không cử động nữa.\"\r\n\r\nDòng nước phẫn nộ đã nhấn chìm chị, không được chùn bước. Cô ấy đã không kịp nhớ ra.\r\n\r\n\"Thân thể của chị, đã lạnh lẽo từ rất lâu rồi.\"', 'uploads/lightcones/U Tối Trở Lại.jpg', 'Khiến Tỷ Lệ Bạo Kích của người trang bị tăng 12%/15%/18%/21%/24%. Sau khi bạo kích có 16%/20%/24%/28%/32% xác suất cố định giải trừ 1 buff hiện có của kẻ địch bị tấn công, hiệu ứng này mỗi lần tấn công chỉ có thể kích hoạt 1 lần.'),
(108, 'Đó Chính Là Tôi!', 1, 846, 370, 529, '4', 'Thiếu nữ mặc trang phục gì trông cũng rất đáng yêu, nhưng cô ấy dường như vẫn không vừa ý, đống quần áo chất chồng ở phía sau không dưới mười mấy bộ.\r\n\r\n\"Hôm nay sẽ là lần cuối cùng sao?\"\r\n\r\nCô ấy lặng lẽ suy nghĩ, rồi ngước nhìn thiếu nữ trong gương, hôm nay trông cô ấy vui hơn mọi ngày.\r\n\r\n\"Hoàn hảo! Đây chính là tôi đấy!\"', 'uploads/lightcones/Đó Chính Là Tôi!.jpg', '\r\nKhiến Phòng Thủ của người trang bị tăng 16%/20%/24%/28%/32%. Tăng sát thương gây ra khi người trang bị thi triển Tuyệt Kỹ, trị số tăng tương đương 60%/75%/90%/105%/120% Phòng Thủ của người trang bị, hiệu ứng này chỉ có hiệu lực 1 lần lên mỗi kẻ địch.'),
(109, 'Hẹn Gặp Lại', 4, 846, 529, 330, '4', 'Cô ấy từng hoài nghi rằng, đó là một loại chương trình nào đó, nếu không thì sao có thể không biết mệt chứ.\r\n\r\nNhưng chương trình sẽ không chủ động vứt bỏ những phương án tối ưu, tránh được những cái bẫy do cô ấy tạo ra.\r\n\r\n\"Xem ra chúng ta phải đối mặt với một trận chiến trường kỳ rồi.\"\r\n\r\nCô ấy ngáp dài.\r\n\r\n\"Nên ngủ sớm thôi, sinh mạng hữu cơ nên biết quý trọng mộng cảnh.\"\r\n\r\nBốn bề vẫn cứ tối mịt.\r\n\r\nCô ấy đột nhiên tỉnh lại, nhưng phần còn lại của dữ liệu đã bị quét mất, tiêu hủy, giống như một giấc mộng.\r\n\r\n——Ngoại trừ sự nếm trải thất bại của buổi ban đầu ấy lại chân thật vô cùng.', 'uploads/lightcones/Hẹn Gặp Lại.jpg', 'Sau khi người trang bị thi triển Tấn Công Thường hoặc Chiến Kỹ, sẽ gây cho 1 kẻ địch ngẫu nhiên bị đánh trúng một lượng sát thương kèm theo bằng 48%/60%/72%/84%/96% Tấn Công của bản thân.'),
(110, 'Đêm Ấm Sẽ Chóng Qua', 8, 1058, 370, 396, '4', 'Thắp một ngọn đèn, bê một ly sô cô la ấm nóng, nhìn những người bạn xung quanh mình.\r\n\r\nNhìn cô ấy đọc sách và bút ký do mình tặng.\r\n\r\nNhìn cô ấy lộ rõ nụ cười mà ngày thường không thể tìm thấy.\r\n\r\nTrong đêm giá lạnh, niềm hạnh phúc nhỏ nhoi có thể xua tan giá rét.', 'uploads/lightcones/Đêm Ấm Sẽ Chóng Qua.jpg', 'Khiến Giới Hạn HP của người trang bị tăng 16%/20%/24%/28%/32%. Sau khi thi triển Tấn Công Thường hoặc Chiến Kỹ, sẽ hồi cho toàn phe ta một lượng HP bằng 2%/2,5%/3%/3,5%/4% Giới Hạn HP của mỗi người.'),
(111, 'Bữa Sáng Đúng Điệu', 7, 846, 476, 396, '4', 'Mặc dù bàn ăn đã được bày biện xong...\r\n\r\nCó người muốn uống một ly hồng trà trước.\r\n\r\nCó người muốn chụp một tấm hình trước.\r\n\r\nCó người muốn đọc một tờ báo trước.\r\n\r\nCó người muốn ghi một trang nhật ký trước.\r\n\r\nCó người lại muốn lướt điện thoại một chút.\r\n\r\n\"Mau ăn lúc thức ăn còn nóng đi nào!\"', 'uploads/lightcones/Bữa Sáng Đúng Điệu.jpg', 'Khiến sát thương gây ra bởi người trang bị tăng 12%/15%/18%/21%/24%. Mỗi khi tiêu diệt 1 kẻ địch, Tấn Công của người trang bị tăng 4%/5%/6%/7%/8%, tối đa cộng dồn 3 tầng.'),
(112, 'Gâu! Thời Gian Tản Bộ', 2, 952, 476, 330, '4', 'Tiếc rằng không ai nhìn thấy...\r\n\r\nNhìn thấy nó hít hít mũi, ngửi ngửi khắp nơi, chui vào con đường nhỏ không người.\r\n\r\nTiếc rằng không ai nhìn thấy...\r\n\r\nNhìn thấy nó co vuốt lại, nhấn mật mã, gửi gắm vào hoài bão tự do.\r\n\r\nVẫn chưa ai nhìn thấy sao...\r\n\r\nMột chú chó trưởng thành, đã học được cách tản bộ trong không trung từ sớm!', 'uploads/lightcones/Gâu! Thời Gian Tản Bộ.jpg', 'Tăng 10%/12,5%/15%/17,5%/20% Tấn Công của người trang bị, sát thương gây ra cho mục tiêu ở trạng thái Thiêu Đốt hoặc Chảy Máu tăng 16%/20%/24%/28%/32%. Hiệu ứng này cũng có hiệu lực với sát thương duy trì.'),
(113, 'Đừng Để Thế Giới Lạnh Lẽo', 7, 846, 476, 396, '4', 'Cô ấy đứng trên sân khấu vẫy vẫy tay, kêu gọi mọi người hòa nhịp cùng cô ấy.\r\n\r\nCho dù là hát, la hét, than thở hay thế nào cũng được.\r\n\r\nCứ phát ra âm thanh của mình, cho đến khi có người nghe thấy thì mới có người gia nhập.\r\n\r\n\"Đừng để tâm hồn mình lạnh lẽo như thành phố này nữa.\"\r\n\r\nĐừng để thế giới này lạnh lẽo thêm nữa.', 'uploads/lightcones/Đừng Để Thế Giới Lạnh Lẽo.jpg', 'Khiến người trang bị khi vào chiến đấu sẽ hồi ngay 20/23/26/29/32 điểm Năng Lượng, đồng thời khiến sát thương Tuyệt Kỹ mà người đó gây ra tăng 32%/40%/48%/56%/64%.'),
(114, 'Lời Thề Bí Mật', 2, 1058, 476, 264, '4', 'Trước mỗi lần xuất phát, cậu bé đều kiểm tra đồ bảo hộ và găng tay của mình một cách cẩn thận.\r\n\r\nĐó là đôi găng tay yêu thích của cậu ấy, nó đủ kín kẽ để che giấu nhiều bí mật.\r\n\r\nNhư vậy thì sẽ không ai trông thấy được dải băng trên tay cậu ấy, và cả vết thương dưới lớp băng ấy.\r\n\r\nMọi người sẽ đối xử với cậu như một người bình thường.\r\n\r\nNhư vậy là đủ.', 'uploads/lightcones/Lời Thề Bí Mật.jpg', 'Tăng 20%/25%/30%/35%/40% sát thương người trang bị gây ra, đồng thời tăng thêm 20%/25%/30%/35%/40% sát thương gây ra cho kẻ địch có phần trăm HP hiện tại cao hơn phần trăm HP hiện tại của người trang bị.'),
(115, 'Điểm Hẹn Hành Tinh', 3, 1058, 423, 330, '4', 'Khi lên đường, cô ấy vẫn không biết gì về chuyến đi xa này.\r\n\r\nChỉ biết cuối cùng mình cũng có thể tạm biệt những lời ra tiếng vào trong nhà, tránh được những lời đả kích ở khắp mọi nơi.\r\n\r\nCho đến khi cô nhìn ra ngoài cửa sổ, và rồi đã hiểu ra ý nghĩa của bài thơ ấy.\r\n\r\n\"Một giây phút nào đó, chúng ta không còn đi nữa mà sẽ bay.\"\r\n\r\nVà ánh sáng vụt qua trong đôi mắt cô chính là hình ảnh phản chiếu của các hành tinh.', 'uploads/lightcones/Điểm Hẹn Hành Tinh.jpg', 'Sau khi vào chiến đấu, khi mục tiêu phe ta gây sát thương cùng thuộc tính với người trang bị, sẽ khiến sát thương gây ra tăng 12%/15%/18%/21%/24%.'),
(116, 'Luận Kiếm', 6, 952, 476, 330, '4', 'Người thiếu nữ vung thanh kiếm mỏng trong tay.\r\n\r\nPhải dùng cả hai tay mới có thể vung được kiếm của mình, kiếm như vậy cũng tính là kiếm sao? Kiếm như vậy cũng có thể giết địch sao?\r\n\r\nChủ nhân của thanh kiếm chẳng hề để tâm đến điều này, vì dẫu sao...\r\n\r\nCó giết địch hay không không phải do thanh kiếm quyết định, mà do lòng người.\r\n\r\nDùng trọng kiếm hay kiếm mỏng thì có gì khác biệt chứ!', 'uploads/lightcones/Luận Kiếm.jpg', 'Khi người trang bị đánh trúng cùng một kẻ địch nhiều lần, sát thương gây ra mỗi lần sẽ tăng 8%/10%/12%/14%/16%, hiệu quả này tối đa cộng dồn 5 tầng. Nếu mục tiêu tấn công có thay đổi, sẽ xóa ngay hiệu ứng buff hiện tại.'),
(117, 'Sự Lựa Chọn Của Landau', 1, 952, 423, 396, '4', '\"Có nghe chưa, ngày mai không được đi nữa, cứ đi chung với mấy người thường dân đó thì học được cái gì hay ho đâu chứ!\"\r\n\r\n\"Nhưng những câu chuyện về Belobog mà họ kể đều rất mới lạ, chưa từng được nghe qua...\"\r\n\r\nCô ấy kéo em trai vẫn còn muốn thanh minh về phía sau lưng mình.\r\n\r\n\"Các người là thành viên tộc Landau. Đặc biệt là Serval, rồi cũng sẽ đến lúc phải lựa chọn thôi.\"\r\n\r\nCô ấy biết em mình không làm gì sai, nhưng cô không biết phải phản bác như thế nào.\r\n\r\nSau khi khó chịu cả ngày trời, cô ấy âm thầm quyết định.\r\n\r\n\"Tôi sẽ trưởng thành, sẽ có một ngày tôi cũng có thể đưa ra...\"\r\n\r\n\"Sự lựa chọn của riêng mình.\"', 'uploads/lightcones/Sự Lựa Chọn Của Landau.jpg', 'Tăng xác suất bị tấn công của người trang bị, đồng thời sát thương phải chịu sẽ giảm 16%/18%/20%/22%/24%.'),
(118, 'Tầm Mắt Của Con Mồi', 4, 952, 476, 330, '4', 'Trên thực tế thì hầu hết các con mồi đều sẽ không ý thức được hoàn cảnh của chính mình.\r\n\r\nCon mồi trước mắt cũng vậy.\r\n\r\nỐng ngắm từ từ xoay chuyển, người đàn ông vẫn đang tung tẩy từng xấp tiền trên tay.\r\n\r\n\"Trông bộ dạng tham lam của anh ta kìa, số tiền này đủ để tôi làm vài cuộc làm ăn... chả trách có người muốn xử anh ta.\"\r\n\r\nGiống như nghe được lời cảm khái trong lòng của anh ấy, người đàn ông quay đầu lại.\r\n\r\n\"Chờ anh đã lâu, chút tiền này có đủ để anh nghe thử điều kiện của tôi rồi mới quyết định không?\"', 'uploads/lightcones/Tầm Mắt Của Con Mồi.jpg', 'Tăng 20%/25%/30%/35%/40% Chính Xác Hiệu Ứng cho người trang bị, đồng thời sát thương duy trì gây ra sẽ tăng 24%/30%/36%/42%/48%.'),
(119, 'Cùng Một Tâm Trạng', 8, 1058, 370, 396, '4', 'Khi người bác sĩ tìm thấy cô gái, cô ấy đã bị thương, nhưng vẫn ôm chặt cỗ máy đã dừng hoạt động, trong túi chất đầy các loại linh kiện bị lục tung lên.\r\n\r\nCô ấy vội vàng đưa cô gái ấy về phòng khám, bó thuốc lên vết thương.\r\n\r\nMồ hôi lăn dài trên gò má, tuy cô gái không nói lời nào, nhưng tay vẫn không dừng lại.\r\n\r\nBác sĩ biết, trong mắt cô ấy lúc này chỉ có cỗ máy đó, giống như bản thân đang đối mặt với bệnh nhân đang chờ chữa trị.\r\n\r\nVì vậy mà cô không hề lên tiếng an ủi, cũng chẳng nhắc nhở phải cẩn thận sau này, cô chỉ ngồi cạnh cô gái ấy, lặng lẽ ở bên cô ấy.', 'uploads/lightcones/Cùng Một Tâm Trạng.jpg', 'Tăng 10%/12,5%/15%/17,5%/20% lượng trị liệu của người trang bị, đồng thời khi thi triển Chiến Kỹ, toàn bộ phe ta sẽ hồi 2/3/3/4/4 điểm Năng Lượng.'),
(120, 'Sự Ra Đời Của \"Tôi\"', 7, 952, 476, 330, '4', 'Lúc đầu nó giống hệt với tư thế trong bức chân dung trước đó.\r\n\r\nCô ấy nhìn gương mặt giống mình y như đúc này, chắc chắn rằng đây không phải là cô ấy.\r\n\r\n\"Trên đời này sao lại có thể có hai bức chân dung giống hệt nhau như vậy chứ?\"\r\n\r\nCô ấy đã chỉnh lại các khớp của nó, dạy nó mở mắt, dạy nó cử động ngón tay linh hoạt hơn.\r\n\r\nCô ấy dừng tay một cách rất hài lòng.\r\n\r\n\"Khác hẳn so với hôm qua, đây mới là tôi của ngày hôm nay.\"', 'uploads/lightcones/Sự Ra Đời Của Tôi.jpg', 'Tăng 24%/30%/36%/42%/48% sát thương gây ra bởi đòn Đòn Đánh Theo Sau của người trang bị. Nếu Phần Trăm HP hiện tại của kẻ địch này nhỏ hơn hoặc bằng 50%, thì sát thương của đòn Đòn Đánh Theo Sau sẽ tăng thêm 24%/30%/36%/42%/48%.'),
(121, 'Băng Chuột Chũi Hoan Nghênh', 2, 1058, 476, 264, '4', '\"Băng Chuột Chũi, Băng Chuột Chũi\"\r\n\r\n\"Chúng ta sắp xuất phát\"\r\n\r\n\"Chuột chũi thông minh không sợ không sợ\"\r\n\r\n\"Băng Chuột Chũi, Băng Chuột Chũi\"\r\n\r\n\"Băng Chuột Chũi bắt đầu thám hiểm!\"', 'uploads/lightcones/Băng Chuột Chũi Hoan Nghênh.jpg', 'Sau khi người trang bị thi triển Tấn Công Thường, Chiến Kỹ hoặc Tuyệt Kỹ lên kẻ địch, sẽ lần lượt nhận 1 tầng \"Trêu Chọc\". Mỗi tầng khiến Tấn Công của người trang bị tăng 12%/15%/18%/21%/24%.'),
(122, 'Dáng Hình Trong Ký Ức', 3, 952, 423, 396, '4', 'Một tấm ảnh cũ được bày trên chiếc bàn của Đấng Bảo Vệ, ở một góc xa xa.\r\n\r\nKhi ánh mắt xa xăm vô tình rơi lên đó, cô ấy luôn tìm cách lảng tránh.\r\n\r\nDáng hình trong ký ức cho dù đã đi xa, nhưng giấc mộng hôm nay vẫn luôn quanh quẩn.', 'uploads/lightcones/Dáng Hình Trong Ký Ức.jpg', 'Khiến Tấn Công Kích Phá của người trang bị tăng 28%/35%/42%/49%/56%. Sau khi người trang bị tấn công sẽ hồi thêm 4/5/6/7/8 điểm Năng Lượng, hiệu ứng này không thể kích hoạt trùng lặp trong 1 hiệp.'),
(123, 'Sự Trầm Mặc Duy Nhất', 6, 952, 476, 330, '4', '\"Đến trạm rồi, không xuống xe sao?\"\r\n\r\nMột tiếng cười nhẹ vang trên đỉnh đầu. Đôi mắt của anh hơi xao động nhưng không hề ngẩng đầu lên.\r\n\r\n\"March thường nói mặt anh vô cảm, nhưng tôi lại không cảm thấy thế.\"\r\n\r\n\"Dữ liệu của Kho Lưu Trữ trong đoàn tàu có cập nhật các vết tích mới, nhưng lại không lưu trữ kết quả.\"\r\n\r\n\"Vậy thì... mục nào còn làm khó được anh nhỉ?\"\r\n\r\nAnh siết chặt cây bút, nhìn vào cảnh tượng thê thảm trong quyển sổ chỉ được viết lại theo ấn tượng.\r\n\r\n\"Thì ra là vậy, là chuyện liên quan đến bản thân anh...\"', 'uploads/lightcones/Sự Trầm Mặc Duy Nhất.jpg', 'Tấn Công của người trang bị tăng 16%/20%/24%/28%/32%. Khi trong trận có 2 kẻ địch trở xuống, Tỷ Lệ Bạo Kích của người trang bị tăng 12%/15%/18%/21%/24%.'),
(124, 'Ngày Đầu Tiên', 1, 952, 463, 370, '4', '\"Hôm nay Pom-Pom đã thay áo mới, vui vui... thích thích~\"\r\n\r\n\"Hôm nay sinh nhật nè, năm nay tôi vẫn rất dễ thương.\"\r\n\r\n\"Hôm nay rốt cuộc tôi cũng có hậu bối rồi! Có phải không?\"\r\n\r\nNhấn nút chụp liên tục hết lần này đến lần khác mà không cần suy nghĩ.\r\n\r\nLà để bắt lấy khoảnh khắc vui vẻ?\r\n\r\nHay là lưu giữ khoảnh khắc của bản thân lúc đó?', 'uploads/lightcones/Ngày Đầu Tiên.jpg', 'Phòng Thủ của người trang bị tăng 16%/18%/20%/22%/24%. Sau khi vào chiến đấu, Kháng Sát Thương của toàn phe ta tăng 8%/9%/10%/11%/12%. Kỹ năng cùng loại sẽ không có hiệu lực trùng lặp.'),
(125, 'Chúc Ngủ Ngon', 4, 952, 476, 330, '4', 'Ánh đèn tĩnh lặng tô điểm nên một vệt sáng bụi mờ,\r\n\r\nnhịp thở đều đặn mang theo lời nói mơ ngọt ngào của người thiếu nữ.\r\n\r\nMột bóng người xuất hiện phía sau cô ấy, im lặng không nói gì.\r\n\r\n\"Chậc chậc chậc, đúng là tháo mắt kính xuống thì dễ thương hơn mà.\"\r\n\r\nTay ghi-ta chăm chú quan sát gương mặt say ngủ của cô ấy rồi tự lẩm bẩm:\r\n\r\n\"Ngủ ngon, thiếu nữ thiên tài hay lo nghĩ.\"', 'uploads/lightcones/Chúc Ngủ Ngon.jpg', 'Mỗi khi kẻ địch nhận phải 1 Hiệu Ứng Xấu, thì sát thương người trang bị gây ra cho kẻ đó sẽ tăng 12%/15%/18%/21%/24%, tối đa cộng dồn 3 tầng. Hiệu ứng này cũng có hiệu lực với sát thương duy trì.'),
(126, 'Buổi Trò Chuyện Hậu Phẫu', 8, 1058, 423, 330, '4', '\"Tỉnh rồi à... cám ơn.\"\r\n\r\n\"Cô... cô đã cứu tôi... Natasha. Người nên nói lời cám ơn... là tôi mới phải.\"\r\n\r\n\"Không, tôi mới phải nói lời cám ơn... cám ơn vì đã kiên trì đến khi tôi hoàn thành ca phẫu thuật.\"\r\n\r\nBác sĩ và bệnh nhân mệt mỏi nhìn nhau cười, rồi không nói gì nữa.', 'uploads/lightcones/Buổi Trò Chuyện Hậu Phẫu.jpg', 'Khiến hiệu suất Hồi Năng Lượng của người trang bị tăng 8%/10%/12%/14%/16%, đồng thời lượng trị liệu khi thi triển Tuyệt Kỹ tăng 12%/15%/18%/21%/24%.');

-- --------------------------------------------------------

--
-- Cấu trúc bảng cho bảng `materials`
--

CREATE TABLE `materials` (
  `id` int(11) NOT NULL,
  `name` varchar(100) NOT NULL,
  `rarity` enum('1','2','3','4','5') DEFAULT NULL COMMENT 'Độ hiếm từ 1-5 sao',
  `type` varchar(100) DEFAULT NULL COMMENT 'Phân loại vật phẩm',
  `icon` varchar(255) DEFAULT NULL,
  `description` text DEFAULT NULL,
  `content` text DEFAULT NULL COMMENT 'Nội dung chi tiết về vật phẩm',
  `obtain` text DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `materials`
--

INSERT INTO `materials` (`id`, `name`, `rarity`, `type`, `icon`, `description`, `content`, `obtain`) VALUES
(1, 'Mắt Rơi Đầy Trời', '5', 'Vật Tiêu Hao', 'uploads/materials/Mắt Rơi Đầy Trời.jpg', 'Đạo Cụ Tìm Kho Báu có thể dùng vô số lần, sau khi dùng có thể hiển thị vị trí của 1 Chiến Lợi Phẩm Thường trong bản đồ liên quan đến Amphoreus.', 'Ban đầu, Aquila không chỉ có trăm mắt, mà là có vô số như sao trời. Đáng tiếc, Zagreus có hứng thú với con mắt của Aquila, nên đã lấy trộm đi rất nhiều. Những con mắt được Zagreus ban phúc này có khả năng truy tìm kho báu, tuyệt đối không được sử dụng chúng vào ban ngày, nếu không có thể sẽ thu hút cơn sét thịnh nộ của Aquila.\r\n\r\n\"Trời cao có mắt, nhìn xuống thế gian. Nhưng muốn nhìn thấu quỷ kế, thì có nhìn nhiều đi nữa cũng vô ích.\"', 'Phần thưởng cấp \"Quà Tặng Thủy Triều\"'),
(2, 'Cuộn Dệt Vàng Tiên Tri \"Aedes Elysiae\"', '3', 'Đạo Cụ Nhiệm Vụ', 'uploads/materials/Cuộn Dệt Vàng Tiên Tri.jpg', 'Cuộn sách ghi chép lại lời tiên tri. Quan sát kỹ mới phát hiện, bìa sách được dệt bằng tơ vàng.\r\n\r\n\"Trong này có một lời tiên tri, nó chỉ hiện ra trước mắt người mở cuộn sách.\"', 'Ở thời khắc cao trào của nhạc khúc,\r\n\r\nTiếng đàn rung động tâm hồn, giai điệu êm đềm như mật ngọt.\r\n\r\nVui đến nỗi làm cả thần linh của lễ hội cũng say mê, quay cuồng ba vòng quanh sân trong điệu múa điên dại, chẳng thể nào tìm được chỗ để ngồi xuống.\r\n\r\n\"Hãy dâng tặng chúng tôi một bài ca!\"\r\n\r\nYêu tinh tiếp đón thần linh, tràn đầy nhiệt huyết.\r\n\r\n\"Fa-si-fa-si. Làm lại si-si!\"\r\n\r\nTitan gõ chuông gió, vui vẻ đồng ý.', ''),
(3, 'Rượu Mật Hỗn Loạn', '3', 'Tiền Tệ Thế Giới', 'uploads/materials/Rượu Mật Hỗn Loạn.jpg', 'Chất lỏng tồn tại ngoài trật tự thời gian, hãy ném nó vào Thủy Triều Trong Bể của Tâm Vòng Xoáy Sáng Thế đi.', 'Món ngon từng được biết đến là \"Rượu Mật Thần Thánh\", khi vòng luân hồi của Amphoreus bị vạch trần, cũng đã lộ ra diện mạo thật sự. Mùi thoát ra từ miệng chai có vẻ nồng đậm hơn trước, nhưng vẫn không nên tự uống.', 'Khám Phá Amphoreus\r\n\r\nNhiệm Vụ Khai Phá\r\n\r\nNhiệm Vụ Thám Hiểm'),
(4, '\"Quả Mọng Thời Gian\"', '3', 'Đạo Cụ Nhiệm Vụ', 'uploads/materials/Quả Mọng Thời Gian.jpg', 'Kho báu cuối cùng của cuộc mạo hiểm, biểu tượng của chương mở đầu truyền kỳ, sinh ra từ cấu trúc mới sau \"Tái Sáng Thế\".\r\n\r\nBị ảnh hưởng bởi sử thi anh hùng Amphoreus, nó được thể hiện dưới dạng một quả táo vàng.\r\n\r\n\"Cho năm tháng văn minh, cho thời gian sinh mệnh.\"', '', ''),
(5, 'Con Rối Vua Kỵ Sĩ Phương Xa', '3', 'Đạo Cụ Nhiệm Vụ', 'uploads/materials/Con Rối Vua Kỵ Sĩ Phương Xa.jpg', 'Thực thể ký ức bị bạn đưa khỏi mộng ảo, ở một vùng đất xa xôi nào đó, một người thợ ngưỡng mộ đức vua đã điêu khắc một con rối gỗ với lòng thành kính. Tuy nó dường như khác xa với vị vua thật sự, tay nghề cũng không lý tưởng lắm, nhưng những gì anh hùng mang đến cho người đời sau, chính là như vậy, cho dù là vụng về, cũng đủ để an ủi tâm hồn.\r\n\r\n\"Hơn nữa, lẽ nào bạn không cảm thấy, sau khi nhìn một lâu rồi, nó cũng rất có hồn sao?\"\r\n\r\n\"Tôi cảm thấy nó đang hút lấy linh hồn của tôi!\"', '', ''),
(6, 'Vinh Quang Phai Màu', '3', 'Đạo Cụ Nhiệm Vụ', 'uploads/materials/Vinh Quang Phai Màu.jpg', 'Một bức ảnh cũ nát, ghi lại quá khứ của \"Quý Cô Nốt Nhạc\". Tiếng gào oan khuất khi bị tống giam, tiếng ca hát vui vẻ khi phá vỡ lồng giam, tiếng vỗ tay như sấm dưới sân khấu, và cả những lời miệt thị do ô danh mang lại, âm thanh đều đã biến mất trong lịch sử, chỉ còn lại quá khứ đã ố vàng.\r\n\r\n\"Nhưng dù vậy, tôi vẫn muốn ca hát.\"', '', ''),
(7, '\"Binh Binh Bùm\"', '4', 'Ảnh Đại Diện', 'uploads/materials/Binh Binh Bùm.jpg', 'Thưởng ảnh đại diện sẽ nhận khi hoàn thành sự kiện Chim Giấy Đại Chiến: Bản Chính Thức.', 'Chim Giấy đang hừng hực khí thế.\r\n\r\n\"Chíp! Chíp chíp chíp!! (Đến xem thử xem ai lợi hại hơn nào chíp!). Chíp chíp! Chíp! (Quyết định rồi, là tôi đó chíp!)\"', 'Chim Giấy Đại Chiến: Bản Chính Thức'),
(8, 'Kho Tàng Biển Sao', '4', 'Vật Phẩm Quý Giá', 'uploads/materials/Kho Tàng Biển Sao.jpg', 'Bộ sưu tập quý giá trôi dạt trong biển sao vô tận, có thể tự chọn 1 Nón Ánh Sáng chỉ định.\r\nSử dụng để chọn 1 Nón Ánh Sáng chỉ định.', 'Những ký ức quá khứ của vô số sinh mệnh đều được lưu trữ trong những chiếc Nón Anh Sáng, chờ đợi Khách Vô Danh đến mở ra.', 'Mở khóa Vinh Quang Khách Vô Danh và tăng lên cấp 30 tại Phiên Bản 3.4 [Vì Mặt Trời Sắp Bị Phá Hủy]'),
(9, 'Biến Số Anh Hùng', '4', 'Không Rõ', 'uploads/materials/Biến Số Anh Hùng.jpg', '\"Biến số\" nhỏ bé của thế giới, sinh ra từ cấu trúc mới sau \"Tái Sáng Thế\".', 'Mỗi khi bạn nhìn chằm chằm vào những điểm sao bên trong, ký ức lại như thủy triều dâng trào vào trong tâm trí, như thể một phần năm tháng của Amphoreus được phong ấn bên trong.\r\n\r\nCó lẽ nó có thể giúp bạn, vào thời khắc nào đó bạn chưa đạt đến.\r\n\r\n\"Rốt cuộc là người viết truyền kỳ mới có thể trở thành anh hùng, hay là phải trở thành anh hùng mới viết được truyền kỳ?\"', ''),
(10, 'Dấu Mốc Tri Thức', '3', 'Đạo Cụ Nhiệm Vụ', 'uploads/materials/Dấu Mốc Tri Thức.jpg', NULL, '', ''),
(11, 'Aglaea - Thời Trang', '5', 'Ảnh Đại Diện', 'uploads/materials/Aglaea - Thời Trang.jpg', 'Thưởng ảnh đại diện sẽ nhận khi mở khoá Vinh Dự Vô Danh.', 'Aglaea được mời tham gia chụp ảnh cho tạp chí thời trang.\r\n\r\n\"Chất liệu và cách dệt của trang phục này đều rất độc đáo, tôi có thể cảm nhận được sự lãng mạn của nó.\"', 'Mở khoá Vinh Dự Vô Danh.'),
(12, 'Firefly - Du Lịch', '5', 'Ảnh Đại Diện', 'uploads/materials/Firefly - Du Lịch.jpg', 'Quà tặng sau khi nhận Thư Tay Ngày Xuân.', 'Khi hoa nở cũng là lúc thích hợp để cùng nhau du ngoạn.', 'Thư Tay Ngày Xuân'),
(13, 'Phainon - Thời Trang', '5', 'Ảnh Đại Diện', 'uploads/materials/Phainon - Thời Trang.jpg', 'Thưởng ảnh đại diện sẽ nhận khi mở khoá Vinh Dự Vô Danh.', 'Phainon được mời tham gia chụp ảnh cho tạp chí thời trang.\r\n\r\n\"Phong cách trang phục rất thời thượng... Đối với tôi mà nói đây cũng là một trải nghiệm rất mới mẻ.\"', 'Mở khoá Vinh Dự Vô Danh.'),
(14, 'Nụ Tàn Ánh Hoàng Hôn', '4', 'Nguyên Liệu Nâng Bậc Nhân Vật', 'uploads/materials/Nụ Tàn Ánh Hoàng Hôn.jpg', 'Lõi ăn mòn trên đầu của Hắc Nhật Sư Ưng, nguyên liệu tăng bậc của nhân vật thuộc tính Phong.', 'Chiếc vương miện tượng trưng cho sự rực rỡ của bầu trời đã bị sụp đổ thành một lõi ăn mòn hình nụ hoa, tạo vật của ánh sáng chìm vào bóng tối, nỗi căm thù mãnh liệt của sư ưng như ngọn lửa thiêu đốt chính bản thân mình.\r\n\r\n“Ánh sáng nuôi dưỡng sự sống, còn bóng tối lấy đi sinh mệnh.”', 'Hư Ảnh Ngưng Đọng - Con Mắt Ngày Và Đêm \"Pháo Đài Mái Vòm\"\r\n\r\nMáy Ghép Vạn Năng - Đổi Nguyên Liệu'),
(15, 'Đặt Tên Theo Gậy Bóng Chày', '4', 'Ảnh Đại Diện', 'uploads/materials/Đặt Tên Theo Gậy Bóng Chày.jpg', 'Ảnh đại diện nhận được khi hoàn thành sự kiện \"Huyền Thoại Hiệp Sĩ Gậy Bóng Chày Ngân Hà: Chương Ma Vương\"', '\"Hiệp Sĩ Gậy Bóng Chày\" tràn đầy tự tin.\r\n\r\n\"Vào lúc này, đúng thời khắc đó. Chương \"Huyền Thoại Hiệp Sĩ Gậy Bóng Chày Ngân Hà\", vẫn tiếp tục mở ra vì bạn!\"', 'Huyền Thoại Hiệp Sĩ Gậy Bóng Chày Ngân Hà: Chương Ma Vương'),
(16, 'Ngựa Của Janus', '4', 'Ảnh Đại Diện', 'uploads/materials/Ngựa Của Janus.jpg', 'Ảnh đại diện nhận được bằng cách đổi tại \"Ngựa Của Janus - Sau Khi Nhập Hàng\"', 'Ngựa Của Janus bất động, nhìn chằm chằm vào một nơi nào đó.\r\n\r\n\"Bạn tới rồi. Vẫn như trước, nếu muốn có được kho báu, thì phải dùng nhiều \"Ký Ức\" hơn nữa. À mà... bạn đang giẫm lên chân tôi đó.\"', 'Đổi tại \"Ngựa Của Janus - Sau Khi Nhập Hàng\"'),
(17, 'Mũ Giáp Phương Bắc', '2', 'Vật Tiêu Hao', 'uploads/materials/Mũ Giáp Phương Bắc.jpg', 'Sau khi dùng, toàn thể phe ta tăng 18% Khiên trong lần chiến đấu tiếp theo.', 'Mặt nạ từng thuộc về binh sĩ Aidonia, được làm từ bờm của Sói Đồng Tuyết nơi vùng địa cực. Hình dáng dữ tợn, kẻ địch sợ chết chỉ cần nhìn một cái đã mất hết ý chí chiến đấu.\r\n\r\nNhững chiến binh đội nó bước vào chiến trường nhẹ nhàng như cỏ dại, rồi tan biến như giọt sương mai hoà mình vào nước.\r\n\r\n\"Người sống sớm muộn cũng sánh vai cùng linh hồn đã khuất, cây cầu qua Sông Styx không phân biệt ai trước ai sau.\" - Châm ngôn Aidonia', 'Máy Ghép Vạn Năng'),
(18, 'Phô Mai Mật Ong Chiên Giòn', '2', 'Vật Tiêu Hao', 'uploads/materials/Phô Mai Mật Ong Chiên Giòn.jpg', 'Sau khi dùng lập tức hồi phục cho 1 đồng đội chỉ định Năng Lượng bằng 30% Giới Hạn Năng Lượng của bản thân.', 'Món ăn đắt đỏ được giới quý tộc và thường dân ưa chuộng, lớp vỏ ngoài được nướng giòn bằng mật ngọn, bên trong là phô mai mềm tan chảy một phần. Tương truyền người phát minh ra món này là một vị tư tế khuyết danh ở Vách Mây Bình Minh. Trong lúc chọn tế phẩm, ông vô tình để nhầm chiếc khay bên cạnh lò lửa rực cháy.\r\n\r\n\"Những công thức tinh xảo của con người thường chẳng bằng một lần ngẫu hứng mà thần linh ban xuống.\"', 'Máy Ghép Vạn Năng'),
(19, 'Bánh Mì \"Khô\"', '2', 'Vật Tiêu Hao', 'uploads/materials/Bánh Mì Khô.jpg', 'Dùng xong lập tức hồi cho 1 đồng đội chỉ định phe ta lượng HP bằng 24% Giới Hạn HP bản thân, và hồi thêm 300 HP.', 'Lúa mì ở Aedes Elysiae từng vang danh khắp mặt đất, bánh mì làm từ loại nguyên liệu này thơm ngọt mềm mại, ngon đến khó quên. Nay ngôi làng đã bị Thủy Triều Đen tàn phá, những ổ bánh còn sót lại cũng đã khô cứng theo thời gian, chỉ còn giá trị kỷ niệm chứ không thể dùng làm thực phẩm.\r\n\r\n\"Nếu bộ hàm của bạn đủ cứng... thật ra cũng không hẳn là không thể ăn...\"', 'Máy Ghép Vạn Năng'),
(20, 'Tàn Tích Dị Thú', '3', 'Vật Tiêu Hao', 'uploads/materials/Tàn Tích Dị Thú.jpg', 'Sau khi dùng, tăng 30% Kháng Hiệu Ứng của toàn phe ta trong lần chiến đấu kế tiếp.', 'Ai cũng biết rằng con người chỉ là sự kết hợp giữa xương cốt, máu thịt và vài chục gram linh hồn. Nếu dỡ đi bất kỳ phần nào cũng không thể sống được. Thế nhưng bộ phận còn sót lại của dị thú này vẫn thỉnh thoảng co giật, như thể đang gào thét trong tuyệt vọng. Một số học giả cho rằng đó là do tín hiệu điện trong hệ thần kinh của nó vẫn chưa hoàn toàn tiêu tan.\r\n\r\n\"Hoặc cũng có thể... sự sống không hề mong manh như ta vẫn tưởng...\"', 'Máy Ghép Vạn Năng'),
(21, 'Sáo Xương Gọi Cá', '4', 'Vật Tiêu Hao', 'uploads/materials/Sáo Xương Gọi Cá.jpg', 'Sau khi dùng, toàn thể phe ta trong lần chiến đấu kế tiếp tăng 15% tấn công, tăng 30% Sát Thương Duy Trì gây ra.', 'Tương truyền tại bờ biển cổ xưa của Styxia, ngư dân thường thổi một loại sáo làm từ xương để dụ cá kéo đến. Đôi khi, cả những Siren ẩn mình trong rạn đá cũng bị âm thanh đó thu hút, trồi lên mặt nước hát đối với ngư dân; đôi khi, ngư dân còn tặng sáo cho chúng, và được trả lễ lại bằng những chiếc vảy lấp lánh.\r\n\r\n\"Con người kết giao với Siren, hứa hẹn một bữa tiệc linh đình, mà khách quý của chính bữa tiệc ấy lại bị giam dưới lòng biển sâu.\"', 'Máy Ghép Vạn Năng'),
(22, 'Công Thức: Mũ Giáp Phương Bắc', '2', 'Công Thức', 'uploads/materials/Công Thức.jpg', 'Một đẳng thức ngắn gọn, sau khi nhập vào Máy Ghép sẽ có thể mở khóa công thức mới.', '', ''),
(23, 'Công Thức: Phô Mai Mật Ong Chiên Giòn', '2', 'Công Thức', 'uploads/materials/Công Thức.jpg', 'Một đẳng thức ngắn gọn, sau khi nhập vào Máy Ghép sẽ có thể mở khóa công thức mới.', '', ''),
(24, 'Công Thức: Bánh Mì \"Khô\"', '2', 'Công Thức', 'uploads/materials/Công Thức.jpg', 'Một đẳng thức ngắn gọn, sau khi nhập vào Máy Ghép sẽ có thể mở khóa công thức mới.', '', ''),
(25, 'Công Thức: Tàn Tích Dị Thú', '3', 'Công Thức', 'uploads/materials/Công Thức.jpg', 'Một đẳng thức ngắn gọn, sau khi nhập vào Máy Ghép sẽ có thể mở khóa công thức mới.', '', ''),
(26, 'Công Thức: Sáo Xương Gọi Cá', '4', 'Công Thức', 'uploads/materials/Công Thức.jpg', 'Một đẳng thức ngắn gọn, sau khi nhập vào Máy Ghép sẽ có thể mở khóa công thức mới.', '', ''),
(27, 'Phiến Đá Ca Tụng \"Pháo Đài Mái Vòm\" Con Mắt Ngày Và Đêm', '3', 'Đạo Cụ Nhiệm Vụ', 'uploads/materials/Phiến Đá Ca Tụng.jpg', 'Phiến đá ghi lại bài thơ ca ngợi và những thứ khác, về điểm cuối mà lời tiên tri hướng đến. Nghe nói trong những mảnh vỡ này ẩn chứa một bí mật, bí mật đó rất quan trọng, đến nỗi bản thân nó cũng hy vọng được người khác phát hiện.\r\n\r\n\"Lời sấm truyền chưa bao giờ truyền tải thông điệp của chúng bằng ngôn từ đơn giản.\" – Tư Tế Khuyết Danh', 'Bàn tay lật ngược ánh bình minh,\r\n\r\nNgón tay lướt qua cầu vồng.\r\n\r\nKhi sao đêm nhắm mắt nghỉ yên,\r\n\r\nChúng ta an giấc dưới phúc lành của bầu trời.', ''),
(28, 'Phiến Đá Ca Tụng \"Pháo Đài Trên Mây\" Con Mắt Ngày Và Đêm', '3', 'Đạo Cụ Nhiệm Vụ', 'uploads/materials/Phiến Đá Ca Tụng.jpg', 'Phiến đá ghi lại bài thơ ca ngợi và những thứ khác, về điểm cuối mà lời tiên tri hướng đến. Nghe nói trong những mảnh vỡ này ẩn chứa một bí mật, bí mật đó rất quan trọng, đến nỗi bản thân nó cũng hy vọng được người khác phát hiện.\r\n\r\n\"Lời sấm truyền chưa bao giờ truyền tải thông điệp của chúng bằng ngôn từ đơn giản.\" – Tư Tế Khuyết Danh', 'Bàn tay lật ngược ánh bình minh,\r\n\r\nNgón tay lướt qua cầu vồng.\r\n\r\nKhi sao đêm nhắm mắt nghỉ yên,\r\n\r\nChúng ta an giấc dưới phúc lành của bầu trời.', ''),
(29, 'Phiến Đá Ca Tụng \"Thành Phố Chìm Vào Hoàng Hôn\" Okhema', '3', 'Đạo Cụ Nhiệm Vụ', 'uploads/materials/Phiến Đá Ca Tụng.jpg', 'Phiến đá ghi lại bài thơ ca ngợi và những thứ khác, về điểm cuối mà lời tiên tri hướng đến. Nghe nói trong những mảnh vỡ này ẩn chứa một bí mật, bí mật đó rất quan trọng, đến nỗi bản thân nó cũng hy vọng được người khác phát hiện.\r\n\r\n\"Lời sấm truyền chưa bao giờ truyền tải thông điệp của chúng bằng ngôn từ đơn giản.\" – Tư Tế Khuyết Danh', 'Tường thành đã sụp đổ ngay khi được dựng lên, bình minh đã nhuốm vàng ngay khoảnh khắc ló rạng.\r\n\r\nYến tiệc đã tàn khi ly rượu đầu tiên được nâng lên, đúng như sinh mệnh, đã bước vào cái chết từ tiếng khóc chào đời đầu tiên.', ''),
(30, 'Phiến Đá Ca Tụng \"Điện Cầu Nguyện U Tối\" Vách Mây Bình Minh', '3', 'Đạo Cụ Nhiệm Vụ', 'uploads/materials/Phiến Đá Ca Tụng.jpg', 'Phiến đá ghi lại bài thơ ca ngợi và những thứ khác, về điểm cuối mà lời tiên tri hướng đến. Nghe nói trong những mảnh vỡ này ẩn chứa một bí mật, bí mật đó rất quan trọng, đến nỗi bản thân nó cũng hy vọng được người khác phát hiện.\r\n\r\n\"Lời sấm truyền chưa bao giờ truyền tải thông điệp của chúng bằng ngôn từ đơn giản.\" – Tư Tế Khuyết Danh', 'Xây một cung điện rực rỡ bên đầu gối của ngài, mặc cho ngài rơi vào vũng lầy ngàn vạn định mệnh.\r\n\r\nKhi bóng tối mài mòn ánh sáng không ngơi nghỉ của ngài, muôn nước trăm bang sẽ là nấm mồ sau cùng của ngài.', ''),
(31, 'Nước Cờ Của Quân Vương', '4', 'Vật Tiêu Hao', 'uploads/materials/Nước Cờ Của Quân Vương.jpg', 'Sau khi dùng, toàn thể phe ta trong lần chiến đấu kế tiếp tăng 16% Tốc Độ, tăng 10% Tấn Công.', 'Một món đồ chơi mới nổi được chế tác từ đá quý. Người chơi chia phe trắng đen, mô phỏng hai quân đối đầu, bên nào bắt được quân vua của đối phương trước sẽ giành chiến thắng. Thế nhưng, ở vùng Bắc địa xa xôi có một kỳ thủ đặc biệt, cô luôn đi nước đầu tiên bằng quân vua, và lần nào cũng chiến thắng.\r\n\r\n\"Nếu vua không bước trước, thần sao nguyện theo sau?\"', 'Máy Ghép Vạn Năng'),
(32, 'Công Thức: Nước Cờ Của Quân Vương', '4', 'Công Thức', 'uploads/materials/Công Thức.jpg', 'Một đẳng thức ngắn gọn, sau khi nhập vào Máy Ghép sẽ có thể mở khóa công thức mới.', '', ''),
(33, 'Hyacine: Giấc Ngủ An Lành', '5', 'Ảnh Đại Diện', 'uploads/materials/Hyacine - Giấc Ngủ An Lành.jpg', 'Thưởng ảnh đại diện sẽ nhận khi mở khóa Vinh Dự Vô Danh.', 'Hyacine trước khi ngủ.\r\n\r\n\"Nhóc Ica mới đó đã ngủ say rồi sao... Vậy cái kết của câu chuyện để ngày mai vậy. Ngủ ngon nhé!\"', 'Mở khóa Vinh Dự Vô Danh.'),
(34, 'Cipher: Giấc Ngủ An Lành', '5', 'Ảnh Đại Diện', 'uploads/materials/Cipher - Giấc Ngủ An Lành.jpg', 'Thưởng ảnh đại diện sẽ nhận khi mở khóa Vinh Dự Vô Danh.', 'Cipher trước khi ngủ.\r\n\r\n\"Meo~ Tìm kho báu hai ngày thật mệt mỏi. Cũng may lấy được kho báu, đêm nay cuối cùng cũng được ngủ ngon rồi!\"', 'Mở khóa Vinh Dự Vô Danh.'),
(35, 'Phiếu Nhiên Liệu', '4', 'Không Rõ', 'uploads/materials/Phiếu Nhiên Liệu.jpg', 'Kết hợp sử dụng với Sức Mạnh Khai Phá, có thể nhanh chóng nhận nguyên liệu do Đài Hoa Nhân Tạo/Hư Ảnh Ngưng Đọng sinh ra. Sau khi kết thúc Hành Trình Trở Về sẽ bị thu hồi.', 'Cho đến ngày nay, con người vẫn không thể nắm được nguyên lý cụ thể của việc Đội Tàu Astral bay khắp vũ trụ. Nhưng lý luận đun nước cổ xưa cho chúng ta biết rằng, vật dễ cháy trong lò càng nhiều, thì đội tàu sẽ chạy càng nhanh.\r\n\r\n“Công nghệ cao cấp nhất của con người là hơi nước, vì vậy đừng hỏi nhiều, cứ đốt là xong.”', 'Hành Trình Trở Về'),
(36, 'Hồi Tưởng Nắng Sấm', '4', 'Nguyên Liệu Vết Tích', 'uploads/materials/Hồi Tưởng Nắng Sấm.jpg', 'Vật liệu cao cấp để tăng cấp Vết Tích.', 'Khi con người uống lấy hạt nhân pha lê, họ mất đi khả năng cảm nhận lòng người. Kể từ đó, sao trời luân chuyển, việc dõi nhìn từ trên cao trở thành thiên chức duy nhất. Họ vẫn yêu sự sống, nhưng không còn coi người bên cạnh là sự sống nữa. \"Cúi mình nơi mặt đất, cố chạm sắc lam. Thân giữa trời cao, hương cỏ non lại trở nên vô giá...\"', 'Dư Âm Chiến Đấu - \"Pháo Đài Mái Vòm\" Con Mắt Ngày Và Đêm'),
(37, 'Khối Xâm Lược', '4', 'Nguyên Liệu Nâng Bậc Nhân Vật', 'uploads/materials/Khối Xâm Lược.jpg', 'Kết tinh lõi của tạo vật Thủy Triều Đen, nguyên liệu tăng bậc của nhân vật thuộc tính vật lý.', 'Quả thật, những người ủng hộ Thủy Triều Đen xuất hiện với muôn hình vạn trạng, và cũng có thể dựa vào tinh thể để nhận dạng, nhưng chẳng ai hay biết thứ trông như lõi ấy thực chất là \"lớp vỏ\" thực sự.\r\n\r\n\"Tôi nhìn thấy một đại dương tinh thể, sau đó là nụ cười hài lòng của thầy thuốc.\"', 'Hư Ảnh Ngưng Đọng - \"Thành Cổ Xương Rồng\" Styxia\r\n\r\nMáy Ghép Vạn Năng - Đổi Nguyên Liệu'),
(38, 'Chúc Phúc Hoàng Kim', '5', 'Vật Phẩm Quý Giá', 'uploads/materials/Chúc Phúc Hoàng Kim.jpg', NULL, 'Chúc phúc dành cho người Săn Đuổi Lửa.\r\n\r\nMong bạn có thể nhảy múa cùng thần linh, kế thừa ngọn lửa anh hùng thắp sáng người khổng lồ đã ngã xuống.\r\n\r\nMong bạn lật đổ vận mệnh, dùng ánh sáng bình minh để soi sáng trời đất và chúng sinh.\r\n\r\nDấu chân bạn để lại nơi đây sẽ vĩnh viễn được khắc vào những vần thơ truyền tụng ngàn đời.', ''),
(39, 'Phiến Đá Ca Tụng \"Thành Cổ Xương Rồng\"-I', '3', 'Đạo Cụ Nhiệm Vụ', 'uploads/materials/Phiến Đá Ca Tụng.jpg', 'Phiến đá ghi lại bài thơ ca ngợi và những thứ khác, tiên tri về điểm tận cùng. Nghe nói trong những mảnh vỡ này ẩn chứa một bí mật, bí mật đó rất quan trọng, đến nỗi bản thân nó cũng hy vọng được người khác phát hiện.\r\n\r\n\"Lời sấm truyền chưa bao giờ truyền tải thông điệp của chúng bằng ngôn từ đơn giản.\" - Tư Tế Khuyết Danh', 'Chiếc cốc đổ xuống, cổ tích vỡ tan, tiếng chuông tĩnh lặng làm gián đoạn bữa tiệc.\r\n\r\nDũng sĩ cầm kiếm đá tung cổng thành, chỉ thấy thi thể của Rồng Khổng Lồ.', ''),
(40, 'Phiến Đá Ca Tụng \"Thành Cổ Xương Rồng\"-II', '3', 'Đạo Cụ Nhiệm Vụ', 'uploads/materials/Phiến Đá Ca Tụng.jpg', 'Phiến đá ghi lại bài thơ ca ngợi và những thứ khác, tiên tri về điểm tận cùng. Nghe nói trong những mảnh vỡ này ẩn chứa một bí mật, bí mật đó rất quan trọng, đến nỗi bản thân nó cũng hy vọng được người khác phát hiện.\r\n\r\n\"Lời sấm truyền chưa bao giờ truyền tải thông điệp của chúng bằng ngôn từ đơn giản.\" - Tư Tế Khuyết Danh', 'Bóng tối xâm chiếm thủy triều, Sông Styx dạt dào vô tận;\r\n\r\nCông chúa không rõ tung tích, dũng sĩ trở về như một ẩn số.', ''),
(41, 'Phiến Đá Ca Tụng \"Vách Mây Bình Minh\"-I', '3', 'Đạo Cụ Nhiệm Vụ', 'uploads/materials/Phiến Đá Ca Tụng.jpg', 'Phiến đá ghi lại bài thơ ca ngợi và những thứ khác, tiên tri về điểm tận cùng. Nghe nói trong những mảnh vỡ này ẩn chứa một bí mật, bí mật đó rất quan trọng, đến nỗi bản thân nó cũng hy vọng được người khác phát hiện.\r\n\r\n\"Lời sấm truyền chưa bao giờ truyền tải thông điệp của chúng bằng ngôn từ đơn giản.\" - Tư Tế Khuyết Danh', 'Thần Phụ lặng lẽ đứng trên đỉnh núi, ánh bình minh bao trùm, lạnh lẽo tĩnh mịch.\r\n\r\nNguyên lão ngước nhìn bậc thang dài, bí ẩn đan xen, âm mưu như vực thẳm.', ''),
(42, 'Phiến Đá Ca Tụng \"Vách Mây Bình Minh\"-II', '3', 'Đạo Cụ Nhiệm Vụ', 'uploads/materials/Phiến Đá Ca Tụng.jpg', 'Phiến đá ghi lại bài thơ ca ngợi và những thứ khác, tiên tri về điểm tận cùng. Nghe nói trong những mảnh vỡ này ẩn chứa một bí mật, bí mật đó rất quan trọng, đến nỗi bản thân nó cũng hy vọng được người khác phát hiện.\r\n\r\n\"Lời sấm truyền chưa bao giờ truyền tải thông điệp của chúng bằng ngôn từ đơn giản.\" - Tư Tế Khuyết Danh', 'Thời đại hoàng kim ẩn hiện như làn khói, tai ương hoành hành khắp thế gian;\r\n\r\nThần Phụ chống đỡ bầu trời ngàn năm, ánh sáng có từng tắt lịm không?', ''),
(43, 'Cuộn Dệt Vàng Tiên Tri-Thành Cổ Xương Rồng', '3', 'Đạo Cụ Nhiệm Vụ', 'uploads/materials/Cuộn Dệt Vàng Tiên Tri.jpg', 'Cuộn sách ghi chép lại lời tiên tri. Quan sát kỹ mới phát hiện, bìa sách được dệt bằng tơ vàng.\r\n\r\n\"Trong này có một lời tiên tri, nó chỉ hiện ra trước mắt người đã mở cuộn sách.\"', 'Tây | Đầu\r\n\r\nChân | Thân\r\n\r\nNếu muốn mở cánh cửa quá khứ, thì cần phải tuân the nghi lễ hiến tế.\r\n\r\nSư tử không mắt hỏi, cái gì là thước đo vạn vật?\r\n\r\nTrong bữa tiệc, trí giả đã trả lời rằng: Là linh hồn của con người.\r\n\r\nThế là anh ta cuộn tròn trong tế đàn, ôm lấy đầu gối, cửa sổ ở trên đầu, hai mắt nhìn thẳng vào răng nanh của sư tử, hiến tặng linh hồn của bản thân...\r\n\r\nĐôi tay dùng để lao động biến mất trước tiên, sau đó là hai chân dùng để đo lường mặt đất. Cho đến khi phần thân duy trì hình dạng bị vỡ vụn, cái đầu của anh vẫn đang thầm thì.', '');

