--
-- Cấu trúc bảng cho bảng `relics`
--

CREATE TABLE `relics` (
  `id` int(11) NOT NULL,
  `name` varchar(100) NOT NULL,
  `type` enum('Relic','Planetary Ornament Set') NOT NULL,
  `icon` varchar(255) DEFAULT NULL,
  `set2_effect` text DEFAULT NULL,
  `set4_effect` text DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `relics`
--

INSERT INTO `relics` (`id`, `name`, `type`, `icon`, `set2_effect`, `set4_effect`) VALUES
(1, 'Học Giả Lạc Lối Trong Biển Tri Thức', 'Relic', 'uploads/relics/Học Giả Lạc Lối Trong Biển Tri Thức.jpg', 'Tỷ Lệ Bạo Kích tăng 8%.', 'Sát thương Chiến Kỹ và Tuyệt Kỹ gây ra tăng 20%, sau khi thi triển Tuyệt Kỹ, sát thương gây ra trong lần thi triển Chiến Kỹ kế tiếp tăng thêm 25%.'),
(2, 'Đấu Trường Ngôi Sao', 'Planetary Ornament Set', 'uploads/relics/Đấu Trường Ngôi Sao.jpg', 'Tăng 8% tỷ lệ Bạo Kích của người trang bị. Khi tỷ lệ Bạo Kích của người đó từ 70% trở lên, sẽ tăng 20% sát thương gây ra khi thi triển Tấn Công Thường hoặc Chiến Kỹ', NULL),
(3, 'Anh Hùng Ca Khúc Khải Hoàn', 'Relic', 'uploads/relics/Anh Hùng Ca Khúc Khải Hoàn.jpg', 'Tấn Công tăng 12%', 'Khi Linh Hồn Ký Ức của người trang bị trong trận, Tốc Độ của người trang bị tăng 6%, Khi Linh Hồn Ký Ức của người trang bị tấn công, Sát Thương Bạo Kích của Linh Hồn Ký Ức và người trang bị tăng 30%, duy trì 2 hiệp.'),
(4, 'Công Viên Chuối Kỳ Ảo', 'Planetary Ornament Set', 'uploads/relics/Công Viên Chuối Kỳ Ảo.jpg', 'Khiến Sát Thương Bạo Kích của người trang bị tăng 16%, khi tồn tại mục tiêu mà người trang bị triệu hồi, Sát Thương Bạo Kích sẽ tăng thêm 32%.', NULL),
(5, 'Nhà Thơ Khúc Bi Ca Vong Quốc', 'Relic', 'uploads/relics/Nhà Thơ Khúc Bi Ca Vong Quốc.jpg', 'Sát Thương Lượng Tử tăng 10%', 'Khiến Tốc Độ của người trang bị giảm 8%. Trước khi vào chiến đấu, nếu Tốc Độ của trang bị dưới 110/95, sẽ khiến Tỷ Lệ Bạo Kích của người trang bị tăng 20%/32%. Hiệu ứng này đồng thời có hiệu lực với Linh Hồn Ký Ức của người trang bị.'),
(6, 'Vùng Đất Nhặt Hài Cốt Tĩnh Lặng', 'Planetary Ornament Set', 'uploads/relics/Vùng Đất Nhặt Hài Cốt Tĩnh Lặng.jpg', 'Tăng 12% Giới Hạn HP của người trang bị. Khi Giới Hạn HP của người trang bị lớn hơn hoặc bằng 5.000, sẽ tăng 28% Sát Thương Bạo Kích của người trang bị và Linh Hồn Ký Ức của người đó.', NULL),
(7, 'Nữ Thần Chiến Binh Nắng Sấm', 'Relic', 'uploads/relics/Nữ Thần Chiến Binh Nắng Sấm.jpg', 'Tốc Độ tăng 6%.', 'Sau khi người trang bị và Linh Hồn Ký Ức của người trang bị cung cấp trị liệu cho mục tiêu phe ta ngoại trừ người trang bị và Linh Hồn Ký Ức của họ, người trang bị sẽ nhận \"Cơn Mưa Ngọt Ngào\", mỗi hiệp kích hoạt tối đa 1 lần, kéo dài 2 hiệp. Khi người trang bị đang có \"Cơn Mưa Ngọt Ngào\", tốc độ tăng 6%, sát thương bạo kích toàn phe ta tăng 15%. Hiệu ứng này không thể cộng dồn.'),
(8, 'Đại Thụ Trầm Tư', 'Planetary Ornament Set', 'uploads/relics/Đại Thụ Trầm Tư.jpg', 'Khiến Tốc Độ của người trang bị tăng 6%. Khi Tốc Độ của người trang bị từ 135/180 trở lên, thì Lượng Trị Liệu của người trang bị và Linh Hồn Ký Ức của người đó sẽ tăng 12%/20%.', NULL),
(9, 'Thuyền Trưởng Đuổi Sóng Biển Sâu', 'Relic', 'uploads/relics/Thuyền Trưởng Đuổi Sóng Biển Sâu.jpg', 'Sát thương bạo kích tăng 16%.', 'Khi người trang bị trở thành mục tiêu kỹ năng của mục tiêu phe ta khác, nhận 1 tầng Trợ Lực. Tối đa cộng dồn 2 tầng. Khi thi triển Tuyệt Kỹ, nếu có 2 tầng Trợ Lực sẽ tiêu hao tất cả Trợ Lực, tăng 48% Tấn Công của người trang bị, duy trì 1 hiệp.'),
(10, 'Thiên Đường Yêu Tinh Dệt Mộng', 'Planetary Ornament Set', 'uploads/relics/Thiên Đường Yêu Tinh Dệt Mộng.jpg', 'Khi số lượng mục tiêu của đội ngũ phe ta ở trong trận hiện tại không bằng 4, mỗi khi thêm/mất 1 mục tiêu phe ta, sẽ khiến sát thương gây ra bởi người trang bị và Linh Hồn Ký Ức của người đó tăng 9%/12%, tối đa cộng dồn 4/3 tầng.', NULL),
(11, 'Thiên Tài Xuất Chúng', 'Relic', 'uploads/relics/Thiên Tài Xuất Chúng.jpg', 'Sát Thương Lượng Tử tăng 10%.', 'Khi người trang bị gây sát thương lên mục tiêu địch, sẽ bỏ qua 10% Phòng Thủ của địch. Nếu mục tiêu có Điểm Yếu Lượng Tử, sẽ bỏ qua thêm 10% Phòng Thủ của địch.'),
(12, 'Lại Một Hành Trình Gian Khổ Của Linh Mục', 'Relic', 'uploads/relics/Lại Một Hành Trình Gian Khổ Của Linh Mục.jpg', 'Tốc Độ tăng 6%.', 'Khi thi triển Chiến Kỹ hoặc Tuyệt Kỹ lên 1 mục tiêu phe ta, sẽ khiến Sát Thương Bạo Kích của mục tiêu kỹ năng tăng 18%, duy trì 2 hiệp, hiệu ứng này tối đa cộng dồn 2 lần.'),
(13, 'Ban Nhạc Sizzling Thunder', 'Relic', 'uploads/relics/Ban Nhạc Sizzling Thunder.jpg', 'Sát Thương Lôi tăng 10%.', 'Khi người trang bị thi triển Chiến Kỹ, Tấn Công của người đó tăng 20%, duy trì 1 hiệp.'),
(14, 'Chim Ưng Chạng Vạng', 'Relic', 'uploads/relics/Chim Ưng Chạng Vạng.jpg', 'Sát Thương Phong tăng 10%.', 'Sau khi người trang bị thi triển Tuyệt Kỹ, khiến Hành Động của người đó ưu tiên 25%.'),
(15, 'Kẻ Cướp Vệt Sao Băng', 'Relic', 'uploads/relics/Kẻ Cướp Vệt Sao Băng.jpg', 'Tấn Công Kích Phá tăng 16%.', 'Tăng 16% Tấn Công Kích Phá của người trang bị. Sau khi người trang bị phá vỡ điểm yếu của kẻ địch, sẽ hồi 3 điểm Năng Lượng.'),
(16, 'Kẻ Hoang Dã Sa Mạc', 'Relic', 'uploads/relics/Kẻ Hoang Dã Sa Mạc.jpg', 'Sát Thương Số Ảo tăng 10%.', 'Khi người trang bị gây sát thương lên mục tiêu địch rơi vào Hiệu Ứng Xấu, Tỷ Lệ Bạo Kích tăng 10%, khi gây sát thương mục tiêu địch rơi vào trạng thái Giam Cầm, Sát Thương Bạo Kích tăng 20%.'),
(17, 'Lãng Khách Âm Thầm', 'Relic', 'uploads/relics/Lãng Khách Âm Thầm.jpg', 'Lượng Trị Liệu tăng 10%.', 'Khi bắt đầu chiến đấu sẽ hồi ngay cho phe ta 1 điểm Chiến Kỹ.'),
(18, 'Môn Đồ Trường Thọ', 'Relic', 'uploads/relics/Môn Đồ Trường Thọ.jpg', 'Giới Hạn HP tăng 12%.', 'Sau khi người trang bị chịu tấn công hoặc bị mục tiêu phe ta tiêu hao HP, Tỷ Lệ Bạo Kích tăng 8%, duy trì 2 hiệp. Hiệu ứng này tối đa cộng dồn 2 tầng.'),
(19, 'Quyền Vương Đường Phố', 'Relic', 'uploads/relics/Quyền Vương Đường Phố.jpg', 'Sát Thương Vật Lý tăng 10%.', 'Sau khi người trang bị tấn công hoặc bị tấn công, Tấn Công của người trang bị trong trận này tăng 5%, tối đa cộng dồn 5 lần.'),
(20, 'Thánh Kỵ Sĩ Giáo Hoàng Tịnh Đình', 'Relic', 'uploads/relics/Thánh Kỵ Sĩ Giáo Hoàng Tịnh Đình.jpg', 'Phòng Thủ tăng 15%.', 'Tăng 20% Khiên mà người trang bị cung cấp.'),
(21, 'Thiện Xạ Bông Lúa', 'Relic', 'uploads/relics/Thiện Xạ Bông Lúa.jpg', 'Tấn Công tăng 12%.', 'Tăng 6% tốc độ của người trang bị, sát thương Tấn Công Thường tăng 10%.'),
(22, 'Thợ Rèn Lửa Dung Nham', 'Relic', 'uploads/relics/Thợ Rèn Lửa Dung Nham.jpg', 'Sát Thương Hỏa tăng 10%.', 'Khiến sát thương Chiến Kỹ của người trang bị tăng 12%, đồng thời tăng 12% Sát Thương Hỏa của lần tấn công kế tiếp sau khi thi triển Tuyệt Kỹ.'),
(23, 'Thợ Săn Tuyết Phủ', 'Relic', 'uploads/relics/Thợ Săn Tuyết Phủ.jpg', 'Sát Thương Băng tăng 10%.', 'Sau khi người trang bị thi triển Tuyệt Kỹ, Sát Thương Bạo Kích tăng 25%, duy trì 2 hiệp.'),
(24, 'Tín Sứ Du Ngoạn Không Gian Hacker', 'Relic', 'uploads/relics/Tín Sứ Du Ngoạn Không Gian Hacker.jpg', 'Tốc Độ tăng 6%.', 'Khi người trang bị thi triển Tuyệt Kỹ lên mục tiêu phe ta, tốc độ toàn phe ta tăng 12%, duy trì 1 hiệp, hiệu ứng này không thể cộng dồn.'),
(25, 'Belobog Của Đấng Kiến Tạo', 'Planetary Ornament Set', 'uploads/relics/Belobog Của Đấng Kiến Tạo.jpg', 'Tăng 15% Phòng Thủ của người trang bị. Khi Chính Xác Hiệu Ứng của người trang bị từ 50% trở lên, Phòng Thủ tăng thêm 15%.', NULL),
(26, 'Biển Lục Địa Chìm - Lushaka', 'Planetary Ornament Set', 'uploads/relics/Biển Lục Địa Chìm - Lushaka.jpg', 'Khiến Hiệu Suất Hồi Năng Lượng của người trang bị tăng 5%, nếu người trang bị không phải nhân vật vị trí đầu tiên trong đội, thì sẽ khiến Tấn Công của nhân vật ở vị trí đầu tiên trong đội tăng 12%.', NULL),
(27, 'Bờ Biển Men Say', 'Planetary Ornament Set', 'uploads/relics/Bờ Biển Men Say.jpg', 'Khiến Tấn Công của người trang bị tăng 12%. Khi Tấn Công của người trang bị lớn hơn hoặc bằng 2.400/3.600, sẽ khiến Sát Thương Duy Trì gây ra tăng thêm 12%/24%.', NULL),
(28, 'Bộ Khung Bị Gãy', 'Planetary Ornament Set', 'uploads/relics/Bộ Khung Bị Gãy.jpg', 'Tăng 10% Kháng Hiệu Ứng của người trang bị. Khi Kháng Hiệu Ứng của người trang bị từ 30% trở lên, Sát Thương Bạo Kích của toàn bộ phe ta tăng 10%.', NULL),
(29, 'Công Ty Kinh Doanh Liên Ngân Hà', 'Planetary Ornament Set', 'uploads/relics/Công Ty Kinh Doanh Liên Ngân Hà.jpg', 'Tăng 10% Chính Xác Hiệu Ứng của người trang bị. Đồng thời tăng Tấn Công bằng 25% Chính Xác Hiệu Ứng hiện tại của người trang bị, tối đa tăng 25%.', NULL),
(30, 'Máy Sai Lệch Hành Tinh', 'Planetary Ornament Set', 'uploads/relics/Máy Sai Lệch Hành Tinh.jpg', 'Khiến Sát Thương Bạo Kích của người trang bị tăng 16%. Khi Sát Thương Bạo Kích của người trang bị từ 120% trở lên, sau khi vào chiến đấu, Tỷ Lệ Bạo Kích của người trang bị tăng 60%, duy trì đến khi kết thúc thi triển lần đầu tấn công', NULL),
(31, 'Salsotto Dừng Xoay', 'Planetary Ornament Set', 'uploads/relics/Salsotto Dừng Xoay.jpg', 'Tăng 8% Tỷ Lệ Bạo Kích của người trang bị. Khi Tỷ Lệ Bạo Kích của người đó từ 50% trở lên, sẽ tăng 15% sát thương của Tuyệt Kỹ và Đòn Đánh Theo Sau.', NULL),
(32, 'Talia - Vương Quốc Trộm Cướp', 'Planetary Ornament Set', 'uploads/relics/Talia - Vương Quốc Trộm Cướp.jpg', 'Tăng 16% Tấn Công Kích Phá của người trang bị. Khi tốc độ của người trang bị từ 145 trở lên, Tấn Công Kích Phá tăng thêm 20%.', NULL),
(33, 'Trạm Phong Ấn Không Gian', 'Planetary Ornament Set', 'uploads/relics/Trạm Phong Ấn Không Gian.jpg', 'Tăng 12% Tấn Công của người trang bị. Khi tốc độ của người trang bị từ 120 trở lên, Tấn Công tăng thêm 12%.', NULL),
(34, 'Vonwacq Hoạt Bát', 'Planetary Ornament Set', 'uploads/relics/Vonwacq Hoạt Bát.jpg', 'Tăng 5% hiệu suất hồi Năng Lượng của người trang bị. Khi tốc độ của người trang bị từ 120 trở lên, vào chiến đấu sẽ lập tức Ưu Tiên Hành Động 40%.', NULL),
(35, 'Xianzhou Không Có Tuổi', 'Planetary Ornament Set', 'uploads/relics/Xianzhou Không Có Tuổi.jpg', 'Tăng 12% Giới Hạn HP của người trang bị. Khi tốc độ của người trang bị từ 120 trở lên, Tấn Công của toàn phe ta tăng 8%.', NULL),
(36, 'Penacony, Vùng Đất Của Những Giấc Mơ', 'Planetary Ornament Set', 'uploads/relics/Penacony, Vùng Đất Của Những Giấc Mơ.jpg', 'Tăng 5% hiệu suất hồi Năng Lượng của người trang bị. Khiến nhân vật khác trong đội của phe ta có cùng thuộc tính với người trang bị tăng 10% sát thương gây ra.', NULL),
(37, 'Glamoth, Chiến Tuyến Không Trung', 'Planetary Ornament Set', 'uploads/relics/Glamoth, Chiến Tuyến Không Trung.jpg', 'Tăng 12% Tấn Công của người trang bị. Khi tốc độ của người trang bị từ 135/160 trở lên, sát thương do người trang bị gây ra tăng 12%/18%.', NULL),
(38, 'Tù Nhân Ngục Tối', 'Relic', 'uploads/relics/Tù Nhân Ngục Tối.jpg', 'Tấn Công tăng 12%', 'Mỗi khi kẻ địch nhận phải 1 hiệu ứng Sát Thương Duy Trì, thì sát thương người trang bị gây ra cho kẻ đó sẽ bỏ qua 6% Phòng Thủ của kẻ đó. Hiệu ứng này có hiệu lực với tối đa 3 hiệu ứng Sát Thương Duy Trì.'),
(39, 'Đại Công Tước Tro Tàn', 'Relic', 'uploads/relics/Đại Công Tước Tro Tàn.jpg', 'Sát thương Đòn Đánh Theo Sau gây ra tăng 20%.', 'Khi người trang bị thi triển Đòn Đánh Theo Sau, mỗi lần gây sát thương sẽ tăng 6% Tấn Công của người trang bị dựa theo số lần Đòn Đánh Theo Sau gây sát thương, tối đa cộng dồn 8 lần, duy trì 3 hiệp. Hiệu ứng này sẽ bị xóa khi người trang bị thi triển Đòn Đánh Theo Sau tiếp theo.'),
(40, 'Tiên Phong Trong Nước Chết', 'Relic', 'uploads/relics/Tiên Phong Trong Nước Chết.jpg', 'Sát thương gây ra cho kẻ địch bị ảnh hưởng bởi Hiệu Ứng Xấu tăng 12%', 'Tăng 4% Tỷ Lệ Bạo Kích, tăng 8%/12% Sát Thương Bạo Kích mà người trang bị gây ra lên kẻ địch rơi vào không dưới 2/3 Hiệu Ứng Xấu. Sau khi người trang bị thi triển Hiệu Ứng Xấu lên kẻ địch, hiệu ứng nói trên tăng 100%, duy trì 1 hiệp'),
(41, 'Thợ Đồng Hồ, Bậc Thầy Máy Móc', 'Relic', 'uploads/relics/Thợ Đồng Hồ, Bậc Thầy Máy Móc.jpg', 'Tấn Công Kích Phá tăng 16%.', 'Khi người trang bị thi triển Tuyệt Kỹ lên mục tiêu phe ta, Tấn Công Kích Phá toàn phe ta tăng 30%, duy trì 2 hiệp, hiệu ứng này không thể cộng dồn.'),
(42, 'Izumo Hiện Thế Và Thần Quốc Cõi Trời', 'Planetary Ornament Set', 'uploads/relics/Izumo Hiện Thế Và Thần Quốc Cõi Trời.jpg', 'Tăng 12% Tấn Công của người trang bị. Khi vào chiến đấu, nếu có ít nhất 1 đồng đội cùng Vận Mệnh với người trang bị, thì Tỷ Lệ Bạo Kích của người trang bị tăng 12%.', NULL),
(43, 'Hành Tinh Vô Chủ Sigonia', 'Planetary Ornament Set', 'uploads/relics/Hành Tinh Vô Chủ Sigonia.jpg', 'Khiến tỷ lệ Bạo Kích của người trang bị tăng 4%. Khi kẻ địch bị tiêu diệt, Sát Thương Bạo Kích của người trang bị tăng 4%, tối đa cộng dồn 10 tầng.', NULL),
(44, 'Thiết Kỵ Diệt Trừ Tai Họa', 'Relic', 'uploads/relics/Thiết Kỵ Diệt Trừ Tai Họa.jpg', 'Tấn Công Kích Phá tăng 16%.', 'Khi Tấn Công Kích Phá của người trang bị lớn hơn hoặc bằng 150%, Sát Thương Phá Vỡ gây ra cho kẻ địch sẽ bỏ qua 10% Phòng Thủ của kẻ đó. Khi Tấn Công Kích Phá của người trang bị lớn hơn hoặc bằng 250%, Siêu Sát Thương Phá Vỡ gây ra cho kẻ địch sẽ bỏ qua thêm 15% Phòng Thủ của kẻ đó.'),
(45, 'Cung Điện Rèn Đúc Kiếp Hỏa Liên Đăng', 'Planetary Ornament Set', 'uploads/relics/Cung Điện Rèn Đúc Kiếp Hỏa Liên Đăng.jpg', 'Tăng 6% Tốc Độ của người trang bị. Khi người trang bị đánh trúng kẻ địch có Điểm Yếu Hỏa, Tấn Công Kích Phá tăng 40%, duy trì 1 hiệp.', NULL),
(46, 'Dũng Khí Ngút Trời', 'Relic', 'uploads/relics/Dũng Khí Ngút Trời.jpg', 'Tấn Công tăng 12%.', 'Tăng 6% Tỷ Lệ Bạo Kích cho người trang bị. Khi người trang bị thi triển Đòn Đánh Theo Sau, khiến sát thương Tuyệt Kỹ gây ra tăng 36%, duy trì 1 hiệp.'),
(47, 'Duran - Vương Triều Sói Hoang', 'Planetary Ornament Set', 'uploads/relics/Duran - Vương Triều Sói Hoang.jpg', 'Khi nhân vật phe ta thi triển Đòn Đánh Theo Sau, người trang bị sẽ nhận 1 tầng Công Huân, tối đa cộng dồn 5 tầng. Mỗi tầng Công Huân sẽ khiến sát thương Đòn Đánh Theo Sau của người trang bị gây ra tăng 5%, khi cộng dồn đầy 5 tầng, tăng thêm 25% Sát Thương Bạo Kích của người trang bị.', NULL),
(48, 'Thiết Vệ Bão Tuyết', 'Relic', 'uploads/relics/Thiết Vệ Bão Tuyết.jpg', 'Sát thương phải chịu giảm 8%.', 'Khi bắt đầu hiệp, nếu người trang bị có phần trăm HP hiện tại thấp hơn hoặc bằng 50%, sẽ hồi HP bằng 8% Giới Hạn HP của bản thân, và hồi 5 điểm Năng Lượng.'),
(49, 'Đấng Cứu Thế Tái Tạo Trời Đất', 'Relic', 'uploads/relics/Đấng Cứu Thế Tái Tạo Trời Đất.jpg', 'Tỷ Lệ Bạo Kích tăng 8%.', 'Sau khi người trang bị thi triển Tấn Công Thường hoặc Chiến Kỹ, nếu Linh Hồn Ký Ức của người trang bị trong trận, sẽ khiến Giới Hạn HP của người trang bị và Linh Hồn Ký Ức của người đó tăng 24%, sát thương, toàn bộ phe ta gây ra tăng 15%, duy trì đến sau khi người trang bị thi triển Tấn Công Thường hoặc Chiến Kỹ tiếp theo.'),
(50, 'Ẩn Sĩ Ẩn Mình Dưới Ánh Sao', 'Relic', 'uploads/relics/Ẩn Sĩ Ẩn Mình Dưới Ánh Sao.jpg', 'Lượng khiên cung cấp tăng 10%.', 'Khiến lượng Khiên mà người trang bị cung cấp tăng 12%, khi mục tiêu phe ta có Khiên do người trang bị cung cấp, Sát Thương Bạo Kích tăng 15%.'),
(51, 'Vùng Đất Vĩnh Hằng Amphoreus', 'Planetary Ornament Set', 'uploads/relics/Vùng Đất Vĩnh Hằng Amphoreus.jpg', 'Khiến Tỷ Lệ Bạo Kích của người trang bị tăng 8%. Khi Linh Hồn Ký Ức của người trang bị trong trận, Tốc Độ của toàn bộ phe ta tăng 8%, hiệu ứng này không thể cộng dồn.', NULL),
(52, 'Tengoku@Livestream', 'Planetary Ornament Set', 'uploads/relics/Tengoku@Livestream.jpg', 'Khiến Sát Thương Bạo Kích của người trang bị tăng 16%. Nếu trong cùng một hiệp tiêu hao từ 3 Điểm Chiến Kỹ trở lên, sẽ khiến Sát Thương Bạo Kích của người trang bị tăng thêm 32%, duy trì 3 hiệp.', NULL);

